INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000074, '201601/Navrat do budoucnosti 2.m4v', 'Navrat do budoucnosti 2', 4, 42000091, 'cz', 'cz', 'en', 'Back to the Future 2', 'http://www.csfd.cz/film/10129-navrat-do-budoucnosti-ii/', 'Navrat do budoucnosti 2.jpg', 2, 'V druhé části vynikající sci-fi komedie má Marty plné ruce práce. Nejprve musí do budoucnosti zachránit syna a potom zase do minulosti, aby zachránil sám sebe. (oficiální text distributora)', '2016-01-04 17:59:06.107', 1989, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000075, '201601/CROCODILE DUNDEE 2.m4v', 'Krokodil Dundee 2', 15, 42000090, 'cz', 'cz', 'en', 'Crocodile Dundee 2', 'http://www.csfd.cz/film/12315-krokodyl-dundee-2/', 'CROCODILE DUNDEE 2.jpg', 2, 'Svérázný australský lovec Michael J. Krokodýl Dundee žije se svou láskou Sue v New Yorku, ale stále více se mu stýská po buši. Když je Sue unesena do luxusního sídla kolumbijského drogového bosse Rika, pomocí odvážného plánu pronikne do jeho sídla a Sue zachrání. Jenže Krokodýl je spíše tvorem džungle a ve městě ji nedokáže ochránit. A tak se Sue odjedou zpět do Austrálie... (oficiální text distributora)', '2016-01-04 17:59:06.12', 1988, '');
