INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000077, '201601/INDEPENDENCE_DAY.m4v', 'Den nezavislosti (ID4)', 4, 0, 'cz', 'cz', 'en', 'Independence Day', 'http://www.csfd.cz/film/2572-den-nezavislosti/', 'INDEPENDENCE_DAY.jpg', 0, 'Je druhého července, letní den jako každý jiný... dokud bez předchozího varování nepadnou na Zemi obrovské stíny. Otázka, zda jsme ve vesmíru sami, je zodpovězena - během pár minut se životy všech lidí na celém světě změní jednou provždy. Stejně šokující jako samotný příchod mimozemšťanů je i naprostá nepřipravenost světa na události, které mají následovat. Pouhý den po svém nenadálém příchodu se obrovské vesmírné lodě nad největšími městy planety chystají zaútočit. Kam dosáhne jejich ničivá síla, zbude jen pár trosek bez života. Není pochyb o tom, že bude lidská rasa během několika dnů vyhlazena... pokud jim v tom ti, kterým se dosud podařilo přežít, nedokáží jen se svou podřadnou výzbrojí, důvtipem a odvahou zabránit. (HBO Europe)', '2016-01-04 17:59:06.144', 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000078, '201601/Kate a Leopold.m4v', 'Kate a Leopold', 6, 0, 'cz', 'cz', 'en', 'Kate and Leopold', 'http://www.csfd.cz/film/13547-kate-a-leopold/', 'Kate a Leopold.jpg', 0, 'Příběh začíná na konci 19. století - mladý Leopold Alexis Elijah Walker Gareth Thomas Mountbatten, který odmítá zaběhlé strnulé konvence i názory svého otce, je jedním z nejžádanějších ženichů v New Yorku. Kromě obdivných pohledů dívek ho pronásleduje i zvláštní mladík. Díky němu se Leopold dostává do časové smyčky. Na jejím konci ho čeká New York dnešních dnů, překvapení v podobě automobilů, telefonů, televize a především krásné a sympatické Kate. Leopold se s moderními technickými vymoženostmi srovnává rozhodně snadněji než s názory a životním stylem moderní ženy. Jejich cesta k sobě je plná nedorozumění, komických situací i překvapivých momentů. Kate dlouho odmítá věřit, že Leopold skutečně pochází z minulých časů považuje ho za trhlého podivína či vtipálka se zvláštním smyslem pro humor. Přesto je její cit k němu stále silnější a nakonec se tato sebejistá, realisticky uvažující žena kvůli němu vrhne do nejistého neznáma. (oficiální text distributora)', '2016-01-04 17:59:06.155', 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000079, '201601/DAY_AFTER.m4v', 'Den pote', 4, 0, 'cz', 'cz', 'en', 'The Day After Tomorrow', 'http://www.csfd.cz/film/107279-den-pote/', 'DAY_AFTER.jpg', 0, 'Výzkumy klimatologa Jacka Halla (Dennis Quaid), naznačují, že by globální oteplování mohlo spustit prudkou a katastrofální změnu zemského klimatu. Všechno začalo, když se Hall stal svědkem odlomení ledové kry o velikosti Rhode Islandu z antarktického ledového pole. Na celé zeměkouli následovaly prudké změny počasí: v Tokiu padaly kroupy o velikosti grapefruitu, rekordy lámající hurikán řádil na Havaji, v Dillí napadl sníh a přes Los Angeles se přehnala ničivá série tornád. Profesor Rapsona (Ian Holm) potvrdil Jackovy nejhorší obavy: tyto prudké změny počasí jsou příznakem velké globální změny. Množství vody z tajících polárních ledovců narušilo mořské proudy stabilizující náš klimatický systém. Globální oteplování posunulo planetu na pokraj nové doby ledové, která nastane během jedné globální superbouře... Zatímco Jack varuje Bílý dům před blížící se klimatickou změnou, jeho sedmnáctiletý syn Sam (Jake Gyllenhaal) uvízl v New Yorku. Obyvatelstvo je evakuováno na jih, avšak Jack míří na sever zachránit Sama. Ale ani Jack není připraven na to, co se stane - jemu, jeho synovi a jeho planetě. V Emmerichově filmu "Den nezávislosti" Zemi téměř zničili vetřelci. Teď ve filmu "DEN POTÉ" je nepřítelem mnohem ničivější síla: je to příroda sama. "Je to výpravný příběh o přežití a hrdinství s neustálou akcí a výbornými vizuálními efekty. Rozhodně má tu ráznost, kterou obecenstvo od Rolanda Emmericha očekává," říká producent Mark Gordon. Filmu nechybí ani lidský prvek. "Je to stále lidské drama bez ohledu na efekty," říká Emmerich. Postavy otce a syna jsou zranitelné, rozporné a milující a to dělá jejich boj proti nesmírné síle přírody tak vzrušující. Jde o obecný boj Člověka proti Přírodě. Odolnost a nakonec vítězství lidského ducha. (oficiální text distributora)', '2016-01-04 17:59:06.163', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000080, '201601/MALA_PRINCEZNA.m4v', 'Mala Princezna', 3, 0, 'en', 'cz', 'en', 'Little Princess', 'http://www.csfd.cz/film/29553-mala-princezna/', 'MALA_PRINCEZNA.jpg', 0, 'Sára žije se svým tatínkem kouzelným životem v exotické Indii. Pouze však do okamžiku, kdy je tatínek povolán do vojenské služby. Sáru pak pošlou do internátní školy v New Yorku. Dívenka si samozřejmě na přísné předpisy a kázeň nemůže zvyknout. Čerpá však sílu alespoň z vědomí, že pro svého tatínka bude vždycky malou princeznou. Svou odvahou, představivostí a laskavostí překonává potíže, které se jí staví do cesty a mění její život, stejně jako osudy lidí okolo ní. (oficiální text distributora)', '2016-01-04 17:59:06.172', 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000081, '201601/CROCODILE_DUNDEE_3.m4v', 'Krokodil Dundee v Los Angeles', 15, 42000090, 'cz', 'cz', 'en', 'Crocodile Dundee in Los Angeles', 'http://www.csfd.cz/film/20016-krokodyl-dundee-v-los-angeles/', 'CROCODILE_DUNDEE_3.jpg', 3, 'Slyšel, že je v L.A. težký život. Nevěděl ale jak těžký. Krokodýl Dundee se vrací!!! Odešel z Austrálie a přišel si pro svou přítelkyni do Los Angeles. Jak bude jejich dobrodružství pokračovat? (oficiální text distributora)', '2016-01-04 17:59:06.18', 2001, '');
