INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000083, '201601/Navrat do budoucnosti 3.m4v', 'Navrat do budoucnosti 3', 4, 42000091, 'cz', 'cz', 'en', 'Back to the Future 3', 'http://www.csfd.cz/film/10130-navrat-do-budoucnosti-iii/', 'Navrat do budoucnosti 3.jpg', 3, 'Tentokrát se Michael J. Fox vydává na záchranou misi do roku 1885. Třetí Martyho cesta do minulosti, kde je třeba zachránit přítele a vyřešit problém, jak se dostat zpátky do budoucnosti. (oficiální text distributora)', '2016-01-04 17:59:06.194', 1990, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000085, '201601/CROCODILE DUNDEE.m4v', 'Krokodil Dundee', 15, 42000090, 'cz', 'cz', 'en', 'Crocodile Dundee', 'http://www.csfd.cz/film/20015-krokodyl-dundee/', 'CROCODILE DUNDEE.jpg', 1, 'Ctižádostivá newyorská reportérka Sue Charlton chce napsat článek o Australanovi Mikovi Dundeem, který přežil útok velkého krokodýla. Vypraví se proto australského městečka Wakabout Creek, kde ji přivítá Dundeeho společník Walter Reilly, který společně s Mikem provozuje malou turistickou kancelář. V místní hospodě plné svérázných hostů jí vypráví historku s krokodýlem, samozřejmě patřičně přikrášlenou, což okolí neopomene náležitě komentovat, a vzápětí vtrhne dovnitř Mike Dundee s vycpaným krokodýlem pod paží, a než se reportérka vzpamatuje, vytáhne ji na parket a během tance a neustálého špičkování ještě stihne pěstí uzemnit muže, který si z něj utahoval. Je jisté, že pokud přijme nabídku, aby jí za 2500 dolarů ukázal místo kde se všechno stalo, čeká ji řada neobvyklých zážitků. Dundee uspí býka, který jim zatarasí cestu, holou rukou chytí hada, který se k ní plazil, zažene bandu opilých lovců, kteří se vydali lovit klokany a zachrání ji před krokodýlem, který ji u řeky napadne. Přitom si z ní nepřestává utahovat a vždy sebejistá reportérka v jeho přítomnosti nečekaně ztrácí svou obvyklou jistotu a navíc začíná podléhat jeho svéráznému kouzlu. Když zjistí, že lovec nikdy nebyl v žádném větším městě, přemluví ho, aby se s v rámci reportáže vydal do New Yorku. Mike po krátkém váhání souhlasí. V New Yorku dvojici přivítá Richard Mason, který je nejen kolegou Sue, ale také jejím přítelem. Přestože se Mike snaží ke každému chovat přátelsky, cítí se v přelidněném městě i přepychovém apartmá hotelu, kde je ubytován, nesvůj. Při společné večeři v luxusní restauraci sice dává samolibý Richard lovci okázale najevo svou převahu, ten si to však nenechá líbit a jednou dobře mířenou ranou do "uklidní". Náladu si pak vylepší v malé hospůdce, kam ho zavede taxikář Danny. Následující dny Sue prožívá s Mikem další neobyčejné chvíle. Stane se svědkem, jak Mike s přehledem zadrží prchajícího lupiče, jak zažene na útěk dvojici mladých lupičů, kteří se je pokusí oloupit o peníze. Rychle začíná být přesvědčena, že si nejen dokáže poradit v každé situace, ale svým přirozeným šarmem si dokáže získat všechny na svou stranu. A stejně tak cítí, že se do něj zamilovala. Jenže právě v tu chvíli Richard Mason veřejně oznámí jejich zasnoubení. Smutný Mike, který se do Sue také zamiloval a kterému už pomalu začínají vadit upjaté způsoby snobské smetánky, se rozhodne odejít. (oficiální text distributora)', '2016-01-04 17:59:06.22', 1986, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000086, '201601/ROMING.m4v', 'ROMing', 9, 0, 'cz', '  ', 'cz', 'ROMing', 'http://www.csfd.cz/film/223890-roming/', 'ROMING.jpg', 0, 'Roman (Marián Labuda) dal kdysi dávno kamarádovi slib, že až jejich děti dospějí, ožení se Romanův syn Jura s jeho dcerou. Cesty rodin se rozešly. Po letech přichází dopis, nevěsta dosáhla dospělosti a je třeba dávný slib naplnit. Potíž je v tom, že Jura studuje vysokou školu, chodí s atraktivní blondýnou, ženit se nechce a už vůbec nestojí o děvče odkudsi ze slovenské vesničky, které v životě neviděl. Po otcově naléhání ale nakonec souhlasí, že se pojede na tajemnou nevěstu podívat. Výprava, jíž velí tatínkův velmi svérázný kamarád Stano (Bolek Polívka), se stane nepřetržitým sledem bláznivých situací, často s komicko-katastrofickým nádechem. Stano má totiž v sobě nespoutanou duši věčného tuláka a neodolatelného dobrodruha. Na cestě tak přátelé zažijí množství komických příhod i dojemných situací, aby nakonec našli cestu především k sobě. Komediální road-movie ROMING o cestě tří mužů za tajemnou nevěstou, nabízí lákavý mix atraktivní situační komiky, laskavého humoru a typické člověčiny v duchu nejlepších tradic české veselohry. Za vznikem filmu stojí producentský tým, který je podepsán pod divácky velmi úspěšnými vieweghovskými adaptacemi - Román pro ženy (režie: Filip Renč) a Účastníci zájezdu (režie: Jiří Vejdělek). Román pro ženy překonal v českých kinech návštěvnost šest set tisíc diváků, Účastníci zájezdu se s téměř osmi sty tisíci diváky se stali nejnavštěvovanějším snímkem roku 2006. (oficiální text distributora)', '2016-01-04 17:59:06.232', 2007, '');
