INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000087, '201601/WildHogs.m4v', 'Dive svine', 15, 0, 'cz', 'cz', 'en', 'Wild Hogs', 'http://www.csfd.cz/film/225906-divocaci/', 'WildHogs.jpg', 0, 'Co kdybyste mohli podniknout výlet, který by změnil váš obyčejný život v divoké a nespoutané dobrodružství? V rozpustilé komedii Divočáci se čtyři přední hollywoodské hvězdy – Tim Allen, na dva Oscary nominovaný John Travolta, Martin Lawrence a na Oscara nominovaný William H. Macy – zhostily rolí kamarádů, kteří se jednoho dne rozhodnou radikálním způsobem opepřit svůj nudný život na předměstí a vydají se na motorkářský výlet napříč Amerikou. Oddech od práce a všedních povinností, o kterém tak dlouho snili, je konečně tady. Ani jeden z nich však nemá tušení, že se během cesty za svobodou a volností setkají se skutečným motorkářským gangem jménem Del Fuegos, a že je čekají odhalení překvapivých tajemství ve vlastních řadách. (oficiální text distributora)', '2016-01-04 17:59:06.265', 2007, '');
