INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000089, '201601/INSIDE_OUT.m4v', 'V hlave', 3, 0, 'cz', 'cz', 'en', 'Inside Out', 'http://www.csfd.cz/film/345734-v-hlave/', 'INSIDE_OUT.jpg', 0, 'Období dospívání může být velice komplikované, a Riley, která je vytržena ze svého starého života na americkém Středozápadu poté, co se její otec musí kvůli novému zaměstnání přestěhovat do San Francisca, není žádnou výjimkou. Stejně jako my všichni, i Riley je ovládána svými emocemi – Radostí (Amy Poehler), Strachem (Bill Hader), Hněvem (Lewis Black), Znechucením (Mindy Kaling) a Smutkem (Phyllis Smith). Emoce žijí v Řídícím centru, centrále uvnitř Rileyiny mysli, odkud jí pomáhají vypořádat se s nástrahami všedních dní. Jak se Riley se svými emocemi společně pokoušejí vyrovnat s problémy nových začátků v San Franciscu, propuká v Řídícím centru zmatek. Ačkoliv se Radost, Rileyina hlavní a nejdůležitější emoce, pokouší zachovat všem pozitivní myšlení, objevují se mezi jednotlivými emocemi neshody v tom, jak se co nejlépe vypořádat s novým městem, domovem a školou. (Falcon)', '2016-01-04 17:59:06.298', 2015, '');
