INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000094, '201602/DEAD_SNOW.m4v', 'Mrtvy snih / Dead Snow', 2, 0, 'no', 'en', 'no', 'Dead Snow', 'http://www.csfd.cz/film/248800-mrtvy-snih/', 'DEAD_SNOW.jpg', 0, 'Skupinka studentů medicíny vyloží zásoby piva a nic nenasvědčuje tomu, že by jejich pobyt na horské chatě neměl být odpočinkovou selankou. Někteří z nich se vysmějí i nezvanému hostovi, který jim vypráví příběh o nacistickém vojsku zmrzlém za druhé světové války opodál. Tajemným nočním hostům je ale lepší věřit. Přinejmenším pokud vystupují v gore zombie hororové komedii, jako je tato. Snímek pracuje s ironií i černým humorem a dokáže vtipně variovat, ale i využívat typické atributy žánru. Postavy zde nejsou určeny k rychlému pobití, namísto toho srdnatě bojují proti přesile a snímek je nechává žít tak dlouho, abychom jejich případný konec považovali za zklamání. Napětí i zájem o vývoj situace jsou posilovány i zapojením několika paralelních dějových rovin. Kolik hrdinů se z této „zombie šlamastyky“ dostane? A jakým vynalézavým způsobem odejdou ze světa ti, kterým není přežití přáno? (MFF Karlovy Vary)', '2016-02-06 15:08:47.45', 2009, '');
