INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000095, '201602/GRAVE_OF_THE_FIREFLIES.m4v', 'Hrob Svetlusek / Grave of the Fireflies', 3, 0, 'en', 'en', 'jp', 'Hotaru no haka', 'http://www.csfd.cz/film/129496-hrob-svetlusek/', 'GRAVE_OF_THE_FIREFLIES.jpg', 0, 'Ponurý příběh vypráví o dvou sourozencích, kteří se snaží přežít hrůzy Druhé světové války. Seita a jeho malá sestřička Setsuko jsou ponecháni osudu, když jejich maminka zemře na následky zranění po ničivém bombardování. Otec slouží u japonského námořnictva, ale rodina od něj už dlouhou dobu nedostala žádné zprávy. Dětí se ujme teta, avšak kvůli častým neshodám Seita se Setsuko odchází. Najdou si přístřeší, ale obstarat dostatek jídla je téměř nemožné... Hrob světlušek je založen na stejnojmenném románu podle skutečných událostí Akiyuki Nosaky. Bývá považován za jeden z nejpůsobivějších protiválečných snímků. V kinech byl promítán společně s pohádkovým anime Můj soused Totoro, který měl diváky po traumatickém zážitku povzbudit. (Fabienne)', '2016-02-06 15:08:47.623', 1988, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000096, '201602/Jackie Brown.m4v', 'Jackie Brown', 15, 0, 'cz', 'cz', 'en', 'Jackie Brown', 'http://www.csfd.cz/film/8850-jackie-brown/', 'Jackie Brown.jpg', 0, 'Adaptace románu Rum Punch klasika soudobé drsné školy a bestsellerového spisovatele Elmora Leonarda. Jackie Brownová je černá letuška, která si přivydělává pašováním špinavých peněz drogového dealera a obchodníka. Je chycena dvěma policejními agenty, kteří ji však slíbí shovívavost, pokud pomůže svého šéfa dopadnout a usvědčit. Jackie, která je na svobodě jen díky kauci svého milence, zosnuje plán, jak najednou získat milionový balík, zbavit se policistů i nepohodlného šéfa, jehož nejčastější replikou je věta "řekneš slovo a jseš mrtvá". Jenže takový plán má spoustu rizik... (oficiální text distributora)', '2016-02-06 15:08:47.632', 1997, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000115, '201602/THE_MARTIAN.m4v', 'Martan', 4, 0, 'cz', 'cz', 'en', 'The Martian', 'http://www.csfd.cz/film/384557-martan/', 'THE_MARTIAN.jpg', 0, 'Astronaut Mark Watney (Matt Damon) během mise na Mars málem zahynul v prašné bouři. Zbytek jeho posádky planetu opustil v domnění, že je mrtvý. Ale Watney přežil a ocitne se tak omylem sám na nepřátelské planetě. Má k dispozici jen hubené zásoby a tak musí využít veškerý důvtip, invenci a technické znalosti, aby nejen přežil, ale také nalezl způsob, jak vyslat signál na Zemi, že je naživu. V okamžiku, kdy se mu to podaří, začne miliony kilometrů daleko od něj NASA a mezinárodní tým vědců neúnavně pracovat, aby dostali tohoto „Marťana" domů. Zatímco členové jeho posádky souběžně prokazují svou odvahu na téměř nemožné záchranné misi. (CinemArt CZ)', '2016-02-13 08:12:59.913', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000097, '201602/HOTEL_TRANSYLVANIA.m4v', 'Hotel Transylvania', 3, 0, 'cz', 'cz', 'en', 'Hotel Transylvania', 'http://www.csfd.cz/film/239678-hotel-transylvanie/', 'HOTEL_TRANSYLVANIA.jpg', 0, 'Málokdo o Draculovi ví, že není jen princem temnoty, ale také vlastníkem stylového Hotelu Transylvánie - luxusního pětihvězdičkového resortu, kde se mohou všechna monstra světa (tedy Frankenstein, Mumie, vlkodlaci, Zombie, neviditelný muž a spoustu dalších) dostat daleko od všech a být jen sami sebou v bezpečném prostředí bez lidí. Ale pro Drákulu existuje ještě daleko důležitější důvod, proč držet obyčejné smrtelníky dál od Hotelu Transylvánie: aby ochránil Mavis, svojí milovanou náctiletou dceru. Jako většina teenagerů i Mavis chce opustit hnízdo a prozkoumávat velký svět za hranicemi hotelu. Jenže to je myšlenka, která Draculu skutečně děsí! A navíc se blíží termín, kdy se budou ve velkém stylu slavit narozeniny jeho milované Mavis a v hotelu se nečekaně objeví člověk… (oficiální text distributora)', '2016-02-06 15:08:47.642', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000098, '201602/DJANGO_UNCHAINED.m4v', 'Divoky Django / Django Unchained', 15, 0, 'cz', 'cz', 'en', 'Django Unchained', 'http://www.csfd.cz/film/294824-nespoutany-django/', 'DJANGO_UNCHAINED.jpg', 0, 'Kultovní režisér Quentin Tarantino natočil jižanské drama z období před vypuknutím Občanské války, jehož hlavním hrdinou je otrok Django. Ten má šanci získat díky nájemnému lovci lidí dr. Schultzovi svobodu, pokud se mu povede dopadnout vraždící bratry Brittlovi. Djangovi se tak zároveň naskytne příležitost najít ženu, kterou musel před lety nedobrovolně opustit. Ani jeden z hlavních hrdinů však netuší, jak blízko jsou nejnebezpečnějšímu dobrodružství svého života. (oficiální text distributora)', '2016-02-06 15:08:47.65', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000099, '201602/ELIZABETH.m4v', 'Elizabeth', 15, 0, 'en', 'en', 'en', 'Elizabeth', 'http://www.csfd.cz/film/12886-kralovna-alzbeta/', 'ELIZABETH.jpg', 0, 'Svrchovanost Anglie je pod stálým tlakem okolních států a v zemi je třeba obnovit silnou katolickou víru. Umírající královna už nemá dost sil a jediným potenciálním nástupcem trůnu je její mladá sestra Alžběta, zapřísáhlá protestantka. Selhávají i naděje na záchranné manželství s některým ze sousedních panovníků a osud celé Anglie se ocitá na okraji hluboké propasti... (oficiální text distributora)', '2016-02-06 15:08:47.659', 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000100, '201602/DEAD_SNOW_2.m4v', 'Mrtvy snih 2 / Dead Snow 2', 2, 0, 'en', 'en', 'no', 'Dead Snow 2', 'http://www.csfd.cz/film/358014-dead-snow-rudy-vs-mrtvy/', 'DEAD_SNOW_2.jpg', 0, 'V roce 2009 přinesl Wirkola na stříbrná plátna kin hororovou komedii Mrtvý sníh, ve které nechal ožít nacistické zombie na zamrzlých pláních severního Norska. Jejich mise však neskončila a ve snímku Dead Snow: Rudý vs. Mrtvý se chystají na totální genocidu, aby tak dostáli svému poslání – nastolit vládu jednoho vůdce. Pokud si myslíte si, že váš nejhorší den je ten, kdy zabijí vaši přítelkyni sekyrou, vám uříznou motorovou pilou ruku a s hrůzou v očích sledujete, jak armáda nacistických zombie požírá vaše kamarády, tak to se šeredně mýlíte. To je teprve začátek. (Film Europe)', '2016-02-06 15:08:47.667', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000101, '201602/KILL_BILL_1.m4v', 'Kill Bill', 15, 0, 'cz', 'cz', 'en', 'Kill Bill', 'http://www.csfd.cz/film/43483-kill-bill/', 'KILL_BILL_1.jpg', 0, 'Bývalá členka špičkového zabijáckého komanda (Uma Thurman) se rozhodne navždy skončit s minulostí a vdát se. Její svatební den se však změní v krvavá jatka v okamžiku, kdy na ni zaútočí její bývalý šéf Bill (David Carradine) a zabije ji. Mladá žena však nezemřela, i když si to všichni myslí: šťastnou náhodou vražedný útok přežila, ale upadla do kómatu. Po pěti mučivě dlouhých letech se vrací z temného zápraží smrti s jedinou myšlenkou: pomstít se všem, kteří jí ublížili, bez ohledu na to, co je k tomu vedlo. Ke všemu odhodlaná hrdinka, která si říká Nevěsta, však teprve musí zjistit, kdo je proti ní - a také to, jestli neexistuje někdo, kdo by mohl stát na její straně... Chladnokrevná pomsta, mrazivá nenávist a vzpomínky na zrazenou lásku vedou elitní vražedkyni po cestě krve, ze které - možná - není návratu. Po pěti letech, která uplynula od světového úspěchu Jackie Brownové, maniak z videopůjčovny Quentin Tarantino stvrzuje svou pověst kultovního autora akčním příběhem o pomstě, lásce a nenávisti. Klíčovou roli v jeho novém autorském opusu hrají klasické asijské filmy ze 70. let využívající prvků wuxia filmů. Chci natočit nejlepší akční film všech dob!, prohlásil Tarantino jen napůl v žertu na loňském MFF v Cannes, kde svůj projekt poprvé představil. Ke slavnostní světové premiéře na nejprestižnějším evropském festivalu letos v květnu také směřoval natáčení filmu, na jehož soubojových a akčních scénách se podílel legendární Woo-Ping Yuen (Matrix, Tygr A Drak). Podobně jako několikrát předtím, i nyní si Tarantino pohrává s chronologií svého příběhu: vyprávění je členěno do deseti kapitol, jejichž časová posloupnost je rafinovaně zpřeházena. Vedle obdivovaných hvězd žánru (Sonnyho Chiby a Gordona Liu) exceluje v v hlavní roli nejnetrpělivěji očekávaného hitu podzimní sezóny Uma Thurman, která se objevila už v režisérově kultovním morytátu Pulp Fiction - Historky Z Podsvětí. (oficiální text distributora)', '2016-02-06 16:16:32.175', 2003, '');
