INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000102, '201602/IN_CASE_OF_DISAPPEARANCE.m4v', 'Boj snezneho pluhu s mafii / Kraftidioten', 15, 0, 'no', 'en', 'no', 'Kraftidioten', 'http://www.csfd.cz/film/351849-boj-snezneho-pluhu-s-mafii/', 'IN_CASE_OF_DISAPPEARANCE.jpg', 0, 'Nils se se svým sněžným pluhem prohání zasněženými cestami v okolí chladného norského městečka. Proráží cesty pro své spoluobčany, což považuje za velmi zodpovědnou práci, kterou ostatní dokáží náležitě ocenit. Jednoho dne je dokonce jmenován Občanem roku, ale zároveň se v ten den dozví o úmrtí svého syna. Předávkování drogami není pro Nilse důvěryhodným vysvětlením, a tak vezme spravedlnost do vlastních rukou. Vyhrát krvavý spor s mafií není lehké, avšak Nils už nemá co ztratit. Navíc má v záloze mnohem silnější zbraně – mohutný stroj a štěstí začátečníka. Příběh "spravedlivé" pomsty s lavinou mrtvých těl vypráví režisér s pořádnou dávkou černého humoru, překvapivých zvratů i elegancí a výsledkem je zábavný a inteligentní akční thriller z prostředí zasněžených drsných norských hor. (Film Europe)', '2016-02-06 16:16:32.347', 2014, '');
