INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000106, '201602/KillBill2.m4v', 'Kill Bill 2', 15, 0, 'cz', 'cz', 'en', 'Kill Bill 2', 'http://www.csfd.cz/film/136304-kill-bill-2/', 'KillBill2.jpg', 0, 'Svatební obřad bývalé členky Komanda Zmijí Zabijáků, Černé Mamby, změnil její pomstychtivý ex-milenec Bill s bývalými kumpány v krvavý masakr. Těhotná Nevěsta však kulku vpálenou do hlavy přežila: po čtyřech letech strávených v kómatu procitla a vydala se na cestu pomsty. Už zlikvidovala první dvě položky na svém seznamu smrti O-Ren Ishii zvanou Korálovec a Vernitu Green Smrtonoše. Zbývá jí však zabít ještě Elle Driver přezdívanou Kalifornský Horský Had, Budda zvaného Chřestýš a samozřejmě bývalého šéfa, milence a otce jejího dítěte - Billa. Ti tři však už vědí, co jim hrozí - a myslí si, že jsou na to připraveni. Druhá část mistrovského akčního hitu Quentina Tarantina (první měla v českých kinech premiéru loni v říjnu) líčí další osudy odhodlané Nevěsty a vyvrcholení její pomsty. Ve strhujícím příběhu, vyprávěném jak je zvykem režiséra Gaunerů (Reservoir Dogs), Pulp Fiction Historek Z Podsvětí a Jackie Brownové - na přeskáčku, se však vracíme i do minulosti. Zbývá toho ještě spoustu dopovědět: Co se vlastně stalo v kostele v texaském El Pasu? A proč se to stalo? Žije dcera, již Nevěsta považovala za mrtvou? Jakým výcvikem prošla hrdinka v Japonsku? A jak se vlastně ve skutečnosti jmenuje? Ale především: dokáže Nevěsta naplnit svou krvavou pomstu tak, jak si naplánovala? (oficiální text distributora)', '2016-02-13 08:12:59.811', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000118, '201602/Perinbaba.m4v', 'Perinbaba', 3, 0, 'sk', '  ', 'sk', 'Perinbaba', 'http://www.csfd.cz/film/4726-perinbaba/', 'Perinbaba.jpg', 0, 'Na začátku zachrání Paní Zima malého Jakubka ze spárů smrti, protože se jí líbí, že se nebál ani samotné zubaté. Vezme jej k sobě, aby jí pomáhal natřásat velkou peřinu, kterou dělá na zemi sníh. Přes neplechy, které rozpustilý chlapec působí (například chumelenici na poušti), má kouzelná Perinbaba Jakuba ráda a pomáhá mu i poté, co opustí její sídlo a vydá se mezi lidi, kam jej přitahuje láska ke krásné Alžbětce. Než však budou moci vystrojit svatbu, čeká Jakuba i Alžbětku ještě nejedno trápení se zlou macechou a její dcerou Dorou, a také se smrtí, která po Jakubovi stále pase. (oficiální text distributora)', '2016-02-13 08:12:59.935', 1985, '');
