INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000129, '201602/hotel_transylvania_2.m4v', 'Hotel Transylvania 2', 3, 0, 'cz', 'cz', 'en', 'Hotel Transylvania 2', 'http://www.csfd.cz/film/332915-hotel-transylvanie-2/', 'hotel_transylvania_2.jpg', 0, 'Zdá se, že Hotelu Transylvánie se začíná dařit... Dracula konečně ustoupil ze svých striktních zásad ubytovávat v hotelu pouze příšery, a ten se proto otevírá i lidským návštěvníkům. Za zavřenými dveřmi krypty ale Draculu sžírají pochybnosti o tom, zda se jeho napůl lidský a napůl upíří vnuk Dennis vůbec někdy stane upírem. Když tedy Mavis ve společnosti Johnnyho vyrazí na návštěvu lidské části příbuzenstva - na které ji ostatně také čeká nejedno překvapení - požádá "Děda" Drac o pomoc své přátele Franka, Murrayho, Waynea a Griffina a společně Dennisovi nachystají "kurz pro strašidla". Nemají ale nejmenší tušení, že se k návštěvě své rodiny v hotelu chystá také Dracův rozmrzelý a velmi, velmi, velmi staromódní otec Vlad. Když se tedy Vlad dozví, že jeho pravnuk není čistokrevný upír - a ještě k tomu teď do Hotelu Transylvánie mohou jezdit i lidé - nastane mela, ze které by dost možná i upírovi ztuhla krev v žilách. (Falcon)', '2016-02-16 18:55:24.092', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000130, '201603/PRAVDIVE_LZI.m4v', 'Pravdive lzi', 15, 0, 'cz', 'cz', 'en', 'True Lies', 'http://www.csfd.cz/film/1251-pravdive-lzi/', 'PRAVDIVE_LZI.jpg', 0, 'Zvláštní agent Harry Tasker (Arnold Schwarzenegger) vede dvojí život. Plynule mluví šesti jazyky, je dobře vycvičen pro kontrašpionáž a pracuje jako mezinárodní špión pro vládní organizaci nejvyššího utajení, ale jeho žena Helen (Jamie Lee Curtis) už patnáct let věří tomu, že má za muže obyčejného nudného obchodníka s počítači. Harry má všechny schopnosti, prostředky a odvahu chránit svoji zemi... jen jestli dokáže ochránit také svoje manželství? (oficiální text distributora)', '2016-03-27 13:37:21.549', 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000131, '201603/IN_THE_NAME_OF_THE_FATHER.m4v', 'V mene otca', 15, 0, 'en', 'cz', 'en', 'In the Name of Father', 'http://www.csfd.cz/film/8344-ve-jmenu-otce/', 'IN_THE_NAME_OF_THE_FATHER.jpg', 0, 'Politické drama režiséra Jima Sheridana Ve jménu otce se zabývá známým případem tzv. Guilfordské čtyřky. Po atentátu IRA na hospodu v Guilfordu v roce 1974, při němž bylo pět mrtvých a několik desítek těžce raněných, což mělo za následek tvrdá opatření proti terorismu, byli ze zločinu obviněni a odsouzeni čtyři mladí lidé a sedm dalších obžalovaných (včetně dětí) dostalo vysoké tresty za spoluúčast. Až patnáct let po vynesení rozsudku byla v odvolacím soudu pod tlakem veřejného mínění kauza zrušena, ale úplné spravedlnosti nebylo a ani nemohlo být učiněno zadost. Snímek vznikl na základě autobiografické knihy hlavního z odsouzených, Gerryho Conlona, Prokázaná nevina. Ve vyprávění protagonisty se odvíjejí události tak, jak je líčí na kazetě své zástupkyni u odvolacího soudu, advokátce Gareth Peirceové, která měla později hlavní zásluhu na osvobozujícím rozsudku. Jsou tu zachyceny události před atentátem, zadržení domnělých pachatelů, soudní proces, pobyt ve vězení a konečně vítězný odvolací soud. V centru stojí vztah Gerryho k jeho otci, který byl odsouzen spolu s ním a žil s ním v jedné cele, ale který se bohužel svobody nedočkal. Otec jako věřící irský katolík dodržoval zásady Písma a těžce nesl, že jeho syn byl lehkomyslný flákač a zloděj. Generační a rodinný střet je ovšem provázen závěrečným smířením. Film sice jednoznačně odsuzuje praktiky IRA, ale zároveň předkládá jako objektivní fakta tvrzení, jež mohou být zpochybňována. Vychází jen ze subjektivního pohledu oběti a místy až zbytečně démonizuje policejní zvůli. V zásadě však klade důraz na obecně lidské hledisko, prezentované obtížným bojem proti nespravedlnosti. (oficiální text distributora)', '2016-03-27 13:37:21.65', 1993, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000132, '201603/THE_DICTATOR.m4v', 'Diktator', 9, 0, 'cz', 'cz', 'en', 'The Dictator', 'http://www.csfd.cz/film/293058-diktator/', 'THE_DICTATOR.jpg', 0, 'Jmenuje se Aladdin a od smrti Kim Čong Ila je považován za posledního mohykána mezi diktátory. Komik a mystifikátor Sacha Baron Cohen si po kazašském Boratovi a rakouském Brunovi navlékl mužný plnovous a spolu s ním masku neomezeného vládce zemičky jménem Wadiya, kterou chce ze všech sil zachránit před demokracií. Takového borce by chtěl mít za svého prezidenta každý. Dominuje ve všech oblastech lidské činnosti, a přesto je schopen večer po vladaření najít čas a energii na porci kvalitního sexu s libovolnou hollywoodskou herečkou, která je ochotná do jeho subsaharského ráje zavítat. Hvězda Transformers Megan Fox by mohla vyprávět. Ke svému lidu se chová jako milující otec, jenž ví, že je škoda každé rány, která padne vedle. I proto tak rád vynáší rozsudky smrti, většinou ve formě setnutí mečem. Tradice se musí dodržovat, i když se právě snažíte vyvinout vlastní jadernou bombu. Na světě jsou ale i země, které nebezpečí demokracie neodolaly, a jejich představitelé se rozhodli pozvat Aladdina do budovy OSN, aby mu vážně promluvili do stylu vládnutí. Aladdin, který se nikoho a ničeho nebojí, se k výzvě postaví čelem a vyrazí do Ameriky. Pro jistotu v doprovodu svého hlavního poradce (Ben Kingsley) a dvojníka, jenž je mu k nerozeznání podobný, až na tu mozkovou kapacitu (pasáček koz ji mít extra velkou nepotřebuje). Až ve stínu Sochy svobody Aladdin na vlastní kůži pozná, jak zhoubné zřízení ta „slavná“ vláda lidu může být. Než se se rozkouká, skončí vinou spiklenců polonahý a hlavně k nepoznání oholený na ulici, odkud může jen zoufale zírat, jak jeho imbecilní záskok zničí během okamžiku všechno, co vybudoval on, jeho táta, táta jeho táty a všichni předešlí tátové. Průvodce „džunglí“ normálního světa najde shodou náhod v člověku, od kterého by si jinak udržoval co největší distanc. V ženě (Anna Faris)! Mezi její další zásadní vady patří to, že je ortodoxní feministkou s chlupatým podpaždím, levicovou aktivistkou, vegetariánkou a nesnáší diktátory všeho druhu. Přesto se Aladdin s její pomocí pokusí pro svou zemi diktaturu a tyranii zachránit. (oficiální text distributora)', '2016-03-27 13:37:21.66', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000133, '201603/The Last Witch Hunter.m4v', 'Posledny lovec carodejnic', 4, 0, 'cz', 'cz', 'en', 'The Last Witch Hunter', 'http://www.csfd.cz/film/337836-posledni-lovec-carodejnic/', 'The Last Witch Hunter.jpg', 0, 'Armády lovců čarodějnic, bojovníci „Sekery a kříže", vedou svou válku proti hrozivému nepříteli po celá staletí a bojují s čarodějnicemi na všech místech Země. Jeden z nich, Kaulder, zničí samotnou čarodějnickou Královnu a ta ho s posledním dechem prokleje k věčnému životu. Navždy odtržen od své rodiny pak Kaulder tráví stovky let tím, co umí nejlépe – loví a zabíjí čarodějnice. Dnes je posledním svého druhu a prořídly i řady nadpřirozených nepřátel. Jeho příběh ani boj ale nemají konec, zlé čarodějnické síly tu stále jsou, ukryty našim zrakům. Navíc se jim konečně povedlo přivést zpět k životu svou Královnu a ta žije jedinou myšlenkou. Rozpoutat na Zemi peklo, ukončit Kaulderovu nesmrtelnost a postavit lidstvo tváří tvář zkáze. Posledního lovce čarodějnic čeká v knize jeho života epická a bojem naplněná kapitola. Bude poslední? (Bontonfilm)', '2016-03-27 13:37:21.668', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000134, '201603/BORAT.m4v', 'Borat', 9, 0, 'cz', 'cz', 'en', 'Borat', 'http://www.csfd.cz/film/223672-borat-nakoukani-do-amerycke-kultury-na-obednavku-slavnoj-kazasskoj-narodu/', 'BORAT.jpg', 0, 'Borat Sagdijev je šestým nejznámějším Kazachem a nejlepším televizním reportérem Kazašské státní televize, která ho vyšle do USA, aby zde natočil dokument o této "zemi neomezených možností". A případně si našel novou manželku, protože tu starou tlustou mu znásilnil a roztrhal medvěd. Známý britský komik Sacha Baron Cohen bývá pro své imitační a improvizační schopnosti často přirovnáván k Peteru Sellersovi. Ve svém pořadu "Da Ali G Show" úspěšně zesměšňuje slavné osobnosti i anonymní lidi na ulici tím, že se vydává nejen za zaostalého nemytého kazašského reportéra Borata, ale také za britského předměstského rappera Aliho G nebo za homosexuálního německého módního komentátora Bruna. (Jeho celovečerní "Ali G - Film" vyšel i u nás.) Film BORAT je natočen podobnou metodou. Cohen s filmovým štábem putuje po Spojených státech a uvádí do rozpaků nic netušící lidi, kteří se domnívají, že jsou svědky natáčení skutečného dokumentu pro Kazašskou televizi. Cohen jde ovšem tentokrát v drsnosti a politické nekorektnosti svého humoru mnohem dál, než bylo možné v televizní show. Nebude ušetřen nikdo! (Skutečná kazašská vláda nemá s tímto filmem nic společného a oficiálně proti němu podala protest.) (oficiální text distributora)', '2016-03-27 13:37:21.676', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000135, '201603/I_ROBOT_SPECIAL_EDITION_DISC_1.m4v', 'Ja, robot', 4, 0, 'cz', 'cz', 'en', 'I, Robot', 'http://www.csfd.cz/film/91975-ja-robot/', 'I_ROBOT_SPECIAL_EDITION_DISC_1.jpg', 0, 'Chicago rok 2035. Nedaleká budoucnost, ve které jsou roboti přirozenou součástí lidského života. Hlavní postava detektiv Del Spooner (Will Smith) začně vyšetřovat podezřelé úmrtí svého dlouholetého přítele doktora Milese Hogenmillera. Podle všech spáchal sebevraždu, ale Spooner věří, že za vraždou stojí robot, což je pro ostatní naprosto nepochopitelné a směšné. I přesto se s půvabnou psycholožkou Susan Calvin pustí do vyšetřování a podivné události na sebe nenechají dlouho čekat. (AkrObatiX)', '2016-03-27 13:37:21.685', 2004, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000136, '201603/Kletba zlateho kvetu.m4v', 'Kletba zlateho kvetu', 6, 0, 'cz', 'cz', 'zh', 'Man cheng jin dai huang jin jia', 'http://www.csfd.cz/film/223794-kletba-zlateho-kvetu/', 'Kletba zlateho kvetu.jpg', 0, 'Imperátor Ping, hlava dynastie Tang, vládce Číny desátého století, spalován neskonalou láskou i nenávistí vůči své ženě Phoenix rozehrává tragédii, která zasáhne každého člena královské rodiny. Panovník ví o skrývaném vztahu své ženy a jeho syna z prvního manželství prince Wana. Pronásledován stínem nevěry, panovník pomalu zabijí svou ženu jedem. Schyluje se k finálnímu boji. Na jedné straně císař, ovládaný pomstou, na druhé straně císařovna, bojující o holý život. Mezi nimi stojí tři synové, tři princové, kteří se musejí rozhodnout na čí stranu se přiklonit. Bolest a ztráta milovaných však čeká na každého z nich... (oficiální text distributora)', '2016-03-27 13:37:21.692', 2006, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000137, '201603/Mune.m4v', 'Muno', 3, 0, 'cz', 'cz', 'en', 'Mune', 'http://www.csfd.cz/film/407369-mune-strazce-mesice/', 'Mune.jpg', 0, 'Aniž by o to žádal, je malý Mune, snílek a šibal, v bájném světě jmenován strážcem Měsíce, tedy tím, kdo přináší noc a bdí nad sny. Mune ale přitahuje katastrofy a omylem umožní strážci temna, aby ukradl Slunce. S pomocí hrdého strážce Slunce Sohona a křehké Ciry se Mune vydává za neobyčejným dobrodružstvím, jež z něj učiní legendu mezi strážci! (Freeman Ent.)', '2016-03-27 13:37:21.7', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000138, '201504/THE_FORCE_AWAKENS.m4v', 'Sila se probouzi', 4, 42000023, 'cz', 'sk', 'en', 'The Force Awakenes', 'http://www.csfd.cz/film/328343-star-wars-sila-se-probouzi/', 'THE_FORCE_AWAKENS.jpg', 7, 'Lucasfilm a vizionářský režisér J.J. Abrams spojili síly, aby vás znovu přenesli do předaleké galaxie. Star Wars se vracejí na plátna kin filmem Star Wars: Síla se probouzí.', '2016-04-21 19:25:51.786', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000139, '201604/Jizda.m4v', 'Jizda', 6, 0, 'cz', '  ', 'cz', 'Jizda', 'http://www.csfd.cz/film/8804-jizda/', 'Jizda.jpg', 0, 'Svěrákův příběh (na scénáři se podílel s Martinem Dostálem) je takřka průzračný a vychází z velmi obehraných premis. Jedná se o českou road movie, aneb jak praví popisek: Svobodný film o dvou chlapcích, jedné dívce, zeleném kabrioletu a horkých letních hrách. Radek (Radek Pastrňák) a Franta (Jakub Špalek) si na začátku zakoupí zmíněný automobil, trochu svérázným způsobem ho upraví a rozjedou se vstříc volání dálek a dobrodružství. Cestou přiberou trochu vyplašenou a trochu provokující dívku Annu (Anna Geislerová), kterou zpovzdálí sleduje její žárlivý přítel v černém opelu. A pak už jen jedou, užívají si bezstarostné pohody, aniž by tušili, že v souladu s pravidly žánru bude mít jejich uvolněná jízda dost dramatický konec. (oficiální text distributora)', '2016-04-22 19:46:00.181', 1994, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000140, '201604/JUSTIN.m4v', 'Justin, maly velky rytir', 3, 0, 'cz', 'cz', 'en', 'Justin and the Knights of Valour', 'http://www.csfd.cz/film/322732-justin-jak-se-stat-rytirem/', 'JUSTIN.jpg', 0, 'Justin je středověký chlapec z dobré rodiny a v království, kde žije, panuje zákon a pořádek. Osvícená královna zde zavedla spolu se svým kancléřem, Justinovým otcem, nekonečné množství předpisů a hlavním náboženstvím je byrokracie. Na věci, které se dříve řešily mečem a soubojem, je tu po ruce vyhláška či nařízení a rytíři byli z království vykázáni. Otec naplánoval Justinovi výnosnou kariéru právníka, ale Justin má úplně jiný sen: stát se rytířem. Vždyť jeho děda byl jedním z nejslavnějších a nejudatnějších. Rozhodne se odejít z domova a vydává se za dobrodružstvím, na jehož konci vidí brnění, meč a věčnou slávu. Potkává odvážnou a prostořekou dívku Taliu, zmateného kouzelníka a především starého šermíře a dvojici mnichů, kteří začnou Justina ve starobylém klášteře opravdu cvičit na opravdického rytíře. Představa Justina jako chrabrého rytíře začíná být jen o malou trochu pravděpodobnější, když se objeví opravdová výzva. Do království se vrací Sir Heraclio, kdysi vyhnaný rytíř, se svou armádou a s touhou se pomstít a nasadit si korunu. A protože rytíři byli ve jménu pořádku vyhnáni, pochlapit se musí alespoň učedník...Justin. S chytrou a statečnou dívkou Taliou, která se Justinovi tak nějak asi začíná líbit, a s kouzelníkem, který umí do pěti počítat, ale dál už ne, stojí Justin před svým prvním velkým úkolem. Zachránit království. Naštěstí je tu ještě krokodýl Gustav. Ačkoliv starý krokodýl má být moudrý a rozvážný, Gustav má stejně jako Justin svůj sen: Chce být drakem. A mít na své straně draka a jeho leteckou podporu se může hodit. (oficiální text distributora)', '2016-04-22 19:46:00.281', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000141, '201604/PRINC_EGYPTSKY.m4v', 'Princ Egyptsky', 3, 0, 'cz', 'cz', 'en', 'The Prince of Egypt', 'http://www.csfd.cz/film/12546-princ-egyptsky/', 'PRINC_EGYPTSKY.jpg', 0, 'Biblický príbeh o Mojžišovi, ktorý sa stal egyptským princom a nevlastným bratom budúceho faraóna Ramzesa. Keď však spoznal svoj židovský pôvod, v pravý čas opustil palác, aby naplnil božiu vôľu a vyviedol svoj ľud z egyptského otroctva do zasľúbenej zeme. (oficiální text distributora)', '2016-04-22 19:46:00.289', 1998, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000142, '201604/THE_GOOD_DINOSAUR.m4v', 'Dobry dinosaurus', 3, 0, 'cz', 'cz', 'en', 'The Good Dinosaur', 'http://www.csfd.cz/film/306068-hodny-dinosaurus/', 'THE_GOOD_DINOSAUR.jpg', 0, 'Animovaný film „Hodný dinosaurus" si pohrává s otázkou: Co kdyby asteroid, který jednou provždy změnil život na Zemi, naši planetu minul a obří dinosauři nikdy nevyhynuli? Pixar Animation Studios vás zvou na dobrodružnou výpravu do světa dinosaurů, kde se Apatosaurus jménem Arlo spřátelí s člověkem. Na cestách drsnou, ale překrásnou divočinou se Arlo naučí čelit svému strachu a zjistí, že je schopen velkých činů. (Falcon)', '2016-04-22 19:46:00.298', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000143, '201604/BRATRICKU_KDE_JSI.m4v', 'Bratricku kde jsi', 9, 0, 'cz', 'cz', 'en', 'Brother, Where Art Thou', 'http://www.csfd.cz/film/1611-bratricku-kde-jsi/', 'BRATRICKU_KDE_JSI.jpg', 0, 'Mississippi, třicátá léta. Tři trestanci Everett Ulysses McGill (George Clooney), Pete (John Turturro) a Delmar (Tim Blake Nelson), vzájemně spoutáni, zdrhají z lapáku krajinou v naději na nalezení pokladu o výši 1 200 000 dolarů. Na cestě potkávají bluesmana Tommy Johnsona (Chris Thomas King), který se k nim připojí a společně u místního hudebního vydavatele nahrají písničku, která se stane přes noc hitem jihu. Jenomže získat poklad a ve zdraví přežít extempore s Ku-Klux-Klanem a nesmlouvavé policejní pronásledovatele nebude zrovna hračka... (oficiální text distributora)', '2016-04-22 19:46:00.305', 2000, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000144, '201604/SPECTRE.m4v', 'Spectre', 15, 15, '  ', '  ', '  ', 'Spectre', 'http://www.csfd.cz/film/328151-spectre/', 'SPECTRE.jpg', 24, 'James Bond dostane záhadný odkaz z minulosti, který ho přivede na stopu známé zločinecké organizace. Postupně rozplétá složitou pavučinu lží, aby nakonec odhalil děsivou pravdu o SPECTRE. (Forum Film CZ)', '2016-04-29 04:53:50.855', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000145, '201604/JEN_BUH_ODPOUSTI.m4v', 'Jen buh odpousti', 15, 0, 'cz', '  ', 'en', 'Only God Forgives', 'http://www.csfd.cz/film/288711-jen-buh-odpousti/', 'JEN_BUH_ODPOUSTI.jpg', 0, 'Před deseti lety Julian zabil policajta, ale podařilo se mu utéct. Dnes je z něj jeden z největších drogových dealerů v Bangkoku. Když Julianův bratr zabije prostitutku, policie si pozve na pomoc Changa – Anděla pomsty, který dovolí otci zavražděné, aby ho osobně zabil. Tím však rozpoutá peklo, protože do situace se vloží Julianova matka, šéfka jedné z nejmocnějších kriminálních organizací, která Juliana vysílá pomstít svého bratra. (oficiální text distributora)', '2016-04-29 04:53:50.986', 2013, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000146, '201604/DEN_ZRADY.m4v', 'Den Zrady', 15, 0, 'cz', '  ', 'en', 'The Ides of March', 'http://www.csfd.cz/film/272410-den-zrady/', 'DEN_ZRADY.jpg', 0, 'Snímek Den zrady je napínavým příběhem o sexu, ambicích, loajalitě, zradě a pomstě, který se odehrává na pozadí okázalého divadla moderní politiky. Film mapuje osudy mladého tiskového mluvčího, který se stane obětí zákulisních intrik, zrádných manipulací zkušených kolegů a svodů mladé praktikantky. (oficiální text distributora)', '2016-04-29 04:53:51.039', 2011, '');
