INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000248, '201609/18 The Dragons Secret.m4v', 'Draci tajemstvi (18)', 3, 42000200, 'cz', 'cz', 'en', 'The Dragons Secret', 'http://www.imdb.com/title/tt1870181/?ref_=fn_al_tt_2', '18 The Dragons Secret.jpg', 18, 'Directed by Victor Cook.  With Frank Welker, Mindy Cohn, Grey Griffin, Matthew Lillard. After an exchange student named MAI LEE shows up in town to stay with Velma, strange Kung Fu wizards begin attacking the town trying to get her. Turns out Mai Lee has a RING they are after.', '2016-09-11 09:55:55.891', 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000158, '201605/BLUE_VALENTINE.m4v', 'Blue Valentine', 6, 0, 'cz', 'cz', 'en', 'Blue Valentine', 'http://www.csfd.cz/film/269398-blue-valentine/', 'BLUE_VALENTINE.jpg', 0, 'Blue Valentine je příběh o nalezené a ztracené lásce. Odehrává se v několika časových rovinách, kdy se mísí okamžiky ze současnosti a romantické vzpomínky na minulost. V tomto dojemném a otevřeném portrétu jednoho vztahu se jako Dean a Cindy představí Ryan Gosling a Michelle Williams, která byla za svůj výkon nominována na Oscara®. Ve snaze zachránit rozpadající se manželství se tento mladý pár rozhodne strávit jednu noc bez své dcery. Ještě před šesti lety leželi vedle sebe a jejich romantické námluvy dokreslovala hravá scenérie. Avšak jejich společná cesta nakonec pomalu spěla k hlubokému zármutku, uvadající lásce a nesplněným slibům. (oficiální text distributora)', '2016-05-07 06:37:53.319', 2010, '');
