INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000185, '201606/INDIANA_CRYSTAL_SKULL.m4v', 'Indiana Jones a Kralovstvo krystalovej lebky', 15, 42000188, 'cz', 'cz', 'en', 'Indiana Jones and the Kingdom of the Crystal Skull', 'http://www.csfd.cz/film/134454-indiana-jones-a-kralovstvi-kristalove-lebky/', 'INDIANA_CRYSTAL_SKULL.jpg', 4, 'Příběh se odehrává symbolicky přesně devatenáct let po Poslední křížové výpravě, v době rodící se Studené války. Indiana Jones je v něm profesorem archeologie na prahu penze, jenž si navzdory bouřlivé minulosti ze všeho nejvíc užívá klid a nebezpečně se začíná podobat vlastnímu otci. Staré instinkty se v něm probudí ve chvíli, kdy se objeví stopy vedoucí k legendární Křišťálové lebce a hlavně informace, že po tomto údajně mocném artefaktu prahnou sovětští agenti. Indy proto setřese únavu a vydá se na další mimořádně dobrodružnou cestu, jež ho zavede až do nitra peruánské džungle, v níž se má tahle vzácná relikvie ukrývat. Jeho úhlavním nepřítelem bude tentokrát všehoschopná ruská agentka Irina Spalko (Cate Blanchett) doprovázená armádou hrdlořezů. Ani Indy není na svou výpravu sám. Na jeho straně stojí spřízněný dobrodruh Mac (Ray Winston), který má ovšem tendenci ho každou chvíli podrazit, napomádovaný frajírek Mutt Williams (Shia LaBeouf), který si permanentně utahuje z jeho věku, a jeho první a zřejmě životní láska Marion (Karen Allen). (oficiální text distributora)', '2016-06-14 18:04:19.399', 2008, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000186, '201606/Toy Story 3-11.m4v', 'Toy Story 3: Pribeh hraciek', 3, 42000187, 'cz', 'cz', 'en', 'Toy Story 3', 'http://www.csfd.cz/film/229387-toy-story-3-pribeh-hracek/', 'Toy Story 3-11.jpg', 3, 'Ve filmu TOY STORY 3: PŘÍBĚH HRAČEK se tvůrci milované série příběhů o hračkách „Toy Story“ vrací do kouzelného světa nám dobře známých hraček. Chlapec Andy už vyrostl a chystá se na vysokou školu. Buzze, Woodyho a zbytek hraček tak čeká nejistá budoucnost. TOY STORY 3: PŘÍBĚH HRAČEK je zbrusu nová dobrodružná komedie, ve které se naši staří známí hrdinové dostanou do školky plné rozjívených dětí a batolat. Obzvláště nezkrotná batolata se už nemohou dočkat, až dostanou tyhle „nové“ hračky do svých malých, upatlaných ručiček. Přestože naše hrdiny čeká nejedna horká chvilka, dobře ví, že nesmí jeden druhého zradit a že se musí pohromadě. A k tomu všemu čeká panenku Barbie první osudové setkání s Kenem. (oficiální text distributora)', '2016-06-14 18:04:19.41', 2010, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000189, '201608/ANGLICKY_PACIENT.m4v', 'Anglicky pacient', 6, 0, 'cz', 'cz', 'en', 'English patient', 'http://www.csfd.cz/film/11918-anglicky-pacient/', 'ANGLICKY_PACIENT.jpg', 0, 'Láska nezná hranice a ve vzpomínkách je nesmrtelná. Ralph Fiennes v titulní roli amerického filmu, oceněného devíti Oscary... Román Michaela Ondaatjeho Anglický pacient (1992), vyznamenaný po svém vydání ve Velké Británii prestižní Bookerovou cenou, jakoby se vzpíral filmovému zpracování. Příběh velké lásky a zrady, zasazený do konce 30. let a do doby 2. světové války, je vyprávěn v několika časových rovinách a v poměrně složité flashbackové struktuře. Těžce zraněný „anglický" pacient (nazývaný tak proto, že byl vytažen z hořícího britského letadla) neví, jak se jmenuje, ani si nic nepamatuje ze své minulosti. Když jsou ostatní ranění převezeni, zůstane znetvořený muž v opuštěném a vyrabovaném klášteře spolu s kanadskou ošetřovatelkou Hanou, která se o něho stará a snaží se s ním komunikovat. Muž, který o sobě tvrdí, že se jmenuje Lászlo de Almásy, postupně odhaluje události, jež začaly před válkou v Egyptě, kde pracoval pro Královskou geografickou společnost při mapování Sahary, a pokračovaly až do června 1942, kdy Angličané opouštěli Tobruk. Jeho milostný vztah k manželce kolegy Katharine se prolíná s osudem Hany, která trpí utkvělou představou, že všem svým blízkým přináší neštěstí a má strach o sikhského poručíka Kipa, s nímž se sblížila a jemuž hrozí neustálé nebezpečí při odstraňování nevybuchlých min. (Česká televize)', '2016-08-17 11:41:31.721', 1996, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000190, '201608/CASABLANCA.m4v', 'Casablanca', 6, 0, 'cz', 'cz', 'en', 'Casablanca', 'http://www.csfd.cz/film/1912-casablanca/', 'CASABLANCA.jpg', 0, 'Američan Rick Blaine rozjíždí v době Druhé světové války v Casablance noční podnik. Chodí sem každý, kdo potřebuje třeba falešný pas, nebo má chuť si trochu zahazardovat. Jednou do Rickova podniku zavítá bojovník proti fašismu Victor Laszlo (ačkoliv s maďarským jménem, tak podle scénáře muž českého původu) společně se svou ženou Ilsou. Chtějí sehnat vízum a co nejdříve uprchnout před gestapem do USA. Rick ale v Lazslově ženě poznává svou bývalou přítelkyní. Zanedlouho se dá pár znovu dohromady a plánují společně útěk... Jeden z najkultovejších hollywoodskych filmov všetkých čias sa odohráva počas II. svetovej vojny na neokupovanom území Maroka, v Casablance. Tá sa stala prestupnou stanicou pre utečencov a členov odboja, ktorí sa pred hrozbou nacizmu snažia utiecť z Európy. Casablanca je však aj rajom pre špiónov, zradcov a podvodníkov, snažiacich sa vyťažiť čo najviac z vojnovej situácie. Centrom nočného života mesta, ale aj miestom, kde sa vybavujú dôležité obchody, predávajú víza, dokumenty a informácie, je Rickov bar. Jeho majiteľom je Američan Rick Blaine, muž, ktorý sa obrnil cynizmom, aby dokázal na tomto kolotoči ľudských osudov prežiť a zostať pritom nad vecou. To sa mu však darí iba dovtedy, kým do jeho baru i života opäť nevstúpi Ilse, s ktorou kedysi v Paríži prežil osudovú lásku. Ilse ho pred ich plánovaným útekom z okupovaného Pariža bez vysvetlenia opustila. Teraz sa s Rickom znovu stretáva a chce jeho prostredníctvom získať tranzitné víza. Ilsa totiž do Casablancy pricestovala so svojím manželom Victorom Lászlóm, vodcom odboja, ktorý musí utiecť do Ameriky. Rick tak zrazu stojí pred dilemou: riskovať krk kvôli žene, ktorá ho v minulosti zradila, alebo zachovať si vlastnú neutralitu? Situácia sa však pre zmení, keď zisťuje, aké dôvody pred rokmi Ilsu viedli k úteku z Paríža. Film získal troch Oscarov - za najlepší film, réžiu i scenár - a vďaka charizme svojich postáv i melodramatickému príbehu sa natrvalo zapísal do zlatého fondu kinematografie. (oficiální text distributora)', '2016-08-17 11:41:31.875', 1942, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000191, '201608/ZIVOT_JE_KRASNY.m4v', 'Zivot je krasny', 6, 0, 'cz', 'cz', 'it', 'Vita è bella, La', 'http://www.csfd.cz/film/642-zivot-je-krasny/', 'ZIVOT_JE_KRASNY.jpg', 0, 'Píše se rok 1939. Italský číšník Guido Orefice, překypující energií a hýřící bláznivými nápady, přijíždí z venkova do velkého města. Na první pohled se tu zamiluje do půvabné učitelky Dory. Ta už sice nápadníka má, on však udělá vše, aby ji zachránil od sňatku s nemilovaným byrokratem. Získá její lásku a vezmou se. Narodí se jim syn Giosué. Po pěti letech šťastného manželství je Guido kvůli svému židovskému původu odvlečen spolu se synem do koncentračního tábora. Aby před malým chlapcem zatajil šokující okolnosti a uchránil ho před hrůzami nacismu, předstírá, že všechno kolem je pouhá hra, připravená k synovým narozeninám. Hra, na jejímž konci na oba čeká velká odměna... (oficiální text distributora)', '2016-08-17 11:41:31.891', 1997, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000192, '201608/DEADPOOL.m4v', 'Deadpool', 4, 0, 'cz', 'cz', 'en', 'Deadpool', 'http://www.csfd.cz/film/261379-deadpool/', 'DEADPOOL.jpg', 0, 'Než se stal Deadpoolem, byl Wadem Wilsonem (Ryan Reynolds), bývalým členem speciálních jednotek. Když mu lékaři diagnostikovali pokročilé stadium rakoviny, podrobil se experimentální léčbě v rámci programu Weapon X, známého a populárního především mezi x-menovskými mutanty. Díky tomu se Wade zbavil rakoviny, a nejenže zůstal naživu, ale také jako bonus získal schopnost rychlého samouzdravování. Bohužel však – například oproti Wolverinovi, který po podobné léčbě zůstal celkem sexy – z tohoto pokusu vyšel trvale znetvořený. I proto celkem bez většího odporu přijal okostýmované alter ego jménem Deadpool. (CinemArt CZ)', '2016-08-17 11:41:31.904', 2016, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000193, '201608/iNNOCENCE+eXPERIENCE_D1.m4v', 'iNNOCENCE + eXPERIENCE', 1, 0, 'en', 'en', 'en', 'iNNOCENCE + eXPERIENCE', 'http://www.csfd.cz/film/416741-u2-innocence-experience-tour-2015-navrat-do-parize/', 'iNNOCENCE+eXPERIENCE_D1.jpg', 0, 'HBO přináší divákům unikátní záznam koncertu irské rockové kapely U2, který odehrála v rámci svého světového turné iNNOCENCE + eXPERIENCE Tour na začátku prosince v Paříži. Vystoupení obsahuje největší hity, kterými tato skupina zásobovala hudební scénu od roku 1980, kdy vyšlo její první album s názvem "Boy", až do jejich zatím poslední desky "Songs of Innocence". Show je určena pro velké stadiony a její nedílnou součástí je několik mobilních pódií, obří LED obrazovka, která je instalována napříč celou koncertní halou a zabírá prostor od země až po strop, a také nejmodernější zvukové systémy. Koncertem v Paříži navíc kapela uctila památku obětí teroristických útoků, jež v listopadu ochromily francouzskou metropoli a šokovaly celý svět. (HBO Europe)', '2016-08-17 11:41:31.916', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000235, '201609/26 All Fear the Freak.m4v', 'Vsichni se boji monstra (26)', 3, 42000200, 'cz', 'cz', 'en', 'All Fear the Freak', 'http://www.imdb.com/title/tt1928857/?ref_=fn_al_tt_1', '26 All Fear the Freak.jpg', 26, 'Directed by Victor Cook.  With Frank Welker, Mindy Cohn, Grey Griffin, Matthew Lillard. The mystery comes to a close as the gang finally gets to the bottom of what happened to the original Mystery Incorporated and why they disappeared. The truth, however, is not without its price.', '2016-09-11 09:55:55.729', 2011, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000194, '201608/BRAVEHEART.m4v', 'Statocne srdce', 15, 0, 'cz', 'cz', 'en', 'Braveheart', 'http://www.csfd.cz/film/3297-statecne-srdce/', 'BRAVEHEART.jpg', 0, 'Film je inspirovaný skutečnou historickou postavou Williama Wallace, který se v 13.století postavil do čela odporu proti anglickému králi Eduardovi I. William se tajně oženil, aby se jeho žena Murron [Marn] vyhnula králem nařízenému právu první noci. Jednoho dne se ji však pokusí znásilnit angličtí vojáci, a když ji William brání, je prohlášen za psance a Murron popravena. Wallace se postaví do čela odporu proti anglické vrchnosti a získává spojence v princezně Isabelle, díky které se vyhne většině králových nástrah. Nakonec je však Wallace kvůli zradě vlastního vojska zajat... (oficiální text distributora)', '2016-08-17 11:41:31.928', 1995, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000195, '201608/Mezi nahrobnimi kameny.m4v', 'Mezi nahrobnimi kameny', 15, 0, 'cz', 'cz', 'en', 'A Walk Among the Tombstones', 'http://www.csfd.cz/film/317783-mezi-nahrobnimi-kameny/', 'Mezi nahrobnimi kameny.jpg', 0, 'Matt Scudder (Liam Neeson) je bývalý newyorský policista a vyléčený alkoholik, který pracuje jako soukromý detektiv bez licence pro ty, kdo z jakýchkoliv důvodů nemůžou jít za policií. Jedním z takových je i drogový dealer Kenny, který si detektiva najme, aby našel ty, kdo mu unesli a brutálně zavraždili ženu. Scudder začne postupně odkrývat stopy vedoucí k únoscům a zjišťuje, že tento zvrácený zločin není jedniným a rozhodně ani posledním, který v ulicích New Yorku spáchali. Sérioví vrazi se chystají opět zaútočit a pro Scuddera, stojícího mimo zákon, se tak rozehrává nelítostný boj s časem. (A-Company CZ)', '2016-08-17 11:41:31.94', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000196, '201609/OKNO_DO_DVORA.m4v', 'Okno do dvora', 15, 0, 'cz', 'cz', 'en', 'Rear Window', 'http://www.csfd.cz/film/4386-okno-do-dvora/', 'OKNO_DO_DVORA.jpg', 0, 'Fotograf J. B. Jeff Jeffries (James Stewart) je kvůli zlomené noze odkázán na kolečkové křeslo. Jeho společníkem se v dlouhých chvílích stane okno vedoucí přímo do dvora, odkud má Jeff výhled do bytů svých sousedů. Sledování osudů nájemníků z protějšího domu ho pohltí natolik, že začne podezřívat jednoho z nich z vraždy své manželky. Jeffries se spolu se svou elegantní přítelkyní (Grace Kelly) pustí do odhalování zapeklitě složitého řetězce událostí... Událostí vedoucích k senzačnímu a strhujícímu konci. (oficiální text distributora)', '2016-09-06 19:21:31.065', 1954, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000197, '201609/Place Beyond the Pines.m4v', 'Za borovicovym hajem', 15, 0, 'cz', 'cz', 'en', 'The Place Beyond the Pines', 'http://www.csfd.cz/film/297577-za-borovicovym-hajem/', 'Place Beyond the Pines.jpg', 0, 'Luke se na poutích živí jako motocyklový kaskadér. Když zjistí, že mu jeho milenka tajně porodila syna, rozhodne se svou rodinu najít a být pro dítě dobrým otcem. Aby zajistil pro rodinu více peněz, opustí kaskadérský život a začne na motorce vykrádat banky. Co ale vypadá jako dokonalé zločiny, brzy přiláká zájem policie a především ambiciózního detektiva Crosse a  zkorumpovaného šéfa místního oddělení Deluca. Začíná nemilosrdný  boj, ve kterém neexistují žádná pravidla. Citově vypjaté a skvěle vygradované kriminální drama Dereka Cainfrance bylo s úspěchem uvedeno na MFF v Torontu. (Febiofest)', '2016-09-06 19:21:31.188', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000198, '201609/Scooby Doo Prisera na Mesiaci.m4v', 'Mesicni nestvura vyleza', 3, 30010, 'cz', '  ', 'en', 'Scooby Doo! Moon Monster Madness', 'http://www.csfd.cz/film/409769-scooby-doo-mesicni-nestvura-vyleza/', 'Scooby Doo Prisera na Mesiaci.jpg', 900, 'Dobrodružná výprava Scooby-Dooa a jeho kumpánů do vzdálených světů znamená velký krok pro celé psí pokolení! Scooby-Doo, Shaggy, Fred, Daphne a Velma vyhrají poslední volné letenky do vesmíru. Na cestu se vydají v ultra moderní kosmické lodi miliardáře Slye Barona zvané Sly Star 1. Jejich nespoutanou zábavu však naruší setkání se záhadným mimozemšťanem, který začne raketu ničit! Posádka musí rozbitou loď opustit a přesunout se na Baronovu základnu... na odvrácené straně Měsíce! Podaří se pětici kamarádů rozluštit mimozemské tajemství? Najde Scooby-Doo na Měsíci pochoutky? Sundá si Fred někdy helmu? Pusťte se spolu s nimi za odpovědí až na samou hranici vesmíru! (Magic Box)', '2016-09-11 07:55:24.971', 2015, '');
