INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000199, '201609/Maly princ.m4v', 'Maly princ', 3, 0, 'cz', '  ', 'en', 'The Little Prince', 'http://www.csfd.cz/film/346886-maly-princ/', 'Maly princ.jpg', 0, 'Od Marka Osborna, režiséra Kung Fu Pandy, přichází první celovečerní animovaná adaptace slavného díla o přátelství, lásce a skutečném štěstí. Ústřední postavou je malá holčička, kterou se její maminka snaží připravit na skutečný svět dospělých. Její plán naruší trochu excentrický, leč dobrosrdečný soused, letec, který holčičce představí neobyčejný svět, jenž mu před dávnou dobou představil Malý princ. Ve světě, kde vše je možné, začne pro malou holčičku kouzelná cesta uvnitř vlastní představivosti, na které znovuobjeví své dětství a zjistí, že skutečně můžeme vidět jen srdcem. (A-Company CZ)', '2016-09-11 07:55:25.103', 2015, '');
