INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000233, '201609/25 Pawn of Shadows.m4v', 'Pesak stinu (25)', 3, 42000200, 'cz', 'cz', 'en', 'Pawn of Shadow', 'http://www.imdb.com/title/tt1931121/?ref_=fn_al_tt_1', '25 Pawn of Shadows.jpg', 25, 'Directed by Curt Geda, Victor Cook.  With Frank Welker, Mindy Cohn, Grey Griffin, Matthew Lillard. The gang investigates the curse of Crystal Cove with the help of Professor H.P. Hatecraft. But when a high-tech creature called the Obliteratrix attacks the gang, it''s up to them to find out why and how is this related to the curse.', '2016-09-11 09:55:55.71', 2011, '');
