INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000267, '201609/FIREFLY_11_Trash.m4v', 'Firefly Part 11: Trash', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 11: Trash', '', 'FIREFLY_11_Trash.jpg', 11, 'Saffron returns to plague Serenity with a scheme to steal a rare antique weapon from a wealthy landowner. Unfortunately for Mal, she neglects to mention just how she came across the information needed to break into the landowner''s home.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000268, '201609/FIREFLY_01_Serenity.m4v', 'Firefly Part 1: Serenity', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 1: Serenity', '', 'FIREFLY_01_Serenity.jpg', 1, 'Malcolm Reynolds is a veteran and the captain of Serenity. He and his crew are smuggling goods, but they need to pick up some passengers for extra money. However, not all the passengers are what they seem.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000269, '201609/FIREFLY_06_Our_Mrs_Reynolds.m4v', 'Firefly Part 6: Our Mrs Reynolds', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 6: Our Mrs Reynolds', '', 'FIREFLY_06_Our_Mrs_Reynolds.jpg', 6, 'As an unexpected reward for an unpaid job, Mal finds himself married to a naïve, subservient young woman named Saffron. The crew are amused at his discomfort and Book lectures him on propriety, but things aren''t as smoothly straightforward as they thought them to be.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000270, '201609/FIREFLY_04_Shindig.m4v', 'Firefly Part 4: Shindig', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 4: Shindig', '', 'FIREFLY_04_Shindig.jpg', 4, 'Inara attends a formal society dance, only to find Malcolm there as well, attempting to set up a smuggling job. Mal comes to blows with Inara''s conceited date and finds himself facing a duel with a renowned swordsman, and only one night to learn how to fence.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000271, '201609/FIREFLY_03_Bushwhacked.m4v', 'Firefly Part 3: Bushwhacked', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 3: Bushwhacked', '', 'FIREFLY_03_Bushwhacked.jpg', 3, 'Serenity is pulled in by a Tohoku-class cruiser while investigating a spaceship that was attacked by Reavers. Simon and River must hide to prevent capture, while something is wrong with the lone survivor of the attacked spaceship.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000272, '201609/FIREFLY_02_The_Train_Job.m4v', 'Firefly Part 2: The Train Job', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 2: The Train Job', '', 'FIREFLY_02_The_Train_Job.jpg', 2, 'The crew of Serenity take on a train heist commissioned by a crime lord. They steal the goods, only to find it is medicine that''s desperately needed by the town.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000273, '201609/FIREFLY_05_Safe.m4v', 'Firefly Part 5: Safe', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 5: Safe', '', 'FIREFLY_05_Safe.jpg', 5, 'Mal must choose which crew members to save when one is gravely wounded and two others are kidnapped. Simon finds an uneasy haven in a remote village, but River''s uncanny perceptions jeopardize the Tams'' temporary safety.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000274, '201609/SERENITY.m4v', 'Serenity', 4, 42000264, 'en', 'en', 'en', 'Serenity', 'http://www.csfd.cz/film/183331-serenity/', 'SERENITY.jpg', 101, 'Joss Whedon, na Oscara * a cenu Emmy nominovaný, scenárista a režisér stojící za fiilmem Buffy, přemožitelka upírů a Angel, tentokrát využil svůj nezaměnitelný důvtip v tomto napínavém velkofiilmu. Kapitán Malcolm Reynolds, ostřílený veterán (na straně poražených) z galaktické občanské války, nyní živoří na palubě své lodi Serenity. Vede malou vybranou posádku, ale když přibere na palubu dva nové pasažéry - mladého doktora a jeho sestru s telepatickými schopnostmi - získává mnohem víc, než na co je připraven. Oba jsou uprchlíci ze seskupení, které vládne vesmíru, a tak se Serenity rázem ocitá lapená v pasti mezi nezastavitelnou vojenskou silou Aliance a brutální zuřivostí zdivočelých Reavers. A další nebezpečí se skrývá i v samém srdci lodi… (oficiální text distributora)', '2016-10-03 17:13:09.789', 2005, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000275, '201609/FIREFLY_09_Ariel.m4v', 'Firefly Part 9: Ariel', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 9: Ariel', '', 'FIREFLY_09_Ariel.jpg', 9, 'Hard up for cash, Serenity takes on a job from Simon: help him get a thorough diagnostic of River in return for the opportunity to loot the vast medical stores of an Alliance hospital on central world Ariel. But River''s pursuers are hot on their trail, and they receive some unexpected inside help.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000276, '201609/FIREFLY_12_The_Message.m4v', 'Firefly Part 12: The Message', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 12: The Message', '', 'FIREFLY_12_The_Message.jpg', 12, 'A former Independence soldier who had served with Mal and Zoë returns in a dramatic manner, with a vicious Alliance officer chasing after him for some unusual smuggled goods.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000277, '201609/FIREFLY_08_Out_of_Gas.m4v', 'Firefly Part 8: Out of Gas', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 8: Out of Gas', '', 'FIREFLY_08_Out_of_Gas.jpg', 8, 'After Serenity suffers a catastrophe that leaves her crew with only hours of oxygen, flashbacks show how Mal and Zoë acquired Serenity and assembled their motley band.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000278, '201609/FIREFLY_14_Objects_in_Space.m4v', 'Firefly 14: Objects in Space', 4, 42000264, 'en', 'en', 'en', 'Firefly 14: Objects in Space', '', 'FIREFLY_14_Objects_in_Space.jpg', 14, 'Serenity encounters a ruthlessly professional bounty hunter, Jubal Early, who will stop at nothing to retrieve River. But River, feeling unwelcome on the ship, takes a novel approach to escaping from the long arm of the Alliance.
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000279, '201609/FIREFLY_07_Jaynestow.m4v', 'Firefly Part 7: Jaynestow', 4, 42000264, 'en', 'en', 'en', 'Firefly Part 7: Jaynestow', '', 'FIREFLY_07_Jaynestow.jpg', 7, 'Returning to a planet where he ran into some serious trouble years ago, Jayne discovers that he''s become a local folk legend. Mal decides to use this entertaining distraction to complete a job, but some unfinished business may derail his plans.', '2016-10-03 17:13:09.877', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000281, '201610/DRSEUSS_THE_LORAX.m4v', 'Dr Seuss Lorax', 3, 0, 'sk', 'sk', 'en', 'Dr Seuss the Lorax', 'http://www.csfd.cz/film/293571-lorax/', 'DRSEUSS_THE_LORAX.jpg', 0, 'Co je to za svět, ve kterém už není ani kousek živé přírody, ptáte se? Je to městečko, v němž žije dvanáctiletý Ted. Všechno je tu náramně umělé, včetně zvířat, kytek a stromů, u kterých si můžete dálkovým ovladačem nastavit jarní, letní, podzimní, zimní a disco variantu. Komu by se chtělo ven, za vysokou a neprodyšně uzavřenou hradbu města, na jejíž neprůchodnost dohlížejí gorily místního vládce, pana O´Hareho? Zdejší lidé si na to zvykli a většina z nich už dávno zapomněla, jak vypadá opravdová příroda. Nepřijde jim divné, že čerstvý vzduch se prodává v pet lahvích, což je voda na O´Hareho mlýn. Jenže Tedova láska Audrey, coby nevyléčitelná romantická duše, touží po stromu „z masa a kostí“. Akční babička Norma chlapci poradí, aby si zašel pro radu k legendami opředenému Jednorázovi. Ten ovšem údajně žije za zdmi města. A tak se Ted poprvé v životě podívá ven, do skutečného světa. Ten pohled za moc nestojí. Vidí jen smutnou měsíční krajinu plnou pařezů. A na obzoru zvláštní dům. Ten, jak se Ted záhy dozví, patří právě Jednorázovi, podivínskému samotáři. Byl to právě on, kdo proměnil kvetoucí krajinu na pustinu a vyhnal z ní jejího mýtického ochránce Loraxe. A na Tedovi bude, aby ji zachránil tim, že vysadí poslední existující sazenici živého stromu a připomene lidem, že vždycky nebylo všechno umělé. Jenže takové chování přímo ohrožuje živobytí pana O´Hareho. A to nemůže zůstat bez odezvy. (oficiální text distributora)', '2016-10-12 19:08:53.469', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000282, '201610/MONSTERSVS_ALIENS.m4v', 'Monstra vs Votrelci', 3, 0, 'sk', 'sk', 'en', 'Monsters vs Aliens', 'http://www.csfd.cz/film/234241-monstra-vs-vetrelci/', 'MONSTERSVS_ALIENS.jpg', 0, 'Na naši modrou kulatou planetu už zase zaútočili mimozemšťané. Ještě že máme obrovskou zásobárnu hrdinů, kteří se jim můžou postavit. I když ti, které si vybrali animátoři ze studia DreamWorks, jsou opravdu výjimeční. Zcela mimořádná je i celá jejich novinka Monstra vs. Vetřelci. Nejenže v ní opustili důvěrně známý a bezpečný svět zvířat a zeleně roztomilých pohádkových postav, ale navíc jako první při natáčení použili revoluční technologii, kterou na vlastní kůži zažijí i diváci. I když většina příběhů svatbou spíš končí, Monstra vs. Vetřelci jí začínají. Susan, obyčejná holka z Kalifornie, má už za pár hodin říct „Ano“ svému vyvolenému. Když ji do hlavy praští meteorit plný kosmického slizu, dost jí to vykolejí. Následně naroste do výšky 15 metrů a to ji rozhodí ještě o něco víc. Než se stačí svého vyvoleného zeptat, zda mu bude její výška vadit, zaútočí na ni speciální vojenská jednotka, zajme ji a uvězní v tajném vládním komplexu. Tady Susan, oficiálně přejmenovaná na Enormiku, zjišťuje, že se svou výjimečností rozhodně není sama. Kromě ní tu jsou věznění geniální vědec Dr. Šváb, PhDr., kterému po jednom neúspěšném pokusu nahradila vlastní hlavu hlava muší a Chybějící článek, což je reprezentant evolučního přechodu mezi rybou a člověkem. Dalšími obyvateli jsou rosolovitý a nezničitelný B.O.B. a sto metrů dlouhá larva přezdívaná Hmyzosaurka. Jejich doživotní internace skončí ve chvíli, kdy na Zemi přistane záhadný mimozemský robot a vezme ji útokem. Protože konvenční ani „standardní nekonvenční“ zbraně na něho nepůsobí, obrátí se zoufalý generál MacBomba na ještě zoufalejšího prezidenta Spojených států a požádá ho, aby oficiálně povolal partičku příšer do boje proti vetřelci z vesmíru a zachránil svět před hrozící zkázou. A monstra ve snaze dokázat, jak moc jsou pro lidstvo potřební, chytí tuhle příležitost pevně za pačesy. (oficiální text distributora)', '2016-10-12 19:08:53.602', 2009, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000283, '201611/JACK_REACHER.m4v', 'Jack Reacher: Posledni vystrel', 15, 0, 'en', 'en', 'cz', 'Jack Reacher', 'http://www.csfd.cz/film/299673-jack-reacher-posledni-vystrel/', 'JACK_REACHER.jpg', 0, 'Jack Reacher (Tom Cruise) je jako duch. Bývalého vojenského vyšetřovatele najdete jen tehdy, kdy chce on sám být nalezen. Objevuje se u případů, které potvrzují, že spravedlnost je slepá, a pro jejich vyřešení neváhá ignorovat zákony své země. Drsný hrdina z bestsellerů spisovatele Lee Childa konečně vstoupil na plátna kin, s odhodláním stvořit stejně silnou a kvalitní akční ságu, jakou se před lety stal jiný původem literární hrdina, Jason Bourne. Život pěti lidí, které osud přivedl ve stejný čas na stejné místo, ukončí šest kulek vypálených odstřelovačem. Takový masakr si od policie vyžádá nadstandardní nasazení, které brzy korunuje dopadení pachatele. Bývalý elitní voják nemá pod tíhou důkazů sebemenší šanci, přesto tváří v tvář vyšetřovatelům zarytě mlčí. Ti z něj dostanou jedinou větu: „Sežeňte Jacka Reachera.“ S jeho příchodem se z naprosto jasného případu stává výbušná kauza opředená mnoha záhadami, jejíž rozuzlení může stát život řadu lidí, Jacka Reachera nevyjímaje. Jenže ten si na drsňáka jenom nehraje a kvůli pravdě je ochoten hodně vydržet. (oficiální text distributora)', '2016-11-01 13:29:55.322', 2012, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000284, '201611/RUKOJEMNIK.m4v', 'Rukojemnik: Jak jsme hrali caru', 15, 0, 'sk', 'cz', 'sk', 'Rukojemnik', 'http://www.csfd.cz/film/354119-jak-jsme-hrali-caru/', 'RUKOJEMNIK.jpg', 0, 'Malý Petr (Richard Labuda, vnuk Mariána Labudy) vyrůstá v péči prarodičů (Milan Lasica, Libuše Šafránková), protože jeho rodiče emigrovali do Vídně a od té doby se ho prostřednictvím úřadů marně snaží získat. I proto Petr vymýšlí nejrůznější způsoby, jak se přes čáru dostat, pro což má čím dál menší pochopení velitel místních pohraničníků (Ondřej Vetchý). Mnohem větší problémy má ale Petr s partou černogardistů, kteří si libují v trápení slabších dětí. Třeba i jejich občasným přivazováním k mučednickým kůlům. Mezi ty, kteří jsou na Petrově straně, patří „pomalejší" spolužák a velmi sympatická kamarádka, kvůli nimž se s černogardisty neváhá opakovaně a důkladně poprat. Jak se šedesátá léta chýlí ke konci a poměry v zemi se dramaticky mění, otevře se nakonec i před Petrem vysněná cesta přes hranice. Vyrazit na ni ale znamená ztratit prarodiče, kamarády a nezapomenutelná dobrodružství. Když je vám „náct", máte v životě přece jen jiné priority. (CinemArt CZ)', '2016-11-01 13:29:55.443', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000285, '201611/08_ZNAMENI_TRI.m4v', 'Sherlock Holmes 8 - Znameni tri', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 8 - The Sign of Three', '', '08_ZNAMENI_TRI.jpg', 8, 'Život přináší události radostné i neradostné a zřejmě jen málokdo by zpochybnil, že svatba jednoznačně spadá do první z jmenovaných kategorií. John a Mary se rozhodli vstoupit do manželského svazku a paní Hudsonová ohlašuje konec celé jedné éry. Pro Sherlocka to ovšem znamená, že bude nucen čelit dosud největší výzvě svého života – musí zvládnout proslov ženichova svědka. A přednést ho před skutečnými lidmi, kteří mu budou opravdu naslouchat. Co se může stát nejhoršího? Třeba to, že se mezi svatebčany vyskytne někdo s vražednými úmysly.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000286, '201611/Black Mass-1.m4v', 'Black Mass: Spinava dohoda', 15, 0, 'cz', 'cz', 'en', 'Black Mass', 'http://www.csfd.cz/film/283586-black-mass-spinava-hra/', 'Black Mass-1.jpg', 0, 'V jižním Bostonu sedmdesátých let minulého století přemluví agent FBI John Connolly (Edgerton) irského mafiána Jamese „Whitey" Bulgera (Depp) ke spolupráci s FBI a vyřazení společného nepřítele, italské mafie, ze hry. Filmové drama líčí příběh nesourodého spojenectví, které se vymklo kontrole a umožnilo Whiteymu vyhnout se právnímu postihu, upevnit si mocenské postavení a stát se jedním z nejbezohlednějších a nejmocnějších gangsterů v historii Bostonu. (Freeman Ent.)', '2016-11-03 19:54:04.399', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000288, '201611/03_VELKA_HRA.m4v', 'Sherlock Holmes 3 - Velka hra', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 3 - The Great Game', '', '03_VELKA_HRA.jpg', 3, 'Sherlock Holmes si zoufá. Již dlouho nenarazil na případ hodný jeho intelektu a jestli něco opravdu z duše nenávidí, pak je to nečinnost kriminálních živlů, která jej uvrhává do hlubin stále nesnesitelnější nudy. Netuší, že již brzy stane před dosud největší výzvou svého života. Baker Street otřese výbuch a šílený útočník hrozí dalšími pumovými atentáty, pokud Sherlock nedokáže ve stanoveném časovém limitu vyřešit hádanky, které mu předkládá. Zvrácená hra začíná. A tentokrát je to velmi osobní.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000289, '201611/Kosti A Skalp-1.m4v', 'Krvavy tomahavk', 15, 0, 'cz', 'cz', 'en', 'Bone Tomahawk', 'http://www.csfd.cz/film/334618-kosti-a-skalp/', 'Kosti A Skalp-1.jpg', 0, 'Divoký západ. Městečko Bright Hope, stojící uprostřed ničeho, zatím vytrhávala z jeho ospalosti pouze loupežná přepadení. Tuto „idylu" ovšem velice rázně utnou příslušníci indiánského kmene, kteří unesou půvabnou Samanthu. Nejedná se totiž o kmen tradiční, nýbrž... kanibalský. A také notně vyhládlý. Místní muži dávají dohromady ne právě klasickou záchrannou výpravu v čele s šerifem Franklinem Huntem ( Kurt Russell), která musí za každou cenu Samanthu vysvobodit dřív, než si na ní divoši začnou pochutnávat. Krutost jejich protivníků je však mnohem větší, než si kdo dokázal představit, a jejich mise je v čím dál větším ohrožení. (Magic Box)', '2016-11-03 19:54:04.44', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000290, '201611/05_PSISKO_BASKERVILSKE.m4v', 'Sherlock Holmes 5 - Psisko Baskervilske', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 5 - The Hounds of Baskerville', '', '05_PSISKO_BASKERVILSKE.jpg', 5, 'Sherlock právě vyřešil případ, ale to bylo ráno. Nutně potřebuje další, jinak jeho mysl vybuchne jako raketa na startovací rampě. Situace je natolik kritická, že se začne pídit po svých tajných zásobách cigaret, nakonec ale přeci jen zazvoní spásný zvonek. Malý Henry Knight byl před dvaceti lety svědkem násilné smrti svého otce, který byl nedaleko jejich domu v Dartmooru roztrhán na kusy děsivým monstrem. Nyní je Henry dospělý a je přesvědčen, že na blízké vojenské základně Baskerville šlechtí zmutovaná zvířata. Sherlocka jeho teorie zjevně neohromují a o případ nejeví zájem. Až do chvíle, kdy Henry pronese slovo psisko. Nevyzpytatelný detektiv mění názor a rozhodne se s Johnem vyrazit na místo, aby záhadě přišli na kloub.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000291, '201611/X Men Apocalypse-35.m4v', 'X-Men Apokalypsa', 4, 28, 'cz', 'cz', 'en', 'X-Men Apocalypse', 'http://www.csfd.cz/film/358916-x-men-apokalypsa/', 'X Men Apocalypse-35.jpg', 8, 'Od počátku civilizace byl uctíván jako bůh. Apocalypse, první a nejsilnější z mutantů marvelovského světa X-Menů načerpal sílu ostatních mutantů a stal se tak nesmrtelným a neporazitelným. Probudil se po tisíci letech a je rozčarovaný ze světa, který nachází. Rekrutuje tým mutantů včetně zklamaného Magneta (Michael Fassbender) s cílem očistit lidstvo a nastolit nový světový řád, kterému chce vládnout. Osud Země tak visí na vlásku. Raven (Jennifer Lawrencová) s pomocí profesora X (James McAvoy) stane v čele týmu mladých X-Menů, aby společně zastavili svého největšího nepřítele a zachránili lidstvo před úplným zánikem. (CinemArt CZ)', '2016-11-03 19:54:04.465', 2016, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000292, '201611/Letiste-2.m4v', 'Letiste', 15, 0, 'cz', 'cz', 'en', 'Airport', 'http://www.csfd.cz/film/24953-letiste/', 'Letiste-2.jpg', 0, 'Nad Lincolnovým letištěm zuří sněhová bouře, která omezuje provoz. V okamžiku, kdy se ke startu chystá linka do Říma, uvízne na ranveji mexické letadlo. Problémy ale nekončí ani po startu: Mezi cestujícími je šílenec s bombou, který chce ukončit svůj život. Začíná boj s časem a napjatými nervy... (oficiální text distributora)', '2016-11-03 19:54:04.487', 1970, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000293, '201611/Warcraft-10.m4v', 'Warcraft: Prvy stret', 4, 0, 'cz', 'cz', 'en', 'Warcraft', 'http://www.csfd.cz/film/233328-warcraft-prvni-stret/', 'Warcraft-10.jpg', 0, 'Království Azeroth a říše Draneor se od sebe nemohou lišit víc. Azeroth je vzkvétající říše lidí, v níž moudře panuje král Llane (Dominic Cooper). Jeho vojsko vedené rytířem Lotharem (Travis Fimmel) a společenství mocných kouzelníků v čele s mágem Medivhem (Ben Foster) důsledně chrání mírumilovnou civilizaci. V Draenoru představuje život permanentní boj mezi znepřátelenými rasami Orků, jež spojí až neporazitelný nepřítel, nákaza, která v jejich zemi postupně ničí vše živé. Jejich vůdce Gul´dan vytvoří s pomocí temné magie portál, kterým můžou Orkové proniknout do jiného světa a ten si osídlit. Shodou okolností se jejich cílem stane právě Azeroth, jehož obyvatelé jsou ochotní bránit svou zemi do posledního muže, ostatně nic jiného jim ani nezbývá. Zdá se, že ničivý střet civilizací nelze zastavit, ti, kdo hledají náhradní řešení, přesto existují. (CinemArt CZ)', '2016-11-03 19:54:04.502', 2016, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000294, '201611/Zvonar Matky Bozej-1.m4v', 'Zvonar Matky Bozej', 3, 0, 'cz', 'cz', 'en', 'Hunchback', 'http://www.csfd.cz/film/14016-zvonik-u-matky-bozi/', 'Zvonar Matky Bozej-1.jpg', 0, 'Kreslený velkofilm světoznámého studia Walta Disneye, inspirován prastarou pařížskou legendou a slavným románem Victora Huga. Dojemný příběh zvoníka Quasimoda (se znetvořeným zevnějškem, ale srdcem z ryzího zlata) začal jedné zimní noci, kdy se do Paříže pokusili tajně proniknout mladí manželé s nemluvnětem. Obávaný soudce Frolo a jeho biřici však byli ve střehu a žena s dítětem v náručí marně hledá útočiště v slavné pařížské katedrále Notre Dame. Frolo pak pozdě pochopil, že v uzlíčku na její hrudi nebyl lup, ale znetvořené nemluvně. Dříve než soudce stačil čerstvého sirotka utopit, zasáhla prozřetelnost v podobě chrámového kněze. Soudce se z obavy před božím trestem zavázal, že dítě vychová, ale vymínil si, že chlapec nikdy neopustí katedrálu. Uplynulo dvacet let. Quasimodo se stal u Matky boží zvoníkem. Na náměstí se koná každoroční festival bláznů a tři oživlé sochy (jediní Quasimodovi přátelé) ho přemluví, aby se konečně vzepřel vůli svého poručníka a šel poprvé v životě mezi lidi. Pařížané nejprve obdivují Quasimodovu znetvořenou tvář jako dokonalou masku, ale když odhalí pravdu, stane se překvapený zvoník snadným terčem jejich krutého posměchu. zastane se ho pouze krásná cikánka Esmeralda a je za svou troufalost stíhána Frolovými nohsledy. Nalezne útočiště v katedrále a s Quasimodovou pomocí se jí pod rouškou noci podaří uprchnout. Zlolajný Frolo nyní touží po pomstě stejně intenzivně, jako po Esmeraldině kráse a přikáže ji vypátrat za každou cenu. Statečný, čestný a do Esmeraldy zamilovaný kapitán pařížské gardy Phoebus se vzepře jeho rozkazům a stává se psancem. Pokud má láska a přátelství zvítězit nad bezcitným zlem, musí Quasimodo společně se svými přáteli Phoebusem a Esmeraldou zbavit Paříž ďábelského Frola... (oficiální text distributora)', '2016-11-03 19:54:04.509', 1996, '');
