INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000295, '201611/The Pirate Fairy-6.m4v', 'Piratska vila', 3, 39, 'cz', 'cz', 'en', 'The Pirate Fairy', 'http://www.csfd.cz/film/338608-zvonilka-a-pirati/', 'The Pirate Fairy-6.jpg', 5, 'Ze světa Petra Pana přichází do našich kin snímek Zvonilka a piráti, velkolepé dobrodružství o Zarině, chytré a ambiciozní víle, která je ohromena neuvěřitelnými možnostmi modrého kouzelného prášku. Když se kvůli svým odvážným nápadům dostane do potíží, uteče ze země víl a spojí své síly s piráty, kteří z ní udělají kapitánku své lodi. Zvonilka se společně se svými přáteli musí vydat na dobrodružnou pouť, aby Zarinu našli a společně pak zkřížili meče s bandou pirátů pod vedením plavčíka Jamese, ze kterého se má záhy stát legendární Kapitán Hook. (oficiální text distributora)', '2016-11-03 19:54:04.524', 2014, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000296, '201611/04_SKANDAL_V_BELGRAVII.m4v', 'Sherlock Holmes 4 - Skandal v Belgravii', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 4 - A Scandal In Belgravia', '', '04_SKANDAL_V_BELGRAVII.jpg', 4, 'S hrdiny seriálu se znovu shledáváme tam, kde jsme se s nimi naposledy rozloučili. Nad mihotavou hladinou zšeřelého plaveckého bazénu. Tři muži, jedna bomba a na ni namířená zbraň. Sherlock Holmes, John Watson a Jim Moriarty. Jakmile se podaří tuto ošemetnou situaci překonat, vše se vrací do zavedených kolejí. John se věnuje pilnému dopisování svého blogu a Sherlock se nudí. Kritické připomínky k literárním výkonům přítele nemohou jeho geniální mozek uspokojit, potřeboval by nějaký šťavnatý případ. Potom se objeví záhada mrtvého turisty s rozbitou hlavou. Je to však pouhé preludium k mnohem rafinovanější partii, jejíž zahajovací tahy se odehrají v Buckinghamském paláci a koncovka málem srazí Británii i Sherlocka na kolena.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000297, '201611/01_STUDIE_V_RUZOVE.m4v', 'Sherlock Holmes 1 - Studie v ruzove', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 1 - A Study In Pink', '', '01_STUDIE_V_RUZOVE.jpg', 1, 'Doktor John Watson se vrátil z bojů v Afghánistánu, kde sloužil jako vojenský lékař. Z válečné mise si přivezl zranění, s jehož následky se potýká dosud. Na radu terapeutky, aby si zapisoval vše, co se mu přihodí, odvětí, že jemu se nikdy nic nepřihodí. To se má ovšem brzy změnit. Díky náhodnému setkání s přítelem ze studií se seznamuje s charismatickým géniem, který stejně jako on hledá spolubydlícího. Doktor John Watson a Sherlock Holmes se poprvé potkávají. A sotva si stačí prohlédnout byt v Baker Street 221B, jsou vtaženi do prvního společného dobrodružství. V opuštěném domě leží mrtvá žena v růžovém. Je to již čtvrtá oběť série záhadných sebevražd, které děsí Londýn. Detektiv inspektor Lestrade patří k nejlepším mužům Scotland Yardu, tentokrát ale ví, že existuje pouze jeden člověk, který může pomoci. Tak začíná vyšetřování, na jehož konci vznikne jedno krásné přátelství a zazní tajemné jméno, dávající tušit věcí příštích. Film volně vychází z Doylova klasického příběhu Studie v šarlatové.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000298, '201611/07_PRAZDNY_POHREBAK.m4v', 'Sherlock Holmes 7 - Prazdny pohrebak', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 7 - The Empty Hearse', '', '07_PRAZDNY_POHREBAK.jpg', 7, 'Od dramatických událostí ze závěru poslední epizody uplynuly dva roky. Víme jen tolik, že Sherlock žije. Vrhl se ze střechy nemocnice sv. Bartoloměje, a přesto žije. Pro svět ovšem zůstává mrtvý. Vyrovnat se s jeho smrtí bylo těžké především pro Johna Watsona, nyní se ale zdá, že konečně dokázal obrátit list a posunout se v životě dál. Jeho znovunalezený klid nemá mít ovšem dlouhého trvání. Londýnu hrozí masivní teroristický útok, jehož odvrácení si žádá Sherlockova génia. Detektiv vstává z mrtvých, k jeho překvapení se věci ale nevyvíjejí tak, jak si představoval.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000299, '201611/Atlantis.m4v', 'Atlantida: Stratena risa', 3, 0, 'cz', 'cz', 'en', 'Atlantis: The Lost Empire', 'http://www.csfd.cz/film/12649-atlantida-tajemna-rise/', 'Atlantis.jpg', 0, 'Před mnoha tisíci lety představovala Atlantida vyspělou civilizaci na vrcholu rozkvětu. Když se však král snažil využít síly říše k ovládnutí světa, došlo k obrovské katastrofě a Altantida nenávratně zmizela V roce 1914 sní kartograf a lingvista Milo Thatch v kotelně muzea svůj sen o objevení Atlantidy. Pokusy o její nalezení učinil kdysi jeho dědeček. Ten odhalil tajemství deníku, který popisuje cestu do Atlantidy. Když se Milo opět pokouší přesvědčit nabubřelou komisi o vypravení expedice, je s posměchem vyhozen z muzea. V tom se objevuje výstřední miliardář Preston Whitmore, dávný přítel Milova dědečka, který se rozhodne završit sen Tadeáše Thatche. Vyhledá jeho vnuka, odevzdá mu onu záhadnou knihu a dokonce financuje výpravu za nalezením Atlantidy. Cesta postupuje velmi nadějně, protože Milo jako jediný umí rozluštit dávnou řeč Atlantiďanů. Ovšem postupně se začíná ukazovat, že podivná posádka pod velením kapitána Rourka, není vedena jen vědeckými zájmy. Po střetnutí s mořskou obludou a řadě dalších podmořských dobrodružství se skupina skutečně dostává do Atlantidy. To, co zde naleznou, je však více než překvapivé. Atlantida žije! (oficiální text distributora)', '2016-11-03 19:54:04.552', 2001, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000300, '201611/02_SLEPY_BANKER.m4v', 'Sherlock Holmes 2 - Slepy banker', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 1 - The Blind Banker', '', '02_SLEPY_BANKER.jpg', 2, 'Na Sherlocka se obrátí známý ze studií s žádostí o pomoc. V jedné z velkých londýnských bank došlo k vloupání, aniž by bezpečnostní systém cokoliv zaznamenal. Pachatel nic neodnesl, ale zanechal po sobě záhadný nápis na zdi. Sherlock na základě indicií na místě činu vydedukuje, že nápis byl ve skutečnosti šifrovaným vzkazem, určeným jednomu ze špičkových zaměstnanců banky. Toho však objeví zastřeleného v jeho zamčeném bytě. Policie by případ ráda uzavřela jako sebevraždu, Sherlock Holmes je ale opačného mínění. A jako vždy má pravdu. Během vyšetřování, které vystaví přátelství obou hrdinů nejedné zkoušce, se doktor Watson zamiluje a je dokonce obviněn z vandalismu.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000301, '201611/06_REICHENBASSKY_PAD.m4v', 'Sherlock Holmes 6 - Reichenbassky pad', 15, 42000302, 'cz', 'cz', 'en', 'Sherlock Holmes 6 - The Reichenbach Fall', '', '06_REICHENBASSKY_PAD.jpg', 6, 'Sherlockův úhlavní nepřítel Jim Moriarty kdysi slíbil, že geniálního detektiva zničí. A nyní je zpět. Tento génius zločinu se během jediného dne dokázal vloupat do tří nejstřeženějších míst v Británii, při pokusu o krádež korunovačních klenotů v londýnském Toweru byl však dopaden policií a čeká jej soud. Soud, k němuž je Sherlock Holmes předvolán jako svědek obžaloby. Sherlock tuší, že to vše musí být nějakým způsobem součástí Moriartyho ďábelského plánu, nemá však příliš na vybranou než vstoupit do mediálního šílenství, jež tento proces století doprovází. Takový je začátek smrtelně nebezpečné hry, která nakonec Sherlocka přivede až na okraj střechy nemocnice sv. Bartoloměje.(Česká televize)
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999952, '201611/Scooby Doo Wwe Curse Speed-1.m4v', 'Scooby Doo a prokleti Speed Demona', 3, 30010, 'cz', 'cz', 'en', 'Scooby Doo and WWE- Curse of the Speed Daemon', 'http://www.csfd.cz/film/449616-scooby-doo-wwe-prokleti-speed-demona/', 'Scooby Doo Wwe Curse Speed-1.jpg', 9000, 'Plyn až na doraz, Scooby-Doo, Shaggy a jejich parta to totiž v nadupaném zbrusu novém původním filmu dají dohromady s hvězdami WWE! Brzy potom, co Scooby a jeho tým Záhady s.r.o. dorazí na závody teréňáků, se začnou dít divné věci. Tajemný jezdec známý pouze jako Inferno chce soutěž za každou cenu sabotovat a všude působí zmatek. Scooby-Doo, Shaggy a jejich nový parťák za volantem přezdívaný Funebrák musí spolu s dalšími hvězdami WWE jako jsou Triple H, Paige a Sheamus nejen zachránit závody, ale i přijít na kloub jedné záhadě. Je nejvyšší čas nastartovat motor a dobrou náladu, protože Scooby-Doo a team WWE právě vyrážejí za dobrodružstvím a smíchem - pouze pro vás! (Magic Box)', '2016-12-03 19:29:47.029', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999954, '201611/INDEPENDENCE_DAY_RESURGENCE.m4v', 'Den nezavislosti - Novy utok', 4, 0, 'cz', 'cz', 'en', 'Independence Day - Resurgence', 'http://www.csfd.cz/film/302950-den-nezavislosti-novy-utok/', 'INDEPENDENCE_DAY_RESURGENCE.jpg', 9200, 'Věděli jsme, že se jednou vrátí. Využili jsme proto k obraně všech možností a zdrojů Země. Studovali jsme ukořistěné mimozemské technologie, plány a způsob myšlení vetřelců. Poučili jsme se z předchozího boje o přežití i z cesty k našemu vítězství. Ale bude to stačit? Jsme připraveni na to, co se nyní z hloubi vesmíru blíží k naší Zemi? Nebo nás před zánikem zachrání hlavně naše vynalézavost a důvtip? Jak je vůbec možné čelit vesmírné lodi, která k zaparkování potřebuje celý Atlantský oceán?  Den nezávislosti režiséra Rolanda Emmericha před dvaceti lety nově vymezil žánr katastrofického filmu, přinesl nový typ celosvětové katastrofy a ohrožení veškerého lidstva. Nyní se vrací, aby dokončil to, co tenkrát započal. Souboj lidstva s brutálním, bezcitným a technologicky nadřazeným mimozemským nepřítelem bude buď vítězný, nebo poslední, protože v případě porážky žádný člověk nepřežije. (CinemArt CZ)', '2016-12-03 19:29:47.092', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999959, '201611/SCOOBY_DOO_AND_KISS.m4v', 'Scooby Doo - Kiss Rock and Roll Mystery', 3, 30010, 'cz', 'cz', 'en', 'Scooby Doo and Kiss - Rock and Roll Mystery', 'http://www.csfd.cz/film/55548-scooby-doo-a-skupina-kiss/', 'SCOOBY_DOO_AND_KISS.jpg', 9001, 'Připravte se na pořádný hukot! Ve zbrusu novém úžasném dobrodružství se Scooby-Doo a jeho tým Záhady s.r.o. sešli se skvělou a jedinečnou kapelou KISS! Vydáme se s celou partou do Světa KISS, kde vyšetřují sérii strašidelných událostí. S pomocí hudebníků zjistí, že se do zábavního parku, kde je všechno KISS, vrátila Rudá stvůra a chystá se povolat Ničitele z dimenze KISSterie. Zločinná dvojice má ďábelský plán: zničit Zemi! Dokáže důvtip našich hrdinů a rockový nářez kapely KISS zachránit situaci? Nalaďte se na jízdu v rytmu rock´n´rollu a užijte si třeskutou ZÁBAVU! (Magic Box)', '2016-12-03 19:29:47.248', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999961, '201611/CERVENY_KAPITAN.m4v', 'Cerveny kapitan', 15, 0, 'sk', '  ', 'sk', 'Cerveny kapitan', 'http://www.csfd.cz/film/385272-rudy-kapitan/', 'CERVENY_KAPITAN.jpg', 9201, 'Příběh se odehrává v horkém létě roku 1992, půl roku před rozdělením Československa. Mladý detektiv Richard Krauz se dostává k novému případu – na hřbitově byla vykopána mrtvola kostelníka Kloknera s hřebíkem v hlavě. Krauz zjišťuje, že za jeho umučením stojí bývalá StB a nikdy nenalezený spis zachycující zločiny kněží, na jehož základě StB církev vydírala. Případem posedlý Krauz se vydává napříč rozpadajícím se Československem hledat muže, ke kterému vedou všechny stopy: Rudého kapitána, specialistu na „konečné" výslechy. Krauz však není sám, kdo ho hledá... (A-Company CZ)', '2016-12-03 19:29:47.296', NULL, NULL);
