INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000000, '201612/Me Before You-1.m4v', 'Predtym ako som ta poznala', 6, 0, 'cz', 'cz', 'en', 'Me Before You', 'http://www.csfd.cz/film/407687-nez-jsem-te-poznala/', 'Me Before You-1.jpg', 9000, 'Louise ví leccos. Ví, že se jí líbí práce v bistru, a ví, že možná nemiluje svého přítele Patricka. Zato neví nic o klasické hudbě, neví, že přijde o místo, a kdyby tušila, co ji čeká, asi by tomu stejně nevěřila. Will zase po dopravní nehodě ztratil chuť do života. Ví, čím byl kdysi a všechno se mu nyní zdá nepodstatné, a také přesně ví, jak s tím skoncovat. Zato neví, že Lou mu vtrhne do života jako náhlá bouře. A ani jeden z nich neví, že jeden druhého navzájem navždy změní ... (Forum Film CZ)', '2016-12-09 18:52:02.701', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000001, '201612/London Has Fallen-1.m4v', 'Londyn v plamenoch', 15, 0, 'cz', 'cz', 'en', 'London Has Fallen', 'http://www.csfd.cz/film/376634-pad-londyna/', 'London Has Fallen-1.jpg', 9000, 'Bílý dům dobyli a teď si teroristé vezmou ještě větší sousto. Na pohřeb britského premiéra se sjedou lídři celého západního světa a díky bezpečnostním manévrům by Londýn v tu chvíli měl být tím nejbezpečnějším místem na zemi. Místo toho se ale stane smrtící pastí. Překvapivé a drtivé útoky za sebou zanechají světové vůdce v pozicích mrtvol, výbuchy šíří zkázu a monumenty anglické metropole jsou v plamenech. Londýn a západní svět se během chvíle ocitnou na kolenou. Hrozivé situaci a budoucnosti v totálním chaosu se znovu postaví Gerard Butler po boku s britským agentem. Záchrana nespočívá v předvídatelných vojenských manévrech, ale v poctivé ruční práci. (Bontonfilm CZ)', '2016-12-09 18:52:02.831', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000002, '201612/Divergent-35.m4v', 'Divergence', 6, 0, 'cz', 'cz', 'en', 'Divergent', 'http://www.csfd.cz/film/328760-divergence/', 'Divergent-35.jpg', 9000, 'Ve světě budoucnosti jsou lidé rozděleni na pět frakcí podle svých dobrých vlastností. Každá vyznává jiné hodnoty, společně ale vytvářejí harmonický celek. Jenže hlavní hrdinka Tris do žádné škatulky nezapadá a test ukáže, že je divergentní - nebo-li má předpoklady a talent pro více frakcí. Systém okamžitě ukáže svou odvrácenou tvář a z Tris se stává lovná zvěř. Kdo se liší, znamená hrozbu. Hrozbu je třeba zlikvidovat. (Bontonfilm CZ)', '2016-12-09 18:52:02.849', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000003, '201612/Snowpiercer-2.m4v', 'Snowpiercer', 4, 0, 'cz', 'cz', 'en', 'Snowpiercer', 'http://www.imdb.com/title/tt1706620/?ref_=fn_al_tt_1', 'Snowpiercer-2.jpg', 0, 'Directed by Joon-ho Bong.  With Chris Evans, Jamie Bell, Tilda Swinton, Ed Harris. Set in a future where a failed climate-change experiment kills all life on the planet except for a lucky few who boarded the Snowpiercer, a train that travels around the globe, where a class system emerges.', '2016-12-09 18:52:02.875', NULL, NULL);
