INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000005, '201612/Finding Dory-1.m4v', 'Hlada sa Dory', 3, 0, 'sk', 'cz', 'en', 'Finding Dory', 'http://www.csfd.cz/film/334813-hleda-se-dory/', 'Finding Dory-1.jpg', 9000, 'V animovaném filmu od studií Disney•Pixar Hledá se Dory se na stříbrná plátna vrací oblíbená modrá rybka Dory, která si spokojeně žije na korálovém útesu s Nemem a Marlinem. Pak si ale Dory z ničeho nic vzpomene, že kdysi a kdesi ztratila své rodiče. Společně s Marlinem a Nemem se vydá na strhující dobrodružství napříč oceánem, až do prestižního Mořského akvária v Kalifornii, kde se léčí nemocní mořští živočichové. Při hledání maminky a tatínka potkává Dory tři svérázné obyvatele Mořského akvária: První je Hank, nerudný chobotničák, který úspěšně uniká zřízencům; druhý je Bailey, samec běluhy, který sám sebe přesvědčil, že mu nefunguje echolokace, a třetí je krátkozraká velryba jménem Naděje. Při prohledávání moderního akvária dojde Dory se svými kamarády k poznání, že své blízké máme nejraději kvůli jejich nedostatkům, a že to nejdůležitější na světě je přátelství a rodina. (Falcon)', '2016-12-09 18:52:02.92', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999964, '201611/s06e10 The Winds of Winter.m4v', 'The Winds of Winter', 4, 1000, 'cz', 'cz', 'en', 'The Winds of Winter', 'http://www.csfd.cz/film/263138-hra-o-truny/425390-vichry-zimy/', 's06e10 The Winds of Winter.jpg', 610, 'Before her trial, Cersei destroys the Sept of Baelor by wildfire, killing the High Sparrow, Margaery, Mace and Loras Tyrell, Lancel and Kevan Lannister, along with hundreds of King,s Landing nobles and the Faith Militant, while Qyburn has Pycelle killed. Tommen commits suicide. In Dorne, Varys meets with Olenna and Ellaria, seeking to form an alliance between Daenerys and their houses against the Lannisters. Seaworth confronts Melisandre over the death of Shireen; Jon banishes her from Winterfell. The Wildlings, the Knights of the Vale and the surviving Houses of the North pledge loyalty to Jon as the new King in the North. Sansa stops Baelish from getting intimate with her. Arya kills Walder Frey and his sons. Tarly and Gilly reach the Citadel in Oldtown. Benjen tells Brandon and Reed to pass the Wall without him. Brandon uses his powers and learns that Jon is actually the son of Lyanna and Rhaegar, being adopted by Eddard after she died during Robert,s rebellion. Jaime returns to King,s Landing to find Qyburn crowning Cersei the new monarch of the Seven Kingdoms. Daenerys leaves Naharis and the Second Sons to rule Meereen before she sets sail to Westeros with her other companions, armies and dragons.', '2016-12-03 19:29:47.395', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000004, '201612/Jurassic World-21.m4v', 'Jursky svet', 4, 0, 'cz', 'cz', 'en', 'Jurrasic World', 'http://www.csfd.cz/film/313628-jursky-svet/', 'Jurassic World-21.jpg', 9000, 'Před dvaadvaceti lety tragicky skončil sen excentrického milionáře Johna Hammonda, který chtěl na odlehlém ostrově z dinosauří DNA vypěstovat Jurský park s živými exponáty. Od té doby se ale mnohé změnilo, park jede na plné obrátky, ročně jím projdou milióny nadšených návštěvníků, kteří s očima navrch hlavy sledují v akci desítky „vyhynulých" živočichů. Jenže vedení parku stále hledá nové způsoby, jak tuto jedinečnou atrakci ještě víc zatraktivnit. A v tu chvíli přijdou komplikace. Vědecké experimenty s novými druhy mohutně podporuje strohá manažerka parku Claire Dearing (Bryce Dallas Howard), která ve zvířatech vidí především čísla. Na rozdíl od ní v nich biolog Owen Grady (Chris Pratt ze Strážců galaxie) vidí hlavně nebezpečí, zvlášť ve tvorech, které nestvořila příroda, ale laboratoř. Události mu dají za pravdu, když se jeden z takových mazlíčků doslova urve ze řetězu a začne si vylepšovat jídelníček zaměstnanci parku a posléze také návštěvníky, mezi nimiž jsou také dva synovci Claire, kteří za tetou přijeli na prázdniny. Teď už nejde o vyšší tržby ani o udržení prestiže. Cílem všech začalo být holé přežití. (CinemArt CZ)', '2016-12-09 18:52:02.899', NULL, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000011, '201612/Drazsi_nez_rubiny-1.m4v', 'A Price Above Rubies', 6, 0, 'cz', 'cz', 'en', '', 'http://www.csfd.cz/film/18092-drazsi-nez-rubiny/', 'Drazsi_nez_rubiny-1.jpg', 9000, 'V upjatém světě má svoboda vysokou cenu... Sonia Horowitzová se přivdala do ortodoxní židovské rodiny, která přísně dodržuje veškeré rituály a pravidla. To, co zpočátku obdivovala, se však brzy stává nesnesitelně svazujícím. Její očekávání emocí a vášní zůstávají nenaplněna, manželský vztah je na bodu mrazu a Sonia tuší, že bude muset obětovat to nejcennější, co má, aby znovu našla sebe samu... (H.C.E.)', '2016-12-16 18:47:32.189', 1998, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000016, '201612/Astroboy-2.m4v', 'Astro Boy', 3, 0, 'cz', 'cz', 'en', 'Astro Boy', 'http://www.csfd.cz/film/238130-astro-boy/', 'Astroboy-2.jpg', 9000, 'Je to pták? Je to letadlo? Ne, mechanický kluk – Astro Boy. Byl stvořen pro napínavá dobrodružství, které může prožít jenom on. Jeho táta, slavný vědec Tenma, mu do vínku dal super sílu, rentgenové paprsky, rychlost a schopnost létat nad oblaky. Astro Boy poznává svět, učí se lidským emocím, když tu je jeho rodina a přátelé ocitnou v nouzi. Odvážný kluk se proto vrací do rodného Metro City, aby čelil možná dosud největší životní výzvě. Astro Boy pochází z hlavy Osamu Tezuky, který je přezdíván Bůh mangy, tedy japonských komiksů. Příběhy malého chlapce začal vydávat počátkem padesátých let minulého století. Ty byly později přepracovány do televizního seriálu. Právě jeho podoba tehdy udala směr celé japonské animované produkci a dodnes je v ní patrný Tezukův vliv. (oficiální text distributora)', '2016-12-16 18:47:32.305', 2009, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000017, '201612/Cokolada-2.m4v', 'Cokolada', 6, 0, 'cz', 'cz', 'en', 'Chocolate', 'http://www.csfd.cz/film/4085-cokolada/', 'Cokolada-2.jpg', 9000, 'Děj filmu začíná v tradiční francouzské vesnici, kde se za posledních sto let život vůbec nezměnil. Jednoho obyčejného dne se však ve městě objevuje cestovatelka Vianne Rocher (Juliette Binoche) a její dcera Anouk (Victoire Thivisol). Záhadná Vianne si otevře obchod s čokoládou plný neodolatelných sladkostí, které probouzejí u obyvatel města skryté chutě. Vianne má totiž tajemnou schopnost odhadnout soukromé vášně vesničanů a uspokojit je tím správným cukrovím, čímž se jí pomalu daří je přesvědčovat, aby se nebáli oddávat se svým vlastním pokušením. Brzy si Vianne získá dobrou pověsta nepřátele: poctivého místního šlechtice Comte de Reynaud (Alfred Molina). Reynaud je přesvědčen, že Viannina čokoláda zamává s městem a dočista podlomí jeho přísný zákon morálky. Postupně tak vznikne spor mezi těmi, kteří chtějí zachovat dosavadní způsob života, a těmi, kteří si chtějí užít chuť své nově objevené svobody. Vianne pomalu získává hluboký vliv na obyvatele vesnice, včetně sedmdesátileté Armande (Judi Dench) a její podivínské dcery Caroline (Carrie-Anne Moss); trpitelky Josephine Muscat (Lena Olin) a jejího britského manžela Serge (Peter Stormare). Vianne nenechá chladným ani dalšího neobvyklého příchozího, cestovatele na lodi Rouxe (Johnny Depp). Rouxovi se přitom konečně podaří probudit ve Vianne její vlastní skryté touhy: k někomu patřit. ČOKOLÁDA je komedie o tom, jak jediné sladké sousto dokáže změnit člověka, lidské vztahy i celé město. Je příběhem pokušení, odříkání a otevření citům - komedií o vznikající maloměstské válce plné jiskrných vášní a strachu způsobených otevřením tajemného obchodu s čokoládou. Scénář filmu napsal Nelson Jacobs na motivy vysoce ceněného románu Joanne Harrise Roberta. (oficiální text distributora)', '2016-12-16 18:47:32.323', 2000, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000007, '201612/Zamrzla Reka-7.m4v', 'Zamrzla reka', 6, 0, 'cz', 'cz', 'en', 'Frozen River', 'http://www.csfd.cz/film/245544-zamrzla-reka/', 'Zamrzla Reka-7.jpg', 9000, 'Děj se odehrává v období těsně před Vánocemi v rezervaci Mohawků, poblíž nepříliš známého hraničního přechodu, spojujícího státy New York a Québec. Převádění nelegálních, převážně asijských imigrantů přes zamrzlou řeku St. Lawrence představuje možnost snadného zisku peněz a ta je až příliš lákavá pro ty, kteří si legální cestou dokážou vydělat byť jen minimální prostředky. Dvě ženy - běloška Ray a indiánka Lila, obě svobodné matky, nacházející se v zoufalé životní situaci - jsou vtaženy do světa obchodování s lidmi a nakonec neuniknou spravedlnosti. (pee-jay)', '2016-12-16 18:47:32.077', 2008, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000018, '201612/Spy Kids 4 Stroj Casu-1.m4v', 'Spy Kids 4: All the Time in the World', 3, 0, 'cz', 'cz', 'en', 'Spy Kids 4 Stroj Casu', 'http://www.csfd.cz/film/267966-spy-kids-4d-stroj-casu/', 'Spy Kids 4 Stroj Casu-1.jpg', 9000, 'Marissa Cortez Wilsonová má vše, po čem touží - úspěšného a pohledného manžela, nádherné miminko a dvě úžasně chytré, nevlastní děti… jenže nic není tak růžové, jak se zdá. Její malá dcera si dělá, co chce, dvojčata, Rebecca a Cecil, ji jemně řečeno nesnáší a manžel by ani nepoznal, že doma žije se superšpionkou. Marissin svět se otočí vzhůru nohama, když šílený padouch hrozí ovládnout svět a ona je povolána do akce. Kromě ní jsou však do záchranné mise zapojeni i Rebecca s Cecilem. Nejen že zjistí pravou totožnost své macechy, musí také zapojit své mozkové závity a přijít na to, jak zloducha zneškodnit. Veškerá legrace jde teď stranou. Spy Kids bojují za záchranu stroje času. (oficiální text distributora)', '2016-12-16 18:47:32.345', 2011, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000012, '201612/Dirty Dancing 2-1.m4v', 'Hriesny tanec 2', 6, 0, 'cz', 'cz', 'en', 'Dirty Dancing 2', 'http://www.csfd.cz/film/135337-hrisny-tanec-2/', 'Dirty Dancing 2-1.jpg', 9000, 'Američanka Katey prichádza aj so svojimi rodičmi na Kubu tesne pred vypuknutím revolúcie. Rodičia od nej očakávajú, že si nájde dobrú partiu a stane sa tak ako oni súčasťou miestnej smotánky, no ona sa zaľúbi do Xaviera - čašníka, ktorý je vynikajúcim tanečníkom. Tajne sa stretávajú v nočnom havanskom klube a začnú spolu trénovať na prestížnu národnú súťaž v tanci. Ale blížiaca sa revolúcia skomplikuje ich plány. (oficiální text distributora)', '2016-12-16 18:47:32.217', 2004, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000010, '201612/Reservation Road-3.m4v', 'Reservation Road', 15, 0, 'cz', 'cz', 'en', 'Reservation Road', 'http://www.csfd.cz/film/224818-reservation-road/', 'Reservation Road-3.jpg', 9000, 'Reservation Road je příběhem Ethana (Joaquin Phoenix) a Dwighta (Mark Ruffalo), dvou otců, kteří se musí smířit se smrtí dítěte. Ethan, otec oběti autonehody, se nedokáže se situací vyrovnat a stává se čím dál posedlejší v hledání vraha a odplaty. Jeho žena (Jennifer Connelly) je opačného názoru a obává se, že tato fixace zničí zbytek jejich rodiny. Dwight je ten, kdo Ethanova syna srazil a odjel z místa činu. Neuvěřitelný strach, že mu jeho bývalá žena (Mira Sorvino) zakáže kontakt s vlastním synem, mu nedovolí se přiznat. Přestože bojuje se svým svědomím, snaží se zůstat neodhalen a udržet rozpadající se rodinu alespoň částečně pohromadě. Jenže lež má krátké nohy a Dwight je svým zaměstnavatelem přiřazen jako právník Ethana v případu jeho syna. Teď už je jen otázkou času, kdy se pravda dostane na povrch! (oficiální text distributora)', '2016-12-16 18:47:32.163', 2007, NULL);
