INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000006, '201612/Lovesong-3.m4v', 'Love Song', 6, 0, 'cz', 'cz', 'en', 'Begin Again', 'http://www.csfd.cz/film/312317-love-song/', 'Lovesong-3.jpg', 9000, 'Gretta (Keira Knightley) a její přítel Dave (Adam Levine) jsou spolu již od vysoké školy a společně skládají hudbu. Když Dave podepíše smlouvu s jednou z největších nahrávacích společností, odstěhují se spolu do New Yorku, aby zde začali život, o kterém vždycky snili. Dave však rychle nabytou slávu neustojí a brzy Grettu opustí. Všechno se však změní, když Gretta potká během svého vystoupení v zapadlém baru v East Village bývalého hudebního producenta Dana (Mark Ruffalo). Ten na tom taky není zrovna nejlíp a svoje zkrachovalé manželství a nedávný vyhazov z práce utápí v alkoholu. Když ji pak uvidí vystupovat, rozhodne se vsadit všechno na její talent a nahrát netradiční album přímo v ulicích New Yorku. Z náhodného setkání dvou zklamaných lidí se tak začne rodit hudba, která možná nedobyje svět, ale rozhodně si získá srdce všech. (A-Company CZ)', '2016-12-16 18:47:31.953', 2013, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000013, '201612/Zare-2.m4v', 'Zare', 6, 0, 'cz', 'cz', 'en', 'Shine', 'http://www.csfd.cz/film/4327-zare/', 'Zare-2.jpg', 9000, 'Otec Davida, Peter, podivín a despota, ač nemá odpovídající hudební vzdělání, učil Davida od dětství hře na klavír a z Davida rostl slibný klavírista. Peter má zvláštní způsoby a rodině vládne silnou rukou. Když se mu na jedné soutěži zdá klavír rozladěný, udělá tam výstup a demonstrativně odvede Davida domů ještě před vyhlášením výsledků. Člen poroty Ben Rosen přinese Davidovi až domů Zvláštní cenu za odvahu, protože David hrál velice těžkou skladbu, což Peter přijímá rozladěně a odmítá Rosenovu nabídku, že bude Davida učit jako mimořádný talent zdarma. Nakonec však Davida k Rosenovi přece jen dovede. Zanedlouho David vyhraje celostátní soutěž a dostane nabídku studovat v Americe. Rodina nemá peníze ani na cestu, ale akce se ujme opět Rosen. David hraje na různých akcích a na jedné z nich se seznámí se známou starší spisovatelkou Katherine Susannah Prichardovou. S ní zůstává v kontaktu až do její smrti. David se těší na studium v Americe, ale Peter v poslední chvíli rozhodne, že nikam nepojede. David zatrpkne, ale dále se věnuje hudbě. Brzy je mu však jasné, že bez dalšího studia se nikam nedostane a bez vědomí otce se zkontaktuje s Královskou hudební školou. Získá stipendium a přes otcův odpor odjíždí studovat do Londýna. Na jeho dopisy otec neodpovídá a oba se tím trápí, ani jeden však nechce ustoupit (oficiální text distributora)', '2016-12-16 18:47:32.246', 1996, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000014, '201612/Predcitac-2.m4v', 'Predcitac', 6, 0, 'cz', 'cz', 'en', 'The Reader', 'http://www.imdb.com/title/tt0976051/?ref_=fn_al_tt_1', 'Predcitac-2.jpg', 9000, 'Directed by Stephen Daldry.  With Kate Winslet, Ralph Fiennes, Bruno Ganz, David Kross. Post-WWII Germany: Nearly a decade after his affair with an older woman came to a mysterious end, law student Michael Berg re-encounters his former lover as she defends herself in a war-crime trial.', '2016-12-16 18:47:32.261', 2009, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000019, '201612/Verejny Nepritel C1-2.m4v', 'Verejny nepritel c1', 15, 0, 'cz', 'cz', 'en', 'L Instinct de mort', 'http://www.csfd.cz/film/246077-verejny-nepritel-c-1/', 'Verejny Nepritel C1-2.jpg', 9000, 'Jacques Mesrine, největší francouzský gangster všech dob, byl ve své vlasti známější než jakákoliv celebrita. Jeho avantýry byly neskutečně lehkomyslné, neuvěřitelně drzé a jeho úmysly absolutně nepředvídatelné. Na každou novou „fušku“ se pomocí plastických operací a nejrůznějších masek měnil k nepoznání, a proto byl přezdíván „mužem tisíce tváří“. Národ ho uctíval, ženy ho zbožňovaly a pro stát byl nepřítelem číslo jedna. Jeho odvážné skutky, nesčetné útěky z vězení, milostné románky, nad nimiž zůstával rozum stát, jeho bájná dobrodružství, obrovský cynismus a výsměch policii, z toho všeho vznikl pro Francouze skutečný národní epos. Epos o člověku, který byl skutečným hrdinou nebo antihrdinou… Filmy Veřejný nepřítel č. 1 a Veřejný nepřítel č. 1: Epilog (premiéra 1.1.2009) se natáčely souběžně celých devět měsíců, na třech kontinentech a s rozpočtem přes 80 miliónů Eur. Jedná se o nejdražší francouzskou produkci všech dob. (Festival francouzského filmu)', '2016-12-16 18:47:32.374', 2008, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000015, '201612/Otcove A Dcery-2.m4v', 'Otcove a dcery', 6, 0, 'cz', 'cz', 'en', 'Fathers and Daughters', 'http://www.csfd.cz/film/367091-otcove-a-dcery/', 'Otcove A Dcery-2.jpg', 9000, 'Jack Davis (Russell Crowe) se musí po tragické smrti své manželky sám postarat o jejich pětiletou dcerku Katie (Kylie Rogers). Jack je slavný spisovatel, držitel Pulitzerovy ceny, který trpí depresemi a duševní poruchou. Po smrti ženy svádí boj nejen sám se sebou, ale i s výchovou Katie. O 25 let později. V dospělosti Katie (Amanda Seyfried) žije se svým přítelem Cameronem (Aaron Paul) na Manhattanu. Jejich vztah se potýká s problémy. Amanda bojuje s vnitřními démony, kteří ji pronásledují od jejího problematického dětství. Film Otcové a dcery zachycuje vztah dítěte a rodiče, kteří se v průběhu let odcizili. Přes staré křivdy a rány, které během života utržili, se snaží znovu k sobě najít cestu... (Bioscop)', '2016-12-16 18:47:32.283', 2015, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000022, '201612/San Andreas-1.m4v', 'San Andreas', 15, 0, 'cz', 'cz', 'en', 'San Andreas', 'http://www.csfd.cz/film/319379-san-andreas/', 'San Andreas-1.jpg', 9000, 'Poté, kdy o sobě dá vědět nechvalně známý tektonický zlom San Andreas a vyvolá v Kalifornii zemětřesení o síle 9 Richterovy stupnice, vydávají se pilot průzkumného a záchranného vrtulníku ( Dwayne Johnson) a jeho bývalá manželka, s níž již delší dobu nežije, na cestu z Los Angeles do San Francisca zachránit svou jedinou dceru. Ale jejich složitá cesta na sever je pouze začátek. A když už si myslí, že to nejhorší už mají za sebou... tak velké drama teprve začíná. (Freeman Ent.)', '2016-12-23 17:16:50.519', 2015, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000023, '201612/Casper-2.m4v', 'Casper', 3, 0, 'cz', 'cz', 'en', 'Casper', 'http://www.csfd.cz/film/8404-casper/', 'Casper-2.jpg', 0, 'Chamtivá Carrigan Crittenden zuří, když zjistí, že její otec odkázal veškerý majetek na dobročinné účely a jí odkázal pouze starý dům poblíž vesničky Friendship. Když v záchvatu vzteku hodí závěť do krbu, objeví se tajné písmo s informací pokladu, který je v domě ukryt. Kromě pokladu se však v opuštěném strašidelném domě ukrývá také malý duch Casper a jeho tři druhové, zlomyslní duchové Smraďoch, Tlusťoch a Pérák. Carrigan a její milenec Dibs se sice pokusí s pomocí vymítačů a krotitelů duchů dům duchů zbavit, jejich snahy jsou však marné. Casper, který marně hledá kamaráda, zahlédne v televizi psychiatra Jamese Harveyho a jeho malou dcerku Kate. Zatímco doktor je údajně odborníkem na terapii duchů, Casperovi se na první pohled zalíbí jeho dcerka. Nenápadně navede Carrigan, aby si doktora najala. James její nabídku přijme, ale Kat není příliš nadšená, protože to znamená další stěhování, už deváté za poslední dva roky. Chtěla by se už konečně usadit a mít kamrády. Její otec však nepřestává hledat svou zemřelou manželku Amelii, která podle něj bloudí světem jako neusmířená duše. První setkání dopadne stejně jako vždy. Kat s křikem uteče. A panice propadne i James, kterého notně potrápí trojice zlomyslných duchů. Postupně se však situace uklidní. James uzavře příměří s trojicí duchů, kterým slíbí, že jim pomůže od Carrigan výměnou za slib, že mu umožní setkání s duchem Amelie, který ovšem nehodlají dodržet. Casper se pomalu sbližuje s Kat, vezme ji na noční let nad mořem a pak jí na starém majáku vypráví o sobě a o tom, že si vůbec nepamatuje na svůj předchozí život. S pomocí hraček, které dívka najde na půdě, si však malý duch vzpomene na to, jak jako malý kluk neposlechl tatínka, nastydl a umřel. Z novin pak dívka zjistí, že jeho otec byl vynálezce, který vymyslel přístroj na oživování mrtvých, látky potřebné k oživení je však málo, jen na jedno použití. Situace se stane vážnou, když se Carrigan pokusí zabít svého milence, aby pro ni jako mohl krást, ale sama se přitom zabije a stane se duchem. Trojice duchů zase zabije Jamese, protože by ho chtěli jako nového kamaráda. Casper, který se chtěl s pomocí přístroje vrátit mezi živé, však pochopí, že Kat potřebuje otce a rozhodne se využít jedinou dávku k tomu, aby oživil Jamese. Během halloweenového večírku, který se koná ve strašidelném domě, se každý po zásluze dočká zasloužené odměny. (oficiální text distributora)', '2016-12-23 17:16:50.559', 1995, NULL);
