INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000021, '201612/101_Dalmatians_II-2.m4v', '101 Dalmatincov II', 3, 0, 'cz', 'cz', 'en', '101 Dalmatians II', 'http://www.csfd.cz/film/88173-101-dalmatinu-ii-flickova-londynska-dobrodruzstvi/', '101_Dalmatians_II-2.jpg', 9000, 'Populární animovaný snímek studia Disney 101 Dalmatinů pokračuje zcela novým příběhem ve filmu 101 Dalmatinů II: Flíčkova londýnská dobrodružství. Flíček je stále obklopen spoustou svých bratrů a sestřiček. V tomto moři černobílých skvrnek zatouží vystoupit z anonymního davu a stát se hrdinou - jako jeho televizní vzor Blesk Jednoho dne se Flíček omylem ztratí své rodině a jako zázrakem se setká s Bleskem. A co víc, stane se jeho parťákem! Život slavného psa ale není zcela tak jednoduchý, jak to vypadá na televizní obrazovce. Mezitím se známá Gruella zamiluje do nového druhu umění - flekatých obrazů. Aby inspirovala jejich tvůrce, unese zbytek dalmatinní rodiny. A tak je na Flíčkovi a Bleskovi, aby spolu vymysleli plán na jejich záchranu. Oba parťáci si tak vyslouží daleko větší obdiv a úctu, než je pomíjivá sláva umělé televizní hvezdy. (oficiální text distributora)', '2016-12-23 17:16:50.314', 2003, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000024, '201612/Iron Giant Signature Edition-1.m4v', 'Zelezny obor', 3, 0, 'en', 'cz', 'en', 'Iron Giant', 'http://www.csfd.cz/film/12110-zelezny-obr/', 'Iron Giant Signature Edition-1.jpg', 9000, 'Teprve devítiletý Hogarth zachrání obrovského robota, který na Zemi přiletěl z vesmíru. Hogarth se snaží svého nového geniálního kamaráda ubránit před vládním agentem a vojáky. Ve zdánlivě klidném městečku Rockwell ve státě Maine žije v roce 1957 devítiletý chlapec Hogarth, který sní o invazi vetřelců a bitvách s komunistickými agenty. Jednoho dne se mu jeho sen splní - do nedalekého jezera prý spadl obrovský robot z vesmíru. Hogarthovi se podaří robota vylovit a navzdory protestům své matky Annie ho schová u kamaráda Deana. O robotově přítomnosti se brzy dozví vládní agent Kent Mansley, který chce vetřelce zničit za každou cenu. A bude jen na Hogathovi, jestli se mu to podaří. (HBO Europe)', '2016-12-23 17:16:50.602', 1999, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000025, '201612/Cesta Za Vanocni Hvezdou-6.m4v', 'Cesta za vianocnou hviezdou', 3, 0, 'cz', 'cz', 'no', 'Reisen til julestjernen', 'http://www.csfd.cz/film/348468-cesta-za-vanocni-hvezdou/', 'Cesta Za Vanocni Hvezdou-6.jpg', 9000, 'Mimořádně poutavý pohádkový příběh s ohromující filmovou výpravou a kostýmy se odehrává v překrásné zasněžené norské přírodě. Začíná v předvánočním čase, kdy do malé vesničky dorazí dvanáctiletá odvážná dívka Sonja v doprovodu lupičů, kteří ji drží v zajetí. Vesnice je totiž zakletá, stejně jako celé království. Kdysi dávno král ztratil svou jedinou dceru Gulltopp, která se vydala do lesa hledat Vánoční hvězdu. Dcera se však už nikdy nevrátila. Královna zemřela žalem a nešťastný král Vánoční hvězdu proklel. Na jeho království sestoupila tma a smutek. Jednoho večera přišel do jejich paláce mudrc, který králi předpověděl, že pokud se mu podaří do deseti let Vánoční hvězdu opět rozzářit, jeho dcera se vrátí domů. Od jejího zmizení uplynulo již devět let a králi zůstává poslední naděje. Sonje se podaří lupičům utéci a dostane se do zámku, kde si vyposlechne smutný příběh královské rodiny. Když jí král podá záchrannou ruku, Sonja se z vděčnosti ke králi rozhodne Vánoční hvězdu najít a konečně zlomit kletbu, která již dlouhou dobu visí nad jeho královstvím. (oficiální text distributora)', '2016-12-23 17:16:50.622', 2012, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000026, '201612/Tooth Fairy-1.m4v', 'Zubkova vil', 3, 0, 'cz', 'cz', 'en', 'Tooth Fairy', 'http://www.csfd.cz/film/239581-vila-zubenka/', 'Tooth Fairy-1.jpg', 9000, 'Chovat se špatně ke svému okolí se nevyplácí. Své o tom ví drsný hokejista (Dwayne Johnson), který musí za trest jeden týden plnit povinnosti tajemné Víly Zuběnky. Derek Thompson (Dwayne Johnson) je drsný hokejista, který s oblibou vyráží svým spoluhráčům zuby. V hokeji se mu kvůli zranění právě moc nedaří, a tak není dobře naladěn. Vzhledem k jeho nemilosrdné pragmatičnosti jsou mu lhostejné naděje a sny malých fanoušků. Když jednoho dne téměř přesvědčí dcerku své přítelkyně Carly, že neexistuje víla Zuběnka, dochází trpělivost nejen Carly, ale vzbudí to velkou nevoli také v pohádkové říši. Derekovi je udělen speciální trest - na jeden týden je proměněn ve vílu Zuběnku se vším všudy, tedy včetně nadýchaných křídel a magické hůlky. Zpočátku má Derek velké potíže, nemá nejmenší tušení, jak si poradit s křídly, snaží se být nenápadný, ale moc mu to nejde. Postupně se však učí kouzla od svých okřídlených nadřízených a přichází na to, jak zvládnout vílí povinnosti. Ba co víc, začnou se mu vybavovat jeho dávné dětské sny... (HBO Europe)', '2016-12-23 17:16:50.672', 2010, NULL);
