INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000075, '201705/Mr And Mrs Smith-1.m4v', 'Pan a pani Smithovci', 6, 0, 'cz', 'cz', 'en', 'Mr. and Ms. Smith', 'http://www.csfd.cz/film/116726-mr-mrs-smith/', 'Mr And Mrs Smith-1.jpg', 0, 'Jane je krásná, mladá, inteligentní resuscitátorka počítačových systémů, John je mladý, pohledný a úspěšný v oblasti nemovitostí. Jejich osudy se prolnou v momentě, kdy na sebe narazí na dovolené a vzájemně si pomohou ze šlamastiky. Brzy na to si řeknou své ano a po pěti nebo šesti letech manželství si už spolu nemají co říct, z vášnivého vztahu je nuda a zdá se, že je nezachrání už ani manželská poradna. Jejich život ale začne nabírat na obrátkách ve chvíli, kdy se z Jane vyklube nájemná vražedkyně a z Johna nájemný vrah. Potíž je v tom, že oba pracují pro jinou agenturu a tak jednoho dne dojde k nevyhnutelnému. Oba dostanou za úkol zlikvidovat stejný cíl. Od toho okamžiku není v ohrožení jen jejich manželství, ale i jejich vlastní životy a konečným řešením už není rozvod, ale vražda. (Damielka)', '2017-05-04 20:42:46.532', 2005, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000076, '201705/Storks-1.m4v', 'Bociany', 3, 0, 'sk', 'cz', 'en', 'Storks', 'http://www.csfd.cz/film/2152-capi-dobrodruzstvi/', 'Storks-1.jpg', 0, 'Čápi nosí děti... nebo alespoň nosívali. Teď nosí balíčky pro celosvětového internetového giganta Cornerstore.com. Nejlepší doručovatel společnosti, čáp Junior, má být zanedlouho povýšen, když náhodně aktivuje stroj na výrobu dětí a ten bez jakékoliv autorizace vyrobí rozkošnou holčičku. Junior a jeho kamarád Tulip, jediný člověk na Čapí hoře, v zoufalé snaze doručit zásilku dřív, než se to dozví šéf, a závodí s časem, aby doručili své první dítě – na divoké a objevné cestě, která by mohla pomoci k úplnosti nejedné rodině a vrátit čápům na celém světě jejich původní poslání. (Freeman Ent.)', '2017-05-04 20:42:46.881', 2016, NULL);
