INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000077, '201705/innocence-d1.mp4', 'Ghost in the Shell: Innocence 1', 4, 47000084, 'en', 'en', 'jp', 'Ghost in the Shell: Innocence 1', 'http://www.csfd.cz/film/91969-innocence/', 'innocence-d1.jpg', 21, 'Inteligentní cyber-policajti, zabijáčtí sexuální roboti, mnohovrstevnatá zápletka, filozofující dialogy a nekompromisní akce - to je druhý díl kultovního anime Mamoru Oshiiho GHOST IN THE SHELL. Tentokrát se detektiv Batou musí krom vlastních existenciálních problémů vypořádat s ilegální společností, která programuje sexboty vraždící své majitele. Ale nic není zdaleka tak jednoduché, jak se na první pohled může zdát. (Matty)', '2017-05-04 20:42:46.921', 2004, NULL);
