INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000081, '201705/Mechanik Vzkriseni-2.m4v', 'Mechanik 2: Vzkriseni', 15, 0, 'cz', 'cz', 'en', 'Mechanic: Ressurection', 'http://www.csfd.cz/film/368019-mechanik-zabijak-vzkriseni/', 'Mechanik Vzkriseni-2.jpg', 0, 'Když už si Arthur Bishop myslel, že nechal svou vražednou minulost daleko za sebou, unesl jeho nejobávanější nepřítel jeho životní lásku. Nyní je nucen cestovat po celém světě, aby spáchal tři naprosto nemožné atentáty a udělal to, co umí nejlépe: spáchat je tak, aby vypadaly jako nehody. (Freeman Ent.)', '2017-05-04 20:42:47.003', 2016, NULL);
