INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000078, '201705/The Secret Life Of Pets-10.m4v', 'Tajny zivot maznacikov', 3, 0, 'sk', 'cz', 'en', 'The Secret Life Of Pets', 'http://www.csfd.cz/film/364125-tajny-zivot-mazlicku/', 'The Secret Life Of Pets-10.jpg', 0, 'Co dělají vaši čtyřnozí miláčci, když nejste doma? Odpověď na otázku, kterou se trápí každý kočičkář a pejskař, najdete v animované komediální lahůdce Tajný život mazlíčků. Max a Katie jsou spřízněné duše, ti naprosto nejlepší kámoši na světě. Co na tom, že Max je pes a Katie člověk? Kdyby se Katie každý den na dlouhé hodiny neztrácela a nenechávala Maxe samotného v jejich bytě, byl by nejspokojenější hafan pod sluncem. Takhle to aspoň platilo do chvíle, kdy si Katie přivedla z útulku chlupaté psí monstrum jménem Baron a rozhodla se, že od té chvíle budou žít ve třech. Mezi Maxem a Baronem to byla nenávist na první pohled. Od první vteřiny vymýšlejí, jak tomu druhému ztrpčit život, což je přivádí do čím dál komplikovanějších situací. Ve finále skončí ztracení, opuštění a obklíčení skupinkou osiřelých zvířat, která pod vedením šíleného králíka vládnou městskému podzemí. Naštěstí má Max doma pořádnou smečku přátel, kteří se rozhodnou, že ho nenechají na holičkách a vydají se ze svých pelíšků na mimořádně dobrodružnou výpravu, jejímž cílem je nalezení a záchrana Maxe. Musí to navíc zvládnout v šibeničním termínu, než se jejich páníčkové vrátí domů. Jestli by nakonec nebylo lepší nepřemýšlet o tom, co bez nás dělají. (CinemArt)', '2017-05-04 20:42:46.942', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000083, '201705/kubo-and-the-two-strings.mp4', 'Kubo a kuzelny mec', 3, 0, 'sk', 'cz', 'en', 'Kubo and the Two String', 'http://www.csfd.cz/film/400559-kubo-a-kouzelny-mec/', 'kubo-and-the-two-strings.jpg', 0, 'Umí vyprávět příběhy tak, že to bere dech. Chlapci Kubovi tenhle talent pomáhá uživit sebe a nemocnou matku. Den co den opouští jeskyni, ve které společně přebývají, a v přilehlé vesnici ohromuje obyvatele svým vyprávěním, které končí vždy před západem slunce. Domů se musí vrátit před setměním, jinak by ho podle matky mohla stihnout strašlivá kletba. Jednoho večera se však vrátit nedokáže. A varování se promění ve skutečnost. Najednou si uvědomí, že se stal hrdinou vlastního dobrodružného příběhu. V doprovodu chytré a přímočaré Opice a trochu praštěného Brouka se vydává po stopách svého otce, legendárního samuraje. Jeho cílem je nalezení otcovy zbroje, která mu má pomoci obstát v boji se zlem, které má v úmyslu zničit vše, co mu kdy bylo drahé. Tahle výprava bude mnohem dobrodružnější, strašidelnější a zábavnější než cokoliv z toho, co dosud vyprávěl. (CinemArt)', '2017-05-04 20:42:47.044', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000079, '201705/BRIDGET_JONES_S_BABY-21.mp4', 'Dieta Bridget Jonesovej', 6, 0, 'cz', 'cz', 'en', 'Bridget Jones'' Baby', 'http://www.csfd.cz/film/267969-dite-bridget-jonesove/', 'BRIDGET_JONES_S_BABY-21.jpg', 0, 'Utrpení největší smolařky v dějinách dosud neskončilo. Bridget Jonesová je stále bez chlapa. Jak název třetího dílu slavné série napovídá, tím hlavním malérem bude tentokrát neplánované těhotenství. Taková věc dokáže vykolejit i životem otřískanou Bridget, zvlášť když naprosto netuší, s kým počala. Jak se to rýmuje s faktem, že je stále bez chlapa? Celkem jednoduše. Stačí v doprovodu kolegyně navštívit odvázaný hudební festival a v jeho průběhu se vyspí s pohledným cizincem (Patrick Dempsey). A když se záhy na jedné společenské akci na vlastní tělo přesvědčí, že stará láska (Colin Firth) nerezaví, byť před pár lety definitivně skončila, je „štěstí" hotovo. Protože jí s rozluštěním zapeklitého rébusu nepomůže ani lehce cynická gynekoložka (spoluscenáristka Emma Thompson), ocitne se Bridget ve velmi nezáviděníhodné situaci. Jak seriózní právník Mark Darcy, tak úspěšný provozovatel internetové seznamky Jack Quant se sice k situaci postaví jako praví chlapi, odhodlaní s Bridget a jejím potomkem vytvořit spořádanou rodinu, to ovšem netuší, že jejich šance na otcovství je přesně padesátiprocentní. Jak se blíží termín porodu, Bridget cítí, že bude muset s pravdou ven a hlavně že si bude muset vybrat, komu bude její dítě říkat „tatínku". Normální člověk by poslal oba dva na testy otcovství. Jenže Bridget Jonesová není normální, ale výjimečná. (CinemArt)', '2017-05-04 20:42:46.962', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000085, '201705/Sully-1.m4v', 'Sully', 15, 0, 'cz', 'cz', 'en', 'Sully', 'http://www.csfd.cz/film/53975-sully-zazrak-na-rece-hudson/', 'Sully-1.jpg', 0, 'V lednu roku 2009 se svět stal svědkem „zázraku na řece Hudson", když kapitán „Sully" Sullenberger (Tom Hanks) nouzově přistál s poškozeným letadlem na ledových vodách řeky Hudson a zachránil tak život 155 pasažérů na palubě letadla. Přestože veřejnost a média mluvila o ojedinělém činu dokazujícím jeho letecké dovednosti, probíhající vyšetřování hrozilo zničit jeho pověst a kariéru. (Freeman Ent.)', '2017-05-08 19:24:57.116', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000086, '201705/Serendipity-1.m4v', 'Laske na stope', 6, 0, 'cz', 'cz', 'en', 'Serendipity', 'http://www.csfd.cz/film/11874-lasce-na-stope/', 'Serendipity-1.jpg', 0, 'Náhodou se setkali v obchodě. Náhodou se potkali ten samý den na tom samém místě ještě jednou. Pak se neviděli řadu let. Když už mají oba před svatbou, celý řetězec neuvěřitelných náhod je znovu přivede k sobě. Jsou to ale všechno opravdu jenom náhody? A je láska náhoda? Nebo je to osud, kterému člověk nemá šanci uniknout? Jsou zdánlivé náhody, vedoucí k tomu, že se lidé setkávají a míjejí, ve skutečnosti znameními osudu? A může mít osud smysl pro humor? Scenáristé tohoto amerického filmu bezpochyby smysl pro humor měli a výše uvedené otázky řeší s nadsázkou a lehkostí. Odvěké téma hledání toho pravého nebo té pravé rozvinuli v příběhu Jonathana a Sary - oba jsou profesionálně úspěšní, žijící ve spokojených partnerských vztazích se svatbou na spadnutí a ve vzdálenosti několika tisíc kilometrů. V této situaci jim oběma pár náhodných událostí připomene setkání před lety - stopováním osudových znamení, které se náhodami jen zdají, se pokouší najít jeden druhého. Toto poněkud pošetilé, nicméně osudové rozhodnutí je oba přivádí do řady komických situací a nakonec jednoho k druhému. (oficiální text distributora)', '2017-05-08 19:24:57.286', 2001, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000087, '201705/Podraz-2.m4v', 'Podraz', 15, 0, 'cz', 'cz', 'en', 'The Sting', 'http://www.csfd.cz/film/15046-podraz/', 'Podraz-2.jpg', 0, 'Robert Redford a Paul Newman v legendární filmu o jednom geniálním dostihovém podrazu. Film je příběhem o přátelství dvou roztomilých podvodníků. Johny, malý šikovný podvodník a jeho kumpán Luther omylem okradou poslíčka, který pracuje pro jednoho z největších a nejuznávanějších gangsterů - Lonnegana. Lonneganovi lidé však na odplatu Luthera zabijí a Johny s Henrym si naplánují ještě rafinovanější přepadení, aby Lonnegana úplně zlikvidovali. (oficiální text distributora)', '2017-05-08 19:24:57.314', 1973, NULL);
