INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000090, '201705/Arrival-3.m4v', 'Prichodzi', 4, 0, 'cz', 'cz', 'en', 'Arrival', 'http://www.csfd.cz/film/379848-prichozi/', 'Arrival-3.jpg', 0, 'Když po celém světě přistanou tajemné vesmírné lodě, dojde k sestavení elitního týmu – v čele s odbornicí na lingvistiku Louise Banksovou (Amy Adams) – s cílem zjistit, jaké mají mimozemšťané úmysly. Zatímco celé lidstvo balancuje na pokraji globální války, Banksová se společně se svým týmem pokouší ve velmi omezeném čase najít odpovědi – a aby je byla schopna najít, bude muset přistoupit k činům, které mohou ohrozit nejen její vlastní život, ale dost možná osud celého lidstva. (Falcon)', '2017-05-11 16:37:14.845', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000091, '201705/Jack Reacher Never Go Back-17.m4v', 'Jack Reacher: Nevracaj sa', 15, 0, 'cz', 'cz', 'en', 'Jack Reacher Never Go Back', 'http://www.csfd.cz/film/359552-jack-reacher-nevracej-se/', 'Jack Reacher Never Go Back-17.jpg', 0, 'Reacher nejraději pracuje tam, kde by byl oficiální postup příliš zdlouhavý. Jako bývalý major vojenské policie má rád zločiny s armádní příchutí a vůbec nejraději ty, které na první pohled nedávají smysl. Jako třeba obvinění velitelky Susan Turnerové (Cobie Smulders) ze špionáže. Krásku s dosud neposkvrněnou pověstí, kolem které začali náhle v podezřelém tempu umírat lidé, chtěl původně pozvat na večeři. Teď ji musí vytáhnout z basy, aby pomohl dokázat její nevinu. Někomu se to samozřejmě nelíbí a ten Někdo bude chtít Reachera pořádně klepnout přes prsty. Neváhá k tomu přitom využít i ty nejodpornější prostředky, jako je likvidace Reacherovy dcery, o jejíž existenci on sám dosud neměl tušení. Tady jde totiž o hodně. (CinemArt)', '2017-05-11 16:37:14.881', 2016, NULL);
