INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000125, '201709/I Dva Jsou Rodina-2.m4v', 'I Dva Jsou Rodina', 6, 0, 'cz', 'cz', 'fr', 'Demain tout commence', 'https://www.csfd.cz/film/426771-i-dva-jsou-rodina/', 'I Dva Jsou Rodina-2.jpg', 0, 'Vtipný a nonšalantní Samuel (Omar Sy) si užívá bezstarostný život na francouzské Riviéře. Jednoho dne ho však navštíví víkendový flirt Kristin (Clémence Poésy) a oznámí mu, že dítě, které drží v náručí, je jeho. Předá mu holčičku a uteče. Samuel vyráží s malou Glorií do Londýna, kde Kristin marně hledá. Nakonec mu nezbývá nic jiného než si najít práci a ve velkoměstě se usadit. Samuel se okouzlujícím způsobem pouští do výchovy malé Glorie. Během následujících osmi let se ukáže, že je nejen milující, ale i hodně netradiční táta. S humorem se vypořádává s nástrahami každodenního života osamělého Francouze v Londýně. Rodinná idylka se však záhy změní, když se zničehonic před jejich dveřmi objeví Kristin.... (Bohemia MP)', '2017-09-26 18:59:08.906', 2016, NULL);
