INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000130, '201709/Ucitelka-5.m4v', 'Ucitelka', 15, 0, 'sk', 'en', 'sk', 'Ucitelka', 'https://www.csfd.cz/film/372832-ucitelka/', 'Ucitelka-5.jpg', 0, 'Zdánlivě empatická a laskavě vyhlížející učitelka manipuluje prostřednictvím svých žáků jejich rodiči za účelem osobního obohacení, ať už v podobě materiálních výhod, či dokonce vidiny milostného poměru. Nejen z obavy o prospěch svých milovaných dětí většina rodičů třídní učitelce základní školy podléhá a poskytuje jí nejrůznější služby, dárky a jiné pozornosti. Najdou se však tři rodiny, které se rozhodnou učitelce vzepřít a stávající situaci se společně s ředitelkou školy pokusí zvrátit na konspirativní třídní schůzce. Děj strhujícího a velmi aktuálního snímku o síle lidského charakteru inspirovaného skutečnou událostí se sice odehrává na začátku 80. let, ale jde o příběh univerzální, který by se mohl odehrát kdekoliv a v jakékoli době, neboť zkorumpovanost, stejně jako malost a vypočítavost vládnou světem bez ustání. (A-Company CZ)', '2017-09-26 18:59:09.242', 2016, NULL);
