INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000128, '201709/Fast And Furious 8-3.m4v', 'Rychlo a zbesilo 8', 15, 37, 'cz', 'cz', 'en', 'Fast And Furious 8', 'https://www.csfd.cz/film/414990-rychle-a-zbesile-8/', 'Fast And Furious 8-3.jpg', 8, 'Dominic Toretto (Vin Diesel) zradil svou rodinu. V jedné z nejúspěšnějších filmových sérií všech dob je sice možné cokoliv, ale tohle je víc než automobilové seskoky padákem, průlety aut mezi mrakodrapy, nebo tažení obřího trezoru ulicemi Ria. Rychle a zběsile 8 rozhodně nepojede na neutrál. Na stranu zla přetáhla Doma legendární kyber-teroristka přezdívaná Cipher (Charlize Theron), která je stejně krásná jako nebezpečná. Není tedy divu, že Toretto naprosto ztratil hlavu a vykašlal se nejen na Letty (Michelle Rodriguez), ale i na všechny staré kámoše. Že s ním má Cipher jiné plány, než jsou romantické večeře při svíčkách, je nabíledni. Kombinace její inteligence a jeho svalů a řidičských schopností z nich mohou udělat neporazitelný tandem. Aby svět zůstal v rovnováze, převezme otěže v osiřelé rodině Hobbs (Dwayne Johnson), který do ní proti své vůli přibere nového člena – Deckarda Shawa (Jason Statham), vysokooktanový záporák ze sedmého dílu, který měl původně shnít ve vězení. Společně s nimi se budeme přesouvat z divokého pobřeží exotické Kuby, přes ucpané ulice New Yorku až na pusté ledovce v Barentsově moři, abychom si užili další zábavnou a nezapomenutelnou jízdu. (CinemArt)', '2017-09-26 18:59:09.13', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000132, '201711/DespicableMe-3-29.m4v', 'Ja, zloduch 3', 3, 33640, 'sk', 'sk', 'en', 'Despicable Me 3', 'https://www.csfd.cz/film/364790-ja-padouch-3/', 'DespicableMe-3-29.jpg', 3, 'Bývalý padouch Gru má problém. Mezi prsty si nechal utéct aktuálně nejhledanějšího zlosyna na světě, a tak ho vyhodili z práce v Antipadoušské lize. Jeho Mimoni jsou navíc čím dál nespokojenější s tím, že už páníček není zlý. Nejmladší dcera Agnes touží po živém jednorožci, který bude neuvěřitelně heboučký. A do toho všeho se Gru dozví, že má dvojče, které je mu k nerozeznání podobné. Až na ty vlasy. Vraťme se ale k tomu lumpovi, který přechytračil Grua. Jmenuje se Balthazar Bratt a v osmdesátých letech minulého století byl dětskou hvězdou populární televizní show. Ke své smůle se nedokázal přenést přes její konec a od té doby škodí, kudy chodí. Nosí retro oblečení a nemožný účes, poslouchá staré písničky na kazetách a jako zbraně používá Rubikovu kostku a žvýkačkové bubliny. Prostě odporný typ. O mnoho větší sympaťák je další nováček v příběhu, Gruův bratr Dru. Je to neuvěřitelně přátelský a milý chlapík, který si ze všeho nejvíc přeje, aby ho bratr zasvětil do umění zločinu. Jenže Gru začal být zatvrzelý dobrák (dokonce tak zatvrzelý, že s ním nehnula ani otevřená vzpoura Mimoňů, kteří ho kvůli jeho dobrému chování opustili). Jediný, kvůli komu by byl Gru ochotný přejít alespoň dočasně na stranu zla, je jeho věčný soupeř Balthazar Bratt. A když si mu tenhle zastydlý klon Michaela Jacksona dovolí sáhnout na rodinu, využije všechny padoušské zkušenosti k tomu, aby své blízké zachránil. V tom mu nadšeně sekunduje jeho nově nalezený brácha Dru a jeho Mimoni, kteří si až ve vězení uvědomili, co je pro ně v životě nejdůležitější. (CinemArt)', '2017-11-18 03:10:01.18', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000134, '201711/Cars3-1.m4v', 'Auta 3', 3, 0, 'sk', 'sk', 'en', 'Cars 3', 'https://www.csfd.cz/film/368710-auta-3/', 'Cars3-1.jpg', 3333, 'Legendární závoďák Blesk McQueen z ničeho nic zjistí, že ho nová generace závodních aut poslala mimo závodní dráhu, kterou nade vše miluje. Zpátky do hry mu může pomoci pouze mladá automechanička Cruz Ramirezová, která však sama pomýšlí na vítězství a inspirace od Doktora Hudsona Horneta. Slavný závoďák s číslem 95 musí ve Zlatém pístu všem dokázat, že nepatří do starého železa. (Falcon)', '2017-11-18 19:04:46.075', 2017, NULL);
