INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000136, '201712/THE_PEANUTS_MOVIE.m4v', 'Snoopy a Charlie Brown. Peanuts vo filme', 3, 0, 'sk', 'sk', 'en', 'The Peanuts Movie', 'https://www.csfd.cz/film/368347-snoopy-a-charlie-brown-peanuts-ve-filmu/', 'THE_PEANUTS_MOVIE.jpg', 0, 'Kdo z vás nezná Snoopyho, toho nejlepšího psího kamaráda na světě? A především jediného pejska, který dokáže ze svého, tak trochu hodně mimózního páníčka, Charlieho Browna, udělat největšího hrdinu. A že to není zrovna snadné. Charlie Brown je totiž pro změnu nejslavnější světový outsider, který šéfuje nejhoršímu baseballovému týmu, nedokáže promluvit na svou tajnou lásku Malou Zrzečku a hlavně je to kluk, kterému se i v těch zdánlivě nejméně nebezpečných situacích přihodí vždy něco naprosto tragického, nebo aspoň směšného. A nyní se Charlie, nejméně pravděpodobný hrdina, vydává na velice nebezpečnou výpravu, zatímco jeho psí kamarád Snoopy se utkává se svým největším nepřítelem, Rudým baronem. (CinemArt)', '2017-12-23 14:04:06.102', 2015, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000138, '201712/Horton Hears A Who-3.m4v', 'Horton', 3, 0, 'sk', 'sk', 'en', 'Horton', 'https://www.csfd.cz/film/224119-horton/', 'Horton Hears A Who-3.jpg', 0, 'Horton je normální slon v dobré fyzické i psychické kondici, a tak ho celkem překvapí, když mu pod nos spadne smítko prachu, které mluví. Celkem logicky se domnívá, že chyba, tedy vlastně hlasy, vězí v jeho hlavě, a že se prostě a jednoduše zbláznil. Jenže Horton se nezbláznil, a to smítko opravdu mluví. Je v něm totiž ukryté malé městečko Kdosice, ve kterém žijí maličkatí Kdovíci. Mezi nimi i Starosta - zasloužilý obyvatel, který má několik desítek roztomilých dcer, spokojený život a před sebou velkolepou oslavou stoletého výročí svého městečka. A je také jediný, kdo slyší Hortona. I Starosta si nejdřív myslí, že mu v hlavě přeskočilo, ale naštěstí mu včas dojde, že neblázní, a že Kdosice jsou ve vážném ohrožení. Dostaly se totiž ze svého starého a bezpečného úkrytu a jejich osud teď závisí na tom, jestli se Hortonovi podaří Kdovíkům pomoct s hledáním nového domova. Ale Horton je trochu pomatenec a ze svých nových kamarádů má tak obrovskou radost, že se o ní rychle podělí s ostatními zvířátky ze svého pralesa. Nikdo mu to samozřejmě nevěří. Dokonce spořádaná Klokanice to bere jako nepřípustné chování a zásah do dobrých mravů a Hortonovo "šílenství" ji naštve natolik, že se rozhodne jetelový květ, na kterém je smítko s Kdosicemi, rezolutně zničit. A Hortonovo dobrodružství začíná. Horton je skvělý film pro každého - dokazuje to nejen kouzelně barevná a propracovaná animace postaviček a celého okolního světa, ale i humor pro všechny věkové kategorie. Horton je totiž nejen tak trochu pomatenec, ale hlavně velký naiva a ještě větší nešika - co byste taky po slonovi chtěli? A tak se občas stává, že osud Kdosic visí doslova na vlásku. To třeba když Horton zdolává provazové mosty a jiné nástrahy, návštěva zubaře v Kdosicích může být pěkně nebezpečná věc. Sloní otřesy totiž často znamenají, že člověk (vlastně Kdoví) přijde o víc, než by rád. (oficiální text distributora)', '2018-01-05 03:10:01.023', 2008, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000143, '201712/Valerian-2.m4v', 'Valerian', 4, 0, 'sk', 'sk', 'en', 'Valerian', 'https://www.csfd.cz/film/53000-valerian-a-mesto-tisice-planet/', 'Valerian-2.jpg', 0, 'Valerián (Dane DeHaan) a Laureline (Cara Delevigne) jsou speciální vládní agenti pro správu lidských území a jejich úkolem je udržovat pořádek v celém vesmíru. Valerián má se svou kolegyní více, než jen profesionální záměry, neustále ji bombarduje milostnými návrhy. Ale pověst, která jeho vztah k ženám provází a konzervativní postoje Laureline vedou k tomu, že ho neustále odmítá. Na rozkaz velitele (Clive Owen) se Valerián a Laureline vydávají na společnou misi do ohromného mezigalaktického města Alfa. Alfa je neustále rostoucí metropole, kterou obývají tisíce tvorů ze všech koutů galaxie. Žije zde 17 milionů obyvatel, kteří spojili své dovednosti, technologie a zdroje pro společné dobro. Jak už to ale bývá, ne každý má v Alfě stejné bohulibé cíle. Zdá se, že jakési tajemné síly se chystají náš rod ohrozit! (Bioscop)', '2018-01-05 03:10:01.292', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000137, '201712/Magicke Stribro-7.m4v', 'Magicke striebro 1', 3, 0, 'sk', 'sk', 'en', 'Julenatt i Blafjell', 'https://www.csfd.cz/film/279184-magicke-stribro/', 'Magicke Stribro-7.jpg', 0, 'Princezna Modrá růže je skřítkem žijícím hluboko v norských horách. Malé společenství skřítků má na starosti přechod mezi denním světlem a nocí. Ten se odehrává v tzv. magické hodině a je vyvolán magickým stříbrem, které vlastní jen tito skřítci. (Zlín Film Festival)', '2018-01-05 03:10:00.879', 2009, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000115, '201708/Barbie In Princess Power-38.m4v', 'Barbie: Odvazna princezna', 3, 0, 'cz', '  ', 'en', 'Barbie In Princess Power', 'http://www.imdb.com/title/tt4215332/?ref_=fn_al_tt_1', 'Barbie In Princess Power-38.jpg', 0, 'Directed by Ezekiel Norton.  With Kelly Sheridan, Britt Irvin, Michael Kopsa, Rebecca Husain. One day, after being kissed by a magical butterfly, Kara soon discovers she has amazing super powers allowing her to transform into Super Sparkle, her secret, crime-fighting alter ego who flies around the kingdom ready to save the day! But it''s not long before her jealous cousin catches the butterfly and also transforms - this time into Dark Sparkle, her nemesis.', '2017-08-01 19:58:14.519', 2015, NULL);
