INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000127, '201709/Alien-Covenant-1.m4v', 'Alien: Covenant', 4, 4, 'cz', 'cz', 'en', 'Alien Covenant', 'https://www.csfd.cz/film/322881-vetrelec-covenant/', 'Alien-Covenant-1.jpg', 10, 'Snímkem Vetřelec: Covenant se režisér Ridley Scott vrací do vesmíru, který sám stvořil ve Vetřelci, prvním filmu věnovaném nejstrašnějšímu mimozemskému monstru filmové historie. V nejnovější a ještě děsivější kapitole se připravuje posádka vesmírné lodi Covenant kolonizovat vzdálenou planetu na druhé straně galaxie. Nový svět, který nečekaně objeví, se jim z počátku zdá být dosud nezmapovaným idylickým rájem. Ve skutečnosti je to ale temný a brutální svět, jehož hrůzy si nikdo z členů posádky nedokázal představit ani v těch nejhorších představách. Když se tito vesmírní poutníci ocitnou tváří v tvář smrtícímu monstru, nezbývá jim nic jiného než se aspoň pokusit o zoufalý únik z tohoto zdánlivého ráje proměněného ve skutečné peklo. (CinemArt)', '2017-09-26 18:59:09.108', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000192, '201712/s01e05 Contrapasso.m4v', 'Contrapasso', 4, 47000177, 'cz', 'cz', 'en', 'Contrapasso', 'https://www.csfd.cz/film/395723-westworld/456198-contrapasso/', 's01e05 Contrapasso.jpg', 105, 'Dolores, William a Logan dorazí do Pariahu, města postaveného na dekadenci a hříchu – a jsou pověřeni nebezpečným posláním. Muž v černém se ve snaze najít cestu bludištěm setkává s nečekaným spojencem. (HBO Europe)', '2018-01-06 03:10:01.04', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000197, '201712/s01e03 The Stray.m4v', 'The Stray', 4, 47000177, 'cz', 'cz', 'en', 'The Stray', 'https://www.csfd.cz/film/395723-westworld/456196-zbehlik/', 's01e03 The Stray.jpg', 103, 'Elsie a Stubbs míří ve snaze najít chybějící hostitele do hor. Teddymu je přidělen nový příběh o jeho původu, díky němuž se musí vydat po stopách nového viníka a nechat Dolores ve městě Sweetwater samotnou. Bernard vyšetřuje příčiny šílenství a halucinací u hostitelů. William objeví atrakci, kterou by rád vyzkoušel, a zatáhne k tomu i Logana. (HBO Europe)', '2018-01-06 03:10:01.14', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000184, '201712/s01e08 Trace Decay.m4v', 'Trace Decay', 4, 47000177, 'cz', 'cz', 'en', 'Trace Decay', 'https://www.csfd.cz/film/395723-westworld/456201-zapomenout/', 's01e08 Trace Decay.jpg', 108, 'Ford nařídí zdráhajícímu se Bernardovi, aby vykonával svou práci jako obvykle. Maeve si chce změnit svůj scénář. Pro Dolores je stále těžší oddělit sny od reality. Teddyho sužují temné vzpomínky, zatímco Muž v černém vzpomíná na utrpení, které ho přivedlo do tohoto kritického okamžiku. (HBO Europe)', '2018-01-06 03:10:00.912', 2016, NULL);
