INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000139, '201712/Rio-1.m4v', 'Rio', 3, 0, 'sk', 'sk', 'en', 'Rio', 'https://www.csfd.cz/film/268267-rio/', 'Rio-1.jpg', 0, 'Film Rio je příběhem vzácného papouška jménem Blu, který žije na malém městě se svoji paničkou Lindou, s níž tvoří nerozlučnou dvojku. Linda ho před léty zachránila před pašeráky, kteří ho ještě jako papouščí miminko ulovili uprostřed brazilského pralesa a odvezli do zasněžené Minessoty. Blu se kvůli tomu sice nikdy nenaučil létat, ale v bezpečí a pohodlí Lindina domova se natolik zabydlel, že pro něj není problém uvařit kávu, připravit snídani, vyčistit si zuby nebo posloužit Lindě jako její osobní budík. Když jednoho dne Linda a Blu zjistí, že Blu není posledním papouškem svého druhu na světě, jak si až doposud mysleli, neváhají a vydají se na cestu do exotické Brazílie za papoušicí Perlou, která žije v samotném srdci Rio de Janeira. Krátce po jejich setkání jsou ale Blu a Perla uneseni skupinou obchodníků se zvířaty. Za pomoci chytré a životem na ulici zocelené Perly a partičky upovídaných městských ptáků se jim sice podaří uprchnout, ale vyhráno rozhodně nemají. Dokáže Blu s pomocí nových přátel najít odvahu, aby se naučil létat, zmařil plány únoscům, kteří jsou jim stále v patách, a vrátil se zpět ke své milované paniččce Lindě? (oficiální text distributora)', '2018-01-05 03:10:01.076', 2011, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000140, '201712/Pirates Deadmentellnotales-1.m4v', 'Pirati Karibiku: Salazarova pomsta', 15, 26, 'sk', 'sk', 'en', 'Pirates of the Caribbean: Dead Men Tell No Tales', 'https://www.csfd.cz/film/290818-pirati-z-karibiku-salazarova-pomsta/', 'Pirates Deadmentellnotales-1.jpg', 9, 'V dalším pokračování svých fantastických dobrodružství se kapitán Jack, ke kterému se štěstěna opět jednou nemilosrdně obrátila zády, ocitá tváří v tvář bandě smrtících pirátských duchů pod vedením strašlivého kapitána Salazara (Javier Bardem), kterým se podařilo uniknout z Ďáblova trojúhelníku a jejichž jedinou touhou je připravit o život každého piráta - a zejména Jacka. Jackovou jedinou nadějí na přežití je bájný Poseidonův trojzubec, ale pokud má mít šanci ho nalézt, musí přesvědčit ke spolupráci Carinu Smythovou (Kaya Scodelario), geniální a krásnou astronomku, a Henryho (Brenton Thwaites), tvrdohlavého mladého námořníka a člena královského námořnictva. Jedině s jejich pomocí má kapitán Jack, svírající v rukách pevně kormidelní kolo Umírajícího racka, své zoufale nicotné a zchátralé lodi, naději nejen zvrátit nepřízeň osudu, se kterou se poslední dobou potýkal, ale také zachránit si život ve střetu s nejmocnějším a nejděsivějším protivníkem, jakému byl kdy nucen čelit. (Falcon)', '2018-01-05 03:10:01.121', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000141, '201712/Ciara-1.m4v', 'Ciara', 15, 0, 'sk', '  ', 'sk', 'Ciara', 'https://www.csfd.cz/film/516331-cara/', 'Ciara-1.jpg', 0, 'Adam Krajňák je otcem rodiny a zároveň také hlavou pohraniční kriminální skupiny, která pašuje přes slovensko-ukrajinskou hranici cigarety. V tomto okruhu nejbližších zastává nenahraditelnou roli starajícího se důvěrníka, ale také zásadového a nekompromisního vůdce, jemuž v rozhodujících okamžicích nechybí sebevědomí a temperament. Klidný souběh obou prolínajících se světů se však se vzrůstající nervozitou, obklopující nejistou budoucnost s nově budovanou hranicí schengenského prostoru, začíná pomalu otřásat. A když pak jedna z pašeráckých dodávek ztroskotá, spustí se nezastavitelná lavina konsekvencí, během níž bude muset Krajňák zpochybnit své vlastní mezníky, které dosud nechtěl překročit. (MFF Karlovy Vary)', '2018-01-05 03:10:01.158', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000142, '201712/Magicke Stribro-9.m4v', 'Magicke striebro 2 - Hladanie kuzelneho rohu', 3, 0, 'sk', 'sk', 'no', 'Blajfell 2 - Jakten pa det magiske horn', 'https://www.csfd.cz/film/317955-magicke-stribro-hledani-kouzelneho-rohu/', 'Magicke Stribro-9.jpg', 0, 'Po nekonečně dlouhém podzimním spánku se modří skřítci probudili do té nejchladnější zimy, jakou kdy zažili. Malé údolí s hospodářstvím, kde žijí červení skřítci, je ohroženo obrovským ledovcem. Lidé už údolí opustili a červení skřítci budou muset brzy udělat totéž. Dokáže královna Fjellrose pomoci Drengovi, králi červených skřítků? Královna Fjellrose věří dávné pověsti o magickém modrém rohu, který dokáže změnit počasí. Před dávnými lety byl roh ukryt v útrobách Šedé hory. Když se ho královně podaří najít, bude moci změnit počasí a nechat ledovec roztát. Ale modří skřítkové nesdílejí její odhodlání a s plánem naopak nesouhlasí. Když byl totiž roh použit naposled, způsobil katastrofu. Navzdory protestům se ale Fjellrose a Drenge vydají na výpravu za legendárním rohem. (Film Europe)', '2018-01-05 03:10:01.272', 2011, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000178, '201712/s01e01 The Original.m4v', 'The Original', 4, 47000177, 'cz', 'cz', 'en', 'The Original', 'https://www.csfd.cz/film/395723-westworld/451743-prvni-vytvor/', 's01e01 The Original.jpg', 101, 'Ve Westworldu – obrovském zábavním parku, kde hosté neváhají vynaložit horentní sumy za to, že prožijí s tamními robotickými hostiteli dobrodružství ve stylu Divokého západu – probíhá další den plný fantazie. Přední programátor Bernard Lowe však upozorní zakladatele projektu dr. Roberta Forda na případy nezvyklého chování, které zaznamenal u několika nedávno přeprogramovaných hostitelů. Ve westworldském městečku Sweetwater se mezitím farmářova dcera Dolores setkává na ulici s pistolníkem jménem Teddy. Jejich načrtnutý příběh je však narušen příchodem nelítostného Muže v černém a posléze také improvizovaným setkáním hostitele s artefaktem z vnějšího světa. (HBO Europe)', '2018-01-06 03:10:00.75', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000189, '201712/s01e02 Chestnut.m4v', 'Chestnut', 4, 47000177, 'cz', 'cz', 'en', 'Chestnut', 'https://www.csfd.cz/film/395723-westworld/456195-pod-slupkou/', 's01e02 Chestnut.jpg', 102, 'Dvojice návštěvníků – William, který se chystá do parku poprvé, a stálý klient Logan – přijíždějí do Westworldu s odlišnými očekáváními a plány. Bernard a Theresa Cullenová, která odpovídá za zajištění kvality Westworldu, debatují o tom, zda jsou nedávné anomálie hostitelů nakažlivé. Inženýr Elsie Hughes, který dohlíží na chování hostitelů, mezitím vyladí emoce Maeve, bordelmamá ze sweetwaterského nevěstince, aby se vyhnul odvolání. Arogantní programátor Lee Sizemore předloží svůj poslední scénář týmu, ale doktor Ford má jiné představy. Muž v černém povolává odsouzeného muže Lawrence, aby mu pomohl odhalit nejhlubší tajemství Westworldu. (HBO Europe)', '2018-01-06 03:10:00.994', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000202, '201712/s01e04 Dissonance Theory.m4v', 'Dissonance Theory', 4, 47000177, 'cz', 'cz', 'en', 'Dissonance Theory', 'https://www.csfd.cz/film/395723-westworld/456197-disonancni-teorie/', 's01e04 Dissonance Theory.jpg', 104, 'Dolores se připojí k Williamovi a Loganovi při hledání pokladu v nehostinné krajině. Muž v černém najde s pomocí Lawrence klíčovou stopu v bludišti slepých uliček. Dr. Ford a Theresa debatují o budoucnosti parku. Maeve sužují opakující se vize. (HBO Europe)', '2018-01-06 03:10:01.204', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000205, '201712/s01e06 The Adversary.m4v', 'The Adversary', 4, 47000177, 'cz', 'cz', 'en', 'The Adversary', '', 's01e06 The Adversary.jpg', 106, 'Maeve charms Felix; Elsie discovers evidence that could point to sabotage; Teddy and the Man in Black conflict with a garrison', '2018-01-06 03:10:01.288', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000190, '201712/s01e07 Trompe L Oeil.m4v', 'Trompe L Oeil', 4, 47000177, 'cz', 'cz', 'en', 'Trompe L Oeil', 'https://www.csfd.cz/film/395723-westworld/456200-opticky-klam/', 's01e07 Trompe L Oeil.jpg', 107, 'S Theresinou pomocí se Charlotte snaží odhalit závažné nedostatky Fordových výtvorů. William, Dolores a Lawrence projíždějí zrádným územím. Maeve dá Lutzovi a Sylvesterovi ultimátum. Bernard, který je zatlačen ke zdi, zvažuje další kroky. (HBO Europe)', '2018-01-06 03:10:01.007', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000188, '201712/s01e09 The Well-Tempered Clavier.m4v', 'The Well-Tempered Clavier', 4, 47000177, 'cz', 'cz', 'en', 'The Well-Tempered Clavier', 'https://www.csfd.cz/film/395723-westworld/456202-dobre-temperovany-klavir/', 's01e09 The Well-Tempered Clavier.jpg', 109, 'Maeve se obrátí na dlouholetého sweetwaterského psance Hectora se smělou nabídkou. William se snaží přesvědčit Logana, aby mu pomohl osvobodit Dolores. Teddy a Muž v černém se dostávají blíž k tomu, co hledají. Stubbsovo podezření roste. Ford varuje Bernarda, aby nehledal odpovědi na otázky, které je lépe nechat nevyřčené. (HBO Europe)', '2018-01-06 03:10:00.969', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000201, '201712/s01e10 The Bicameral Mind.m4v', 'The Bicameral Mind', 4, 47000177, 'cz', 'cz', 'en', 'The Bicameral Mind', 'https://www.csfd.cz/film/395723-westworld/456203-bikameralni-mysl/', 's01e10 The Bicameral Mind.jpg', 110, 'Ford představí svou vizi nového odvážného příběhu. Dolores se díky Bernardovi a Muži v černém konečně vyrovná s tím, kým skutečně je. Maeve dává do pohybu svůj plán na osvobození, zatímco Westworld čelí velkému převratu. (HBO Europe)', '2018-01-06 03:10:01.191', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000187, '201712/s07e02 Stormborn.m4v', 'Stormborn', 4, 1000, 'cz', 'cz', 'en', 'Stormborn', 'http://www.imdb.com/title/tt7247190/?ref_=fn_al_tt_1', 's07e02 Stormborn.jpg', 702, 'Daenerys sends the Dornishmen with Yara''s fleet to Sunspear and the Unsullied to Casterly Rock, deciding to place King''s Landing under siege. She questions Varys'' loyalty and threatens to burn him alive if he ever betrays her. Melisandre arrives and encourages her to invite Jon Snow to Dragonstone. Grey Worm and Missandei consummate their relationship. Cersei gathers several lords, asking for their fealties and elevating Randyll Tarly as Warden of the South. Qyburn shows Cersei a prototype ballista capable of harming dragons. Arya meets with Hot Pie and learns of Jon''s ascension to King in the North, halting plans to travel to King''s Landing and instead setting course for Winterfell. After receiving Samwell''s letter, Jon leaves for Dragonstone in hopes of convincing Daenerys to support the fight against the White Walkers. He leaves Sansa in charge and aggressively warns Littlefinger to keep his distance. Samwell applies a forbidden treatment on Jorah''s greyscale infection. Euron''s fleet attacks Yara''s. Obara and Nymeria are killed, while Ellaria, Tyene, and Yara are captured. Theon shows flashes of his time as Reek, hesitating to challenge Euron before fleeing the carnage by jumping into the sea.', '2018-01-06 03:10:00.949', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000198, '201712/s07e01 Dragonstone.m4v', 'Dragonstone', 4, 1000, 'cz', 'cz', 'en', 'Dragonstone', 'http://www.imdb.com/title/tt5654088/?ref_=fn_al_tt_1', 's07e01 Dragonstone.jpg', 701, 'At the Twins, Arya fatally poisons the remaining lords of House Frey. The White Walkers march toward the Wall, where Tollett allows Bran and Meera inside. At Winterfell, despite Sansa''s disapproval, Jon secures the loyalties of Houses Umber and Karstark, who fought alongside Ramsay in the Battle of the Bastards. At the Citadel, Samwell steals books that reveal a large reservoir of dragonglass in Dragonstone, and sends word to Jon. He later finds Jorah in a cell. In the Riverlands, Arya meets a group of friendly Lannister soldiers, who take her intention to kill Cersei as a joke. Thoros shows Sandor a vision in the fire. The revelation leads him to believe in the Lord of Light. In King''s Landing, Jaime tells Cersei of the crucial need for allies. She welcomes Euron, who proposes marriage to her in exchange for his Iron Fleet and a chance to kill Theon and Yara. Cersei declines citing trust as a concern, so Euron promises to return with a "gift" to prove his loyalty. Daenerys arrives at Dragonstone, the home of House Targaryen once occupied by Stannis, with her army and dragons.', '2018-01-06 03:10:01.153', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000181, '201712/s07e04 The Spoils of War.m4v', 'The Spoils of War', 4, 1000, 'cz', 'cz', 'en', 'The Spoils of War', '', 's07e04 The Spoils of War.jpg', 704, 'Arya returns to Winterfell, where she reunites with Sansa and spars with Brienne, both of whom are unnerved by her exceptional fighting skills. Bran unemotionally bids farewell to Meera, divulging that he is no longer the boy she accompanied through the North. Littlefinger presents Bran with the dagger that was previously used in his attempted assassination. Cersei assures the Iron Bank a full return on their investment, as a train carrying gold from Highgarden is on its way to King''s Landing. In a cave filled with dragonglass, Jon reveals ancient paintings to Daenerys depicting the First Men and the Children of the Forest joining forces against the undead. Later, Daenerys learns of the sacking of Highgarden by Lannister forces and realizes her attack on Casterly Rock was a distraction. Despite Tyrion''s protests, she decides to take action. Led by Daenerys riding Drogon, the Dothraki cavalry catches the Lannister army by surprise and decimate or capture its remaining forces. Drogon is wounded during the fight by a spear fired from the scorpion ballista being manned by Bronn. Jaime mounts a desperate charge on horseback at a vulnerable Daenerys, but Drogon spews fire in time to thwart the attack. Bronn tackles Jaime into the river in time to save him.', '2018-01-06 03:10:00.864', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000220, '201804/The Dark Tower-1.m4v', 'The Dark Tower - Temna veza', 4, 0, 'en', 'cz', 'en', 'The Dark Tower', 'https://www.csfd.cz/film/263261-temna-vez/', 'The Dark Tower-1.jpg', 0, 'Roland Deschain ( Idris Elba), poslední z řádu rytířů, vede odvěkou bitvu s Walterem O''Dimem, známým též jako Muž v černém ( Matthew McConaughey) a je odhodlaný zabránit mu ve zničení Temné věže, která svou existencí drží pohromadě celý vesmír. Dobro a zlo se tak ocitají v souboji, který rozhodne o osudu všech světů, protože Roland je jediným, kdo je schopen Temnou věž před Mužem v černém ochránit. (Falcon)', '2018-04-06 18:07:20.376', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000221, '201804/Kingsman The Golden Circle-1.m4v', 'Kingsman 2 The Golden Circle - Zlaty kruh', 15, 0, 'cz', 'cz', 'en', 'Kingsman The Golden Circle', 'https://www.csfd.cz/film/52975-kingsman-zlaty-kruh/', 'Kingsman The Golden Circle-1.jpg', 0, 'Svět Kingsmanů se poprvé představil ve filmu Kingsman: Tajná služba jako nezávislá, mezinárodní, špionážní agentura pracující v hlibokém utajení a jejímž cílem je bezpečný svět. V dalším pokračování Kingsman: Zlatý kruh čelí naši hrdinové novým výzvám. Centrála Kingsmanů je zničena a celý svět se stává rukojmím. Kingsmany čekají nové úkoly i překážky. Spojí se se spřátelenou americkou špionážní organizací Statesman. Obě dvě agentury musí společnými silami porazit nebezpečného nepřítele a zachránit tak svět. (CinemArt)', '2018-04-06 18:07:20.41', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000219, '201804/The Star-1.m4v', 'Stastna hviezda', 3, 0, 'cz', 'cz', 'en', 'The Star', 'http://www.imdb.com/title/tt4587656/?ref_=fn_al_tt_1', 'The Star-1.jpg', 0, 'Directed by Timothy Reckart.  With Steven Yeun, Keegan-Michael Key, Aidy Bryant, Gina Rodriguez. A small but brave donkey and his animal friends become the unsung heroes of the first Christmas.', '2018-04-06 18:07:20.356', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000196, '201712/s07e05 Eastwatch.m4v', 'Eastwatch', 4, 1000, 'cz', 'cz', 'en', 'Eastwatch', '', 's07e05 Eastwatch.jpg', 705, 'Jaime and Bronn return to King''s Landing. Against Tyrion''s advice, Daenerys has Drogon burn Randyll and Dickon Tarly alive for remaining allegiant to Cersei, forcing the other captives to pledge fealty to the former. Jorah arrives at Dragonstone and reunites with Daenerys. Maester Wolkan alerts Jon and the Citadel about the approaching wights to the Eastwatch by the Sea. Jon decides to travel beyond the Wall and capture a wight in order to convince Cersei for a temporary alliance. Davos smuggles Tyrion inside King''s Landing, where he secretly meets with Jaime and offers an armistice, which Cersei accepts, informing Jaime that she is pregnant. Davos rendezvouses with Gendry and returns him to Dragonstone. With the Citadel ignoring Wolkan''s letter, Samwell steals several books and leaves with Gilly and Little Sam. At Winterfell, Littlefinger notices Arya spying on him and leads her to take a letter written by Sansa from his room. Jon, Jorah, and Gendry, joined by the Hound, the Brotherhood, and a group of the Free Folk led by Tormund, leave Eastwatch and pass the Wall, intending to capture a wight as evidence for Cersei that the Army of the Dead is real.', '2018-01-06 03:10:01.12', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000183, '201712/s07e06 Beyond The Wall.m4v', 'Beyond The Wall', 4, 1000, 'cz', 'cz', 'en', 'Beyond The Wall', '', 's07e06 Beyond The Wall.jpg', 706, 'At Winterfell, Littlefinger plots to isolate Sansa. Tensions between Arya and Sansa grow following Arya''s discovery of the letter Sansa sent as a child begging for Robb''s fealty to Joffrey. Sansa discovers Arya''s bag of faces she took from Braavos. At Dragonstone, Tyrion counsels Daenerys about her upcoming negotiations with Cersei. Beyond the Wall, Jon leads a raiding party to capture a wight to bring South as evidence of the White Walkers'' existence. After successfully capturing one, the group is besieged by the White Walker army. Jon has Gendry return to Eastwatch and send a raven to Daenerys to request her help. Thoros freezes to death overnight. Daenerys arrives and saves the group from the wights, with the help of her dragons. The Night King, leader of the White Walkers, throws an ice spear at Viserion, one of Daenerys'' dragons, and kills it. Daenerys flies off, along with the raiding party, but is unable to save Jon. Benjen Stark intervenes and sacrifices himself to allow Jon to escape. When Jon and Daenerys are reunited, Jon pledges himself and the North to Daenerys as Queen. The Night King resurrects Viserion, making the dragon a part of his army.', '2018-01-06 03:10:00.89', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000180, '201712/s07e07 The Dragon and The Wolf.m4v', 'The Dragon and The Wolf', 4, 1000, 'cz', 'cz', 'en', 'The Dragon and The Wolf', '', 's07e07 The Dragon and The Wolf.jpg', 707, 'At King''s Landing, the wight is presented to the Lannisters. Cersei demands Jon''s neutrality in the Great War, but he upholds his oath to Daenerys, provoking Cersei to leave. Tyrion meets privately with Cersei, apparently persuading her to ally. Cersei later reveals to Jaime that she was lying and instead intends to use the Golden Company of Braavos to secure her hold on Westeros. Disgusted, Jaime deserts her and rides north. Bound for White Harbor, Jon and Daenerys make love. At Dragonstone, Theon earns the respect of his men and leads them to rescue Yara. At Winterfell, Littlefinger sows dissent using Arya''s threatening demeanor with Sansa, who assembles a trial. After listing Littlefinger''s acts of treason against House Stark and the North, Sansa has Arya execute him. Samwell arrives at Winterfell and meets with Bran, where both discuss Jon''s parentage. They realise Jon is a trueborn Targaryen named Aegon and valid heir to the Iron Throne, with his parents—Rhaegar Targaryen and Lyanna Stark—having married in secret. At Eastwatch, an undead Viserion damages the Wall, allowing the Army of the Dead to march south.', '2018-01-06 03:10:00.827', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999973, '201611/s06e07 The Broken Man.m4v', 'The Broken Man', 4, 1000, 'cz', 'cz', 'en', 'The Broken Man', 'http://www.csfd.cz/film/263138-hra-o-truny/425387-zlomeny-muz/', 's06e07 The Broken Man.jpg', 607, 'Margaery convinces Olenna to return to Highgarden after the High Sparrow says that he will pursue Olenna following her attempt to engage the Faith. Jon, Sansa, and Davos recruit the Wildlings and House Mormont to their cause, but remain outnumbered by the Boltons. In desperation, Sansa writes a letter begging for aid. Jaime arrives in Riverrun with Bronn, assuming control of the siege. The former goes to parley with Brynden, ultimately being unsuccessful. Theon and Yara spend their last night in Volantis, deciding to sail to Meereen to ally with Daenerys. In Braavos, Arya prepares to return to Westeros, but is attacked by the Waif. Arya is badly wounded but escapes. Sandor is revealed to be alive, having been saved by a Septon and his followers. When men from the Brotherhood threaten and eventually slaughter the group, Sandor decides to get revenge.', '2016-12-03 19:29:47.729', 2016, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000147, '201605/S05E01 The Wars to Come.m4v', 'The Wars to Come', 4, 1000, 'cz', 'cz', 'en', 'The Wars to Come', 'http://www.csfd.cz/film/263138-hra-o-truny/417533-v-ocekavani-valek/', 'S05E01 The Wars to Come.jpg', 501, 'In a flashback, a witch tells a teenage Cersei that someone younger and more beautiful than herself will one day steal her accomplishments. In the present, Tywin is buried and Lancel returns, now a devoutly religious man and a member of the "Sparrows". In Pentos, a despondent Tyrion agrees to accompany Varys to Meereen to support Targaryen''s claim on the Iron Throne. In Meereen, the insurgent "Sons of the Harpy" murder an Unsullied, forcing Targaryen to launch more restrictive measures without respecting the old traditions. Missandei gets suspicious when she realizes that the Unsullied visit brothels. Targaryen''s locked-away dragons attack her when she checks on them. In the Vale, Baelish puts Arryn in the care of House Royce and leaves with Sansa. At the Wall, Stannis seeks to enlist the Wildlings in his war against Roose. Snow is unable to convince Rayder to accept Stannis''s authority. The latter has Mance burned alive, though Snow shoots him with an arrow to kill him quickly.', '2016-05-07 06:37:53.114', 2015, '');
