INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000149, '201605/S05E07 The Gift.m4v', 'The Gift', 4, 1000, 'cz', 'cz', 'en', 'The Gift', '', 'S05E07 The Gift.jpg', 507, 'Snow leaves for Hardhome with Giantsbane and some rangers. Aemon dies shortly after. Gilly is attacked by two Watchmen. Tarly intervenes and is overpowered until Ghost arrives and forces the duo to leave. Tarly and Gilly develop a romantic relationship. Sansa asks Theon to help her escape Winterfell, but he instead informs Ramsay, who flays the woman working for Brienne. At Stannis, army,s camp, Melisandre suggests sacrificing Shireen, which he refuses. Mormont and Tyrion are sold to slaver Qaggaz. The two are brought to a local fighting pit that Targaryen and her consort happen to be visiting in preparation for the coming games at Daznak,s pit. Mormont defeats the other slaves and reveals his identity to her, and that he brought Tyrion as a "gift" for her. In Dorne, Myrcella tells Jaime that she wants to marry Trystane, Doran,s son. Olenna,s demand for Loras and Margaery,s release is declined by the High Sparrow. Later, the High Sparrow arrests Cersei due to her former incestuous relationship with Lancel.', '2016-05-07 06:37:53.235', 2015, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000185, '201712/s07e03 The Queen Justice.m4v', 'The Queen Justice', 4, 1000, 'cz', 'cz', 'en', 'The Queen Justice', '', 's07e03 The Queen Justice.jpg', 703, 'Jon arrives at Dragonstone. Daenerys demands his fealty. He refuses and focuses instead on convincing her to fight the Army of the Dead. Following Tyrion,s advice, Daenerys grants Jon access to the island,s dragonglass. Melisandre hides her presence from Jon and leaves for Volantis. Bran arrives at Winterfell and reveals his newfound role as the Three-Eyed Raven to Sansa. In King,s Landing, Euron returns with Ellaria and Tyene as a gift for Cersei, who promises to marry him after the war is won. She also awards him co-control of her military alongside Jaime. Cersei administers to Tyene the same poison used to kill Myrcella, forcing Ellaria to watch her daughter,s impending death. In Oldtown, a healed Jorah leaves to find Daenerys. Ebrose forgives Samwell. Grey Worm and the Unsullied attack Casterly Rock, only to find it mostly abandoned. Jaime led most of the Lannister forces in an attack on Highgarden, while Euron,s fleet ambushes and destroys the Unsullied,s ships. The Lannister forces overwhelm Olenna,s army. Jaime induces Olenna to drink poison, offering her a quick and painless death. After drinking it, she confesses to poisoning Joffrey.', '2018-01-06 03:10:00.922', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000206, '201801/Princezna-2.m4v', 'Princezna nevesta', 3, 0, 'cz', 'cz', 'en', 'Princess Bride', 'https://www.csfd.cz/film/7625-princezna-nevesta/', 'Princezna-2.jpg', 0, 'Hrdinové bez bázně a hany, chrabří piráti, všemocní kouzelníci, démoničtí zlouni a princezna čekající na záchranu – prostě pohádka, jak má být, jen tentokrát trochu lépe a s nadhledem... Žila, byla překrásná dívka jménem Pomněnka, která vyrůstala na statku. Tam také pracoval mladý čeledín Westley a jak už to tak bývá, postupně oba zjistili, že je k sobě něco pojí. Přísahali si věčnou lásku, ale aby mohli být svoji, odešel Westley do světa vydělat si peníze. Jenže loď, na které se plavil, přepadl Strašný pirát Roberts, jenž jak známo nebere zajatce. Truchlící Pomněnka padla do oka rozmarnému princi Humperdinckovi a ten oznámil, že si ji vezme za ženu. Ale než dojde ke svatbě, odehraje se sousta věcí, počínaje únosem princezny a konče znovuzrozením Westleyho. A to nemluvě o nezměrných dobrodružstvích, která naši hrdinové cestou za láskou a svobodou budou muset podstoupit, a setkáních s roztodivnými postavami jako jsou veršující obr Fezik, strůjce zázraků Divotvorný Max a především mistrovský šermíř Inigo Montoya. (Česká televize)', '2018-01-30 20:16:15.338', 1987, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000207, '201801/Ztraceny Poklad-2.m4v', 'Strateny poklad templarskych rytierov', 3, 0, 'cz', '  ', '  ', 'Tempelriddernes skat', 'http://www.imdb.com/title/tt0488059/?ref_=fn_al_tt_1', 'Ztraceny Poklad-2.jpg', 0, 'Directed by Kasper Barfoed.  With Julie Grundtvig Wester, Christian Heldbo Wienberg, Nicklas Svale Andersen, Frederikke Thomassen. Danish brilliant pre-teen student Nis uses his summer holiday to learn Latin so he can properly study the Knights Templar inheritance on Bjornholm island from sources. His friends Mathias, a dumb jock, and Kathrine, who only comes to spend time wear her dad, who really wanted a boy, prefer childish fun, but get dragged in his quest for a treasure. Mats''s strength, Kathrine''s connections and even ...', '2018-01-30 20:16:15.671', 2006, NULL);
