INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000209, '201801/Kletba Bratri Grimmu-1.m4v', 'Klietba bratov Grimmovcov', 3, 0, 'cz', 'cz', 'en', 'The Brothers Grimm', 'https://www.csfd.cz/film/116495-kletba-bratri-grimmu/', 'Kletba Bratri Grimmu-1.jpg', 0, 'Dobrodružný příběh o dvou legendárních bratrech německého původu Jakobovi a Wilhelmovi Grimmových se začal natáčet v Praze v červnu 2003. Režie se ujal známý režisér Terry Gilliam (Monty Pythonův létající cirkus, Strach a hnus v Las Vegas) a scénář zpracoval Ehren Kruger (Kruh). Smyšleným dějem provází sběratelé bájí a pověstí, nyní nazvaní Jake a Will, kteří cestují od vesnice k vesnici, sbírají tajemné kletby a předpokládají, že tak dokáží ochránit lidi z města před kouzelnými příšerami a zaklínadly. Budou však potřebovat notnou dávku odvahy, když se osobně střetnou se skutečnými čarodějnými stvůrami a zaříkávadly. (oficiální text distributora)', '2018-01-30 20:16:15.754', 2005, NULL);
