INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (42000092, '201601/Spievankovo_5.m4v', 'Spievankovo 5', 3, 12, 'sk', '  ', 'sk', 'Spievankovo 5', 'http://www.csfd.cz/film/98806-spievankovo-5-o-povolaniach/', 'Spievankovo_5.jpg', 50, 'V Spievankove sa niečo chystá. Zahrajko má zajtra narodeniny a Spievanka mu chce spraviť prekvapenie. Na pomoc si prizve deti z nášho sveta. Spolu sa rozhodnú, že mu pripravia oslavu a vyrobia darčeky. Všetko ale tajne, aby Zahrajko nič nezistil. A tak začína dobrodružstvo plné zábavných aj dramatických situácií s úplne novými pesničkami. Deti spoznávajú rôzne povolania cez rozprávkový svet Spievankova a učia sa čo robiť, keď sa niekto zraní, keď horí, alebo ako sa pečie špeciálna buchtová narodeninová torta. Charaktery postáv takto ožívajú vďaka talentu profesionálnych hercov slovenského divadla a filmu. (oficiální text distributora)', '2016-01-19 20:36:57.891', 2015, '');
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000211, '201802/V Dobre Spolecnosti-1.m4v', 'V Dobre Spolecnosti', 6, 0, 'cz', 'cz', 'en', 'In Good Company', 'https://www.csfd.cz/film/168382-v-dobre-spolecnosti/', 'V Dobre Spolecnosti-1.jpg', 0, 'Ve studiích UNIVERSAL PICTURES vznikla komedie, pod kterou se podepsala jedna z nejúspěšnějších dvojic Hollywoodu bratři Weitzové (Prci, prci, prcičky, Jak na věc). V DOBRÉ SPOLEČNOSTI tentokrát svedli na filmové plátno proti sobě dvě generace. 49-letý marketingový ředitel jednoho vydavatelství je po mnoha letech ve vrcholné funkci degradován. Jak už to bývá při moderních restrukturalizacích. Jeho nadřízeným se stane teprve 26 letý čerstvý, ambiciózní, všeznalý expert na moderní vedení. Počáteční vzájemná apatie se časem mění v neobvyklé přátelství, které je ale vystaveno zkoušce to když se mladý šéf zamiluje do dcery svého staromódního podřízeného. Divák se může těšit na jedinečnou vybroušenou situační i konverzační komiku, doplněnou o skvělé herecké výkony Denise Quaida, coby otce stále populárnější hvězdičky Scarlett Johansson. (oficiální text distributora)', '2018-02-03 14:41:22.868', 2004, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000212, '201802/Christmaswedding-2.m4v', 'Svatba na spadnuti', 6, 0, 'cz', 'cz', 'en', 'A Christmas Wedding', 'https://www.csfd.cz/film/228078-svatba-na-spadnuti/', 'Christmaswedding-2.jpg', 0, 'Emily a Ben sa stretli na Vianoce, teraz po dvoch rokoch chcú práve na Vianoce spojiť svoje životy. Emily ale musí chodiť do práce, tak celú realizáciu svadby prenechá Benovi. Prvé problémy nastávajú už pri obstarávaní šiat pre družičky... Emilyn sen o dokonalom dni sa pomaly rozplýva do nenávratna. Keď sa k tomu pridá ešte snehová búrka, idú všetky ideály na bok. (oficiální text distributora)', '2018-02-03 14:41:23.029', 2006, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000214, '201802/Smrt Modelky-2.m4v', 'Smrt Modelky', 6, 0, 'cz', 'cz', 'en', 'Anna Nicole', 'https://www.csfd.cz/film/248619-smrt-modelky/', 'Smrt Modelky-2.jpg', 0, 'Hlavní postava ve filmu popisuje svůj život a začátky modelingu, po kterém toužila. Po neúspěšných konkurzech, kde zjistila co to obnáší, se rozhodla pracovat v baru jako tanečnice. Asi po roce si ji pozval do salonku významný host Hawart, který ji obdivoval a moc mu připomínala Marilyn Monroe. Při té příležitosti ji pozval na večeři, kde se dobře bavili, kdy Anna Nicole souhlasila s jeho přáním, že se do práce už nevrátí. Po nějakém čase ji požádá aby se k němu i se synem nastěhovala a spolu tak vytvořili rodinu. Fotila i pro časopis a některé fotky poslala do Playboye, kde ji nakonec vybrali a všichni jeli do Kalifornie, kde nafotila spoustu fotek a rozjela svou kariéru modelky. Po nějakém čase ji Hawert požádal o ruku... (ČSFD.cz)', '2018-02-03 14:41:23.093', 2007, NULL);
