INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000223, '201804/It-1.m4v', 'To', 2, 0, 'cz', 'cz', 'en', 'It', 'https://www.csfd.cz/film/9509-to/', 'It-1.jpg', 0, 'Když ve městě Derry ve státě Maine začnou mizet děti, musí skupina mladých chlapců a děvčat čelit svým největším obavám v boji proti zlému klaunovi jménem Pennywise, jehož vražedná a násilnická historie se táhne napříč staletími. (Vertical Ent.)', '2018-04-06 18:07:20.475', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000217, '201804/Iron Sky-9.m4v', 'Iron Sky', 4, 0, 'en', 'en', 'en', 'Iron Sky', 'https://www.csfd.cz/film/247090-iron-sky/', 'Iron Sky-9.jpg', 0, 'V posledních chvílích druhé světové války se tajný nacistický program vyhnul destrukci a ukryl se na odvrácené straně Měsíce. Během 70 let přísného utajení Nacisté zkonstruovali gigantickou vesmírnou pevnost s masivní armádou létajících talířů. Když americký astronaut James Washington přistane se svým lunárním modulem příliš blízko tajné nacistické základně, měsíční Führer se rozhodne, že zářivý moment ovládnutí Země nastal… i když o něco dříve, než bylo plánováno. Washington prohlašuje, že jeho mise je jen propagační kampaní Prezidentky Spojených států, ale co jiného by člověk mohl být, když ne zvědem pro bezprostřední útok pozemskými silami? Čtvrtá říše musí reagovat! (oficiální text distributora)', '2018-04-06 18:07:20.086', 2012, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000218, '201804/Bladerunner_2049-1.m4v', 'Bladerunner 2049', 4, 23, 'cz', 'cz', 'en', 'Bladerunner 2049', 'http://www.imdb.com/title/tt1856101/?ref_=fn_al_tt_1', 'Bladerunner_2049-1.jpg', 2049, 'Directed by Denis Villeneuve.  With Harrison Ford, Ryan Gosling, Ana de Armas, Dave Bautista. A young blade runner''s discovery of a long-buried secret leads him to track down former blade runner Rick Deckard, who''s been missing for thirty years.', '2018-04-06 18:07:20.336', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000224, '201804/Priserakovi-1.m4v', 'Priserakovci', 3, 0, 'sk', 'sk', 'en', 'Monster Family', 'http://www.imdb.com/title/tt6572702/?ref_=fn_al_tt_1', 'Priserakovi-1.jpg', 0, 'Directed by Holger Tappe.  With Emily Watson, Jason Isaacs, Nick Frost, Jessica Brown Findlay. Despite the title, the Wishbone family are far from happy. In an attempt to reconnect as a family, Mum and Emma plan a fun night out. However, her plan backfires when an evil witch curses them, and they''re all turned into Monsters.', '2018-04-06 18:07:20.487', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000225, '05/Ferdinand-1.m4v', 'Ferdinand', 3, 0, 'cz', 'cz', 'en', 'Ferdinand', 'https://www.csfd.cz/film/378900-ferdinand/', 'Ferdinand-1.jpg', 0, 'Nina a Ferdinand jsou největší kamarádi pod španělským sluncem. To by nebylo až tak zvláštní, kdyby Nina nebyla malá holčička a Ferdinand velký býk. A to pořádně velký býk. Narodil se sice jako malé, rozpustilé telátko, ale časem vyrostl o pořádný kus do výšky, do délky i do šířky. Neznalí lidé by jej mohli považovat za nebezpečné, zuřivé monstrum, jenže Ferdinand je naštěstí ten nejroztomilejší a nejhodnější sudokopytník na světě, který nade vše miluje kytičky a nikdy by neublížil ani mouše. Bohužel zároveň je taky nešikovným popletou, který i díky svým rozměrům občas dokáže způsobit pořádnou katastrofu. Jednoho dne si Ferdinand neopatrně sedne na čmeláka, dostane od něj žihadlo a splaší se. Celou událost vidí hledači býků na zápasy v koridě a rozdivočelý Ferdinand je svým výkonem uchvátí natolik, že jej okamžitě chytí a odvezou. Ferdinand ale nechce s nikým zápasit a především je odhodlaný vrátit se ke své Nině. Naštěstí se spřátelí s třemi šikovnými ježky, Unem, Dosem a Kvatrem (prosím, neptejte se, kde je Tres). A když se k nim připojí jedna koza a pár dalších býků, společně se vydají na dlouhou cestu Španělskem. Sice mají v patách pár pronásledovatelů, ale to je určitě nemůže zastavit. Snad jen trochu zpomalit. Ostatně, jak říká sám Ferdinand:  „Každý se chová podle toho, jak vypadá, ala tak jednoduché to není. Zvláště v mém případě. Se mnou je to složitější.“ (CinemArt)', '2018-05-03 18:06:29.713', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000226, '05/Full Metal Jacket-1.m4v', 'Olovena vesta', 15, 0, 'en', 'cz', 'en', 'Full Metal Jacket', 'https://www.csfd.cz/film/5401-olovena-vesta/', 'Full Metal Jacket-1.jpg', 0, 'Snímek Stanleyho Kubricka Olověná vesta (1987) patří k vlně filmů, které se v druhé polovině osmdesátých let vyjadřovaly k tématu vietnamské války (např. Četa a Narozen 4. července Olivera Stonea). Mistrovský snímek se odehrává ve dvou stejně dlouhých kapitolách, jejichž hlavním hrdinou je inteligentní voják s přezdívkou Vtipálek. První část se odehrává ve výctikovém středisku námořní pěchoty v Jižní Karolíně, kde sadistický instruktor Hartman psychicky i fyzicky týrá svěřené nováčky. Ne každý je schopen takový dril přežít a zachovat si zdravé myšlení. Druhá část se již odehrává na vietnamské frontě, kde se i Vtipálek musí uchýlit k tomu nejhoršímu činu. Stanley Kubrick se ve svých dílech vždy zajímal o krajní stavy lidské psychiky a není tomu jinak ani v Olověné vestě. Film lze vnímat jako kritiku či odmítnutí válečné mašinerie. Autor však využil válečného tématu spíše k obecnější výpovědi o tom, do jakého stavu se může i humanisticky založený člověk dostat, ocitne-li se v nelidských podmínkách a beznadějných situacích. (oficiální text distributora)', '2018-05-03 18:06:30.003', 1987, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000227, '05/500daysofsummer-1.m4v', '500 dni so Summer', 6, 0, 'cz', 'cz', 'en', '500 days of Summer', 'https://www.csfd.cz/film/253070-500-dni-se-summer/', '500daysofsummer-1.jpg', 0, 'Neotřelá romantická komedie o ženě, která nevěří v pravou lásku a muži, který se do ní bezhlavě zamiluje. Nezvyklá romantická komedie o lásce a osudu přináší příběh mladého reklamního textaře (Joseph Gordon-Levitt), který až dosud zoufale a bezúspěšně hledal dívku svých snů... A teď to vypadá, že by jeho nová kolegyně Summer Finnová (Zooey Deschanelová) mohla být tou pravou. Ale 500 dní jejich nekonvenčního vztahu dokazuje, že cesta ke štěstí může být nepředvídatelná a nekontrolovatelná - jeden z nich je totiž nenapravitelný snílek a druhý moderní cynik. Originálně pojatá love story vás rozhodně chytí za srdce. (oficiální text distributora)', '2018-05-03 18:06:30.024', 2009, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000228, '201805/Amadeus-1.m4v', 'Amadeus', 6, 0, 'cz', 'cz', 'en', 'Amadeus', 'https://www.csfd.cz/film/2971-amadeus/', 'Amadeus-1.jpg', 0, 'Píše se rok 1781 a Antonio Salieri (Abraham) působí jako dvorní skladatel císaře Josefa II (Jeffrey Jones). Když ke dvoru přijede Mozart (Tom Hulce, nominovaný na Oscara), Salieri si s hrůzou uvědomí, že božské hudební nadání, které si přál mít on sám, dostal do vínku tento obscénní a rozpustilý vtipálek. Salieri dohnán žárlivostí k zuřivosti organizuje intriky s cílem zničit Mozarta, k čemuž je ochoten použít všechny dostupné prostředky. Možná i vraždu. Podařilo se Salierimu umlčet jednoho z největších géniů světové hudby? Záhada - a hudba - zůstávají. AMADEUS - velkolepý historický epos, nádherná oslava hudby Wolfganga Amadea Mozarta. Na Berlinale 2002 Miloš Forman představil AMADEA v nezkrácené verzi. (oficiální text distributora)', '2018-05-11 18:31:17.508', 1984, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000229, '201805/Hori Ma Panenko-1.m4v', 'Hori Ma Panenko', 9, 0, 'cz', '  ', 'cz', 'Hori Ma Panenko', 'https://www.csfd.cz/film/2975-hori-ma-panenko/', 'Hori Ma Panenko-1.jpg', 0, 'Autorem námětu a scénáře je známá a osvědčená trojka Miloš Forman, Jaroslav Papoušek a Ivan Passer. Modelový průzkum českého maloměšťáctví ve vypouklém zrcadle zvýrazňujícím hospodské vztahy, prázdné hlavy, dlouhé prsty a plytké svědomí, se odehrává na pozadí hasičského bálu. V karikování a pranýřování české národní povahy jsou režisér Forman a kameraman Miroslav Ondříček doslova nelítostní. Komedie se brzy mění v tragickou frašku s citem pro detail, gesto a dialog. V zobrazení požárnického plesu se spojují groteskní gagy s nekompromisním odhalením lidské přízemnosti a tuposti. Mravní stav společnosti se tu zrcadlí především v postavách starých lidí, obětí organizačních zmatků, živelných katastrof a hlouposti svých bližních, kteří svoji nekulturnost a nesvobodu považují za normální. První Formanův barevný film byl opět obsazen neherci známými z předcházejících filmů (za všechny jmenujme Jana Vostrčila v roli předsedy plesového výboru a Miladu Ježkovou jako ženu hlídající tombolu). Upocenou atmosféru vesnické tancovačky podtrhují dechovkové coververze soudobých beatových hitů, např. Hvězdy na vrbě či From Me To You od Beatles. Hovorová řeč představuje katalog komunikačních defektů, zvlášť když se postavy snaží být oficiální. Pamflet o společnosti, ve které přestávají fungovat kamufláže, ale není si schopná přiznat pravý stav věcí, se tehdy stal synonymem společensky podvratného filmového cynismu - proti filmu například zcela oficiálně protestovali čeští hasiči. Italský koproducent snímku Carlo Ponti se dokonce díla zřekl a žádal zpět vložený kapitál. Finanční trn z režisérovy paty nakonec vytrhl francouzský producent Claude Berri, který uhradil chybějící částku. Ponti ale udělal chybu, neboť film byl nakonec nominován na Oscara v kategorii cizojazyčný film. Text k písni "Hoří!", jejíž refrém dal jméno filmu, napsal Fanda Mrázek, ale zde je skladba uvedena pouze v orchestrální podobě. Mimopražská premiéra se uskutečnila ve Vrchlabí v říjnu 1967. (oficiální text distributora)', '2018-05-11 18:31:17.765', 1967, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000230, '201805/Spalovac Mrtvol-2.m4v', 'Spalovac Mrtvol', 15, 0, 'cz', '  ', 'cz', 'Spalovac Mrtvol', 'https://www.csfd.cz/film/4244-spalovac-mrtvol/', 'Spalovac Mrtvol-2.jpg', 0, 'Konec 30. let: Pan Karel Kopfrkingl je obětavý zaměstnanec pražského krematoria a vzorný otec rodiny, který důsledně dbá o řádný chod domácnosti a všestrannou výchovu svých dětí. Rád předčítá ze své oblíbené knihy o Tibetu. Tíživá atmosféra napjaté doby násobí všeobecný strach. V měšťáckém stereotypu žijící Kopfrkingl spolupracuje s nacistickými okupanty a s obludnou samozřejmostí se zbavuje všeho nebezpečného ve svém okolí, včetně členů vlastní rodiny. Jeho velká a nebezpečná kariéra začíná... Filmová adaptace psychologického hororu, vzdáleně upomínajícího na Hitchcockovy grotesky, kongeniálně vystihuje morbidní atmosféru a jakoby neskutečný svět panoptikálních postav vynikající předlohy Ladislava Fukse (vyšla v roce 1967). Pan Kopfrkingl se stal životní rolí Rudolfa Hrušínského a tragikomedie Spalovač mrtvol jedním z nejvýznamnějších filmů Juraje Herze, jenž tu plně uplatnil svůj smysl pro bizarnost, perverzi a fantaskní vidění. I když byl snímek normalizátory českého filmu příkladně zatracován a nemohl se po dlouhá léta objevit ani v kinech, ani v televizi, sotva lze tento titul pominout ve výčtu nejhodnotnějších českých filmů. Na scénáři spolupracoval s Jurajem Herzem autor původní novely Ladislav Fuks, kameramanem byl Stanislav Milota, vedlejší role ztělesnili Vlasta Chramostová, Jana Stehnová, Ilja Prachař, Jiří Lír, Míla Myslíková, Vladimír Menšík. V roce 1969 získal Herz za režii a Milota za kameru tohoto filmu cenu FITESU Trilobit. Na Přehlídce českých a slovenských filmů v Sorrentu obdržel snímek Stříbrnou Sirénu. (oficiální text distributora)', '2018-05-11 18:31:17.791', 1968, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000231, '201806/PADDINGTON2.m4v', 'Paddington 2', 3, 0, 'sk', 'sk', 'en', 'Paddington 2', 'https://www.csfd.cz/film/455414-paddington-2/', 'PADDINGTON2.jpg', 0, 'Náš kamarád Paddington se už šťastně usadil s rodinou Brownových a je oblíbeným členem místní komunity. Aby mohl koupit ten nejlepší dárek ke 100. narozeninám jeho tety Lucy, začne pracovat. Cesta za dobrodružstvím začne, když je tento dárek ukraden... (Vertical Ent.)', '2018-06-30 07:19:34.398', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000232, '201806/COCO.m4v', 'COCO', 3, 0, 'sk', 'sk', 'en', 'COCO', 'https://www.csfd.cz/film/66830-coco/', 'COCO.jpg', 0, 'Navzdory prazvláštnímu rodinnému zákazu muziky se Miguel touží stát uznávaným hudebníkem jako je jeho idol, Ernesto de la Cruz. V touze dokázat svůj talent se Miguel souhrou neuvěřitelných okolností ocitne v překrásné a barevné Říši mrtvých. Tam potká okouzlujícího šejdíře Hectora a společně se vypraví na strhující cestu odhalit skutečný příběh Miguelovy rodiny. (Falcon)', '2018-06-30 07:19:34.659', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000233, '201808/BLACK_PANTHER.m4v', 'Black Panther', 4, 0, 'cz', 'cz', 'en', 'Black Panther', 'https://www.csfd.cz/film/331877-black-panther/', 'BLACK_PANTHER.jpg', 0, 'Film od studia Marvel  Black Panther je dobrodružstvím válečníka T’Chally, který se po smrti svého otce, krále Wakandy, vrací domů do odloučené, avšak technologicky vyspělé africké země, aby se stal novým právoplatným králem. Když se však znovu objeví mocný dávný nepřítel, jsou T’Challovy schopnosti jako krále a Black Panthera podrobeny těžké zkoušce, neboť T’Challa je vtažen do konfliktu ohrožujícího nejen Wakandu, ale i celý svět. Tváří tvář zradě a ohrožení musí mladý král zmobilizovat své spojence a využít naplno sílu Black Pantera, aby porazil své nepřátele a zajistil svému národu a jeho kultuře bezpečnou budoucnost. (Falcon)', '2018-08-14 03:10:01.642', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000234, '201808/Leto S Kovbojem-1.m4v', 'Leto s kovbojem', 6, 0, 'cz', '  ', 'cz', 'Leto s kovbojem', 'https://www.csfd.cz/film/26964-leto-s-kovbojem/', 'Leto S Kovbojem-1.jpg', 0, 'Hlavními postavami je mladá psycholožka Doubravka a vesnický mladík Honza Macháček. On je traktorista, který při ztřeštěné sázce dočasně přišel o řidičský průkaz, takže teď pase družstevní stádo krav, ona je na chatě se svým chlapcem Bobou, který nepříliš úspěšně studuje medicínu a právě se připravuje na opravné zkoušky. Honzova bezprostřednost, optimismus a přístup k životu i práci ostře kontrastuje se sobeckým, slabošským a nevyrovnaným Bobou a Doubravka si stále víc uvědomuje, že její cit k Bobovi zmizel a doopravdy má vlastně ráda Honzu. Otázkou je, jak se na takovou partii bude dívat její poněkud konzervativní rodina... (oficiální text distributora)', '2018-08-14 17:36:46.056', 1976, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000235, '201808/Lawless-1.m4v', 'Zeme bez zakona', 15, 0, 'cz', 'cz', 'en', 'Lawless', 'https://www.csfd.cz/film/271458-zeme-bez-zakona/', 'Lawless-1.jpg', 0, 'Bratři Bondurantovi jsou tvrdí hoši. Nejstarší z nich Howard (Jason Clarke) přežil 1. světovou válku, ale to co v ní prožil, v něm zanechalo hluboké následky. Prostřední Forrest (Tom Hardy) málem zemřel, když se spolu s rodiči nakazil španělskou chřipkou. Na rozdíl od nich nemoc přežil a tento zážitek ho zocelil. Nejmladší Jack (Shia LaBeouf) se od bratrů trochu odlišuje, svou vnímavostí a citlivostí jako by se do drsného prostředí ani nehodil. Zatímco většina obyvatel městečka živoří, podnikavým bratrům se daří, vybudovali si totiž prosperující byznys s načerno pálenou whisky. Nekalému podnikání ve Franklin County ale začínají krušné časy, když do města přijede zkorumpovaný zástupce šerifa Charlie Rakes (Guy Pearce). Zatímco většina obyvatel se podvoluje jeho teroru, bratři Bondurantové se nehodlají nikomu podřizovat. Ačkoliv proti Rakesovi bojují jako rodina, jejich vztahy se začínají měnit. Jack dospívá, sní o drahých autech, luxusních oblecích a krásných ženách a také jeho ambice v prodeji načerno pálené whisky se čím dál víc rozšiřují. A tak se svým přítelem Cricketem (Dane DeHaan) rozjíždí proti vůli Forresta vlastní obchod s whisky a kvůli tomu se zapletou s největším gangsterem ve městečku, Floydem Bannerem (Gary Oldman). Do životů bratří Bondurantových vstupují i dvě krásné ženy. Tajemná a živelná Maggie (Jessica Chastain) padne do oka uzavřenému Forrestovi a tichá křesťanka Berta (Mia Wasikowska) zaplane pro Jacka. Situace ve městečku se začíná vyostřovat a bratři se rozhodnou hájit, co si tvrdě vybudovali. Postaví se přesile, která si říká Zákon, aby definitivně rozhodli, kdo má nad městečkem kontrolu. (oficiální text distributora)', '2018-08-14 17:36:46.224', 2012, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000236, '201808/Three Billboards Outside Ebbing-1.m4v', 'Tri billboardy za Ebbingom', 15, 0, 'cz', 'cz', 'en', 'Three Billboards Outside Ebbing', 'https://www.csfd.cz/film/439559-tri-billboardy-kousek-za-ebbingem/', 'Three Billboards Outside Ebbing-1.jpg', 0, 'Mildred Haynesové ( Frances McDormand) zavraždili dceru. A protože se vyšetřování už několik měsíců bezvýsledně vleče, rozhodne se jednat. U vjezdu do města vylepí na tři billboardy velmi kontroverzní vzkazy adresované váženému šéfovi místní policie ( Woody Harrelson). Tímto nezvyklým činem začíná bitva mezi Mildred a zástupci zákona. Když se do věci vloží ještě další policista ( Sam Rockwell), nevyzrálý maminčin chlapeček se zálibou v násilí, souboj mezi místním policejním oddělením a osamělou, ukřivděnou a hlavně rozzuřenou bojovnicí se může rozjet na nejvyšší obrátky. (CinemArt)', '2018-08-14 17:36:46.244', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000237, '201808/Vlak-2.m4v', 'The Train', 15, 0, 'cz', 'cz', 'en', 'Vlak', 'https://www.csfd.cz/film/7205-vlak/', 'Vlak-2.jpg', 0, 'Děj se odehrává koncem druhé světové války v Paříži. Německý plukovník von Waldheim dobře ví, že pařížská muzea jsou plna vzácných děl. Nechá mnohé z nich naložit do vlaku a hodlá je odeslat do Německa. Brání mu v tom přednosta stanice, který vymění návěští podél trati za fingovaná a nechá vlak jezdit dokolečka. (oficiální text distributora)', '2018-08-14 17:36:46.264', 1964, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000238, '201808/Next-1.m4v', 'Next', 4, 0, 'cz', 'cz', 'en', 'Next', 'https://www.csfd.cz/film/215209-next/', 'Next-1.jpg', 0, 'NEXT je adaptací povídky Golden Man od jednoho z největších velikánů světové sci-fi literatury P.K. Dicka. Cris Johnston (N. Cage) je obdařen schopností vidět do budoucnosti a také určovat správný tok budoucího času. Novodobý mesiáš, který musí spasit lidstvo a především svou rodinu. Někdy to ale osud zařídí tak, že si musíte vybrat jedno, nebo druhé... Možná ale existuje ještě nějaká DALŠÍ... možnost. Každému, kdo si přečte jména tvůrců, musí být jasné, že nás letos na jaře čeká dokonalá akční podívaná se sílou bomby hromadného ničení. (oficiální text distributora)', '2018-08-14 17:36:46.285', 2007, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000239, '201808/Zabiti Posvatneho Jelena-9.m4v', 'Zabiti posvatneho jelena', 15, 0, 'cz', 'cz', 'en', 'The Killing of a Sacred Deer', 'https://www.csfd.cz/film/452767-zabiti-posvatneho-jelena/', 'Zabiti Posvatneho Jelena-9.jpg', 0, 'Špičkový kardiochirurg žije spořádaný život se svou spořádanou ženou a dvěma dětmi. Je v něm jediná podezřelá věc: šestnáctiletý Martin, s nímž se vídá a kterého pomalu, ale jistě zatahuje do své rodiny. Aniž by to věděl, přivolává na sebe tragédii antických rozměrů. I když se Martin zdá být obyčejný teenager, ve skutečnosti v něm dříme schopnost, pro kterou neexistuje racionální vysvětlení. (Film Europe)', '2018-08-14 17:36:46.313', 2017, NULL);
