INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (45999962, '201611/ZOOTROPOLIS.m4v', 'Zootopia - Zootropolis', 3, 0, 'cz', 'cz', 'en', 'Zootropolis', 'http://www.csfd.cz/film/407365-zootropolis-mesto-zvirat/', 'ZOOTROPOLIS.jpg', 9202, 'Moderní metropole zvířat Zootropolis je město jako žádné jiné. Jen v Zootropolisu najdete luxusní čtvrti jako je Sousedství Sahara nebo chladný Tundrov. Je to velkolepé velkoměsto, ve kterém žijí pospolu zvířata ze všech koutů světa. Je to metropole, kde můžete být kýmkoli bez ohledu na to, zda jste obří slon nebo malá myška. Když však do města dorazí optimistická strážnice Judy Hopkavá, zjistí, že být prvním králíkem v policejním sboru velkých a drsných zvířat není žádná legrace. A protože chce všem dokázat, že na to má, skočí po první příležitosti řešit náročný a záhadný případ, i když to znamená vzít do party upovídaného a všemi mastmi mazaného lišáka Nicka Wilda. (Falcon)', '2016-12-03 19:29:47.319', 2016, NULL);
