INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000492, '201812/Frozen Adventure Frozen Fever-1.m4v', 'Ledove kralovstvi: Vanoce s Olafem', 3, 0, 'sk', 'sk', 'en', 'Frozen Adventure Frozen Fever', 'https://www.csfd.cz/film/553552-ledove-kralovstvi-vanoce-s-olafem/', 'Frozen Adventure Frozen Fever-1.jpg', 0, 'V krátkém filmu Ledové království: Vánoce s Olafem od Walt Disney Animation Studios prožívají sněhulák Olaf a sob Sven veselé vánoční dobrodružství. Princezna Anna a královna Elsa připraví v království Arendelle vůbec první oslavu Vánoc. Obyvatelé městečka však z oslavy rychle odejdou, aby si užili vlastních rodinných vánočních tradic. Královským sestrám dojde, že samy žádné rodinné tradice nemají. A tak se Olaf se Svenem vypraví na cestu skrz naskrz Ledovým královstvím, aby pro Annu a Elsu našli ty nejlepší a nejkrásnější vánoční tradice. (Falcon)', '2018-12-12 16:57:32.626', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000490, '201812/Kung Fu Panda-16.m4v', 'Kung Fu Panda', 3, 0, 'sk', 'sk', 'en', 'Kung Fu Panda', 'https://www.csfd.cz/film/226216-kung-fu-panda/', 'Kung Fu Panda-16.jpg', 0, 'V líhni animovaných talentů studia DreamWorks se zrodil nový hrdina, který dokáže skvěle konkurovat jeho dosavadnímu tahounovi Shrekovi. Zatímco zelený zlobr se konečně usadil, na scénu nastupuje mladý, neklidný, roztomilý a totálně nešikovný pandí chlapík jménem Po. Ten musí v akcí a vtipem nabitém příběhu ujít neuvěřitelnou cestu, během níž se z uťápnutého prodavače nudlových polévek stane legendární Dračí bojovník. Ačkoliv se příběh odehrává ve středověké Číně, jeho kulisami by mohla být jakákoliv éra a oblast. Jeho hlavní náplní je totiž poselství, že i tomu největšímu zoufalci se může splnit jeho sen, když se ke snění přidá i trocha štěstí a především píle. Nicméně bez Číny bychom neměli hlavního hrdinu v podobě chlupaté pandy, která zbožňuje Kung Fu. Po je bojovým uměním posedlý natolik, že ignoruje vlastní nebetyčnou lenost a vydá se po pro něj nekonečných schodech do paláce v rodném Údolí klidu, aby na vlastní oči viděl volbu Dračího bojovníka – legendárního válečníka, který by zemi ochránil před pomstou démonického tygra Tai Lunga. Všeobecně se přepokládá, že se jím stane některý z pěti místních mistrů kung fu – tygřice, zmije, jeřába, kudlanky nebo opice – jenže prst guru Ugveje nakonec nečekaně ukáže na Poa. Zdejší učitel bojových umění, mistr Sifu, tak rázem čelí největší výzvě své kariéry. Musí z nemotorného, líného a nenažraného pandy stvořit legendu kung fu dřív, než se do Údolí klidu vrátí zlý Tai Lung a přeorá ho k obrazu svému. A protože Po i jeho mistr záhy pochopí, že v těch zdánlivě největších slabinách se mohou skrývat ty největší přednosti, nemusel by sen o Dračím bojovníkovi v černobíle chlupatém provedení být pohou utopií. Hlavním cílem režisérů Marka Osbornea a Johna Stevensona bylo natočit animovaný film, který by se nestal jen dalším z mnoha rodinných filmů se zvířátky. „Samozřejmě se lišíme už tím, že mísíme rodinnou komedii s poctivým kung fu filmem, který naprosto respektuje pravidla žánru. Nám šlo ale především o to, aby Kung Fu Panda byla nadčasová, aby nestárla. Aby se u ní stejně bavili dnešní diváci i ti, kteří film uvidí za pár let,“ říká Stevenson. Tvůrcům se podařilo slavné kung fu bojovníky obsadit ještě slavnějšími hlasy. V originále je mluví Jack Black, Dustin Hoffman, Angelina Jolie nebo Jackie Chan. První hereckou ligu se ale podařilo obsadit i v českém dabingu (Otakar Brousek starší, Saša Rašilov, Nela Boudová), takže zdejší diváci ochuzeni nebudou. (oficiální text distributora)', '2018-12-12 16:57:32.602', 2008, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000488, '201812/Hotel Transylvania 3-1.m4v', 'Hotel Transylvania 3', 3, 0, '  ', '  ', '  ', 'Hotel Transylvania 3', 'https://www.csfd.cz/film/422713-hotel-transylvanie-3-priserozni-dovolena/', 'Hotel Transylvania 3-1.jpg', 0, 'Ve filmu od Sony Animation Pictures  Hotel Transylvánie 3: Příšerózní dovolená se s naší oblíbenou rodinkou strašidel vydáme na výletní parník, kde si má Drákula zaslouženě oddechnout od práce v hotelu. Drákulův doprovod si užívá klidnou plavbu a s radostí využívá vše, co může luxusní plavidlo nabídnout, od strašidelného volejbalu po exotické exkurze a opalování při Měsíčku. Když však Mavis zjistí, že se Drákula zbláznil do záhadné kapitánky lodi Eriky, která skrývá strašlivé tajemství, jež by mohlo zničit strašidla po celém světě, stane se z dovolené snů noční můra. (Falcon)', '2018-12-12 16:57:32.576', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000485, '201812/Triky S Trpasliky-2.m4v', 'Triky S Trpasliky', 3, 0, 'sk', 'sk', 'en', 'Gnome Alone', 'https://www.csfd.cz/film/534174-triky-s-trpasliky/', 'Triky S Trpasliky-2.jpg', 0, 'Chloe a její maminka Catherine se už zase stěhují. Tentokrát do Nové Anglie, ve které si pořídily nádherný gotický dům, plný zvláštních zahradních trpaslíků. Chloe má ale jiné starosti. Začíná chodit do nové školy a snaží se spřátelit se svými spolužačkami. Navíc se do ní zakoukal kluk ze sousedství Liam, který se ji neustále snaží oslnit. Chloe si ale postupně začne všímat, že se v domě dějí divné věci. Všude jsou obaly od sladkostí, které někdo snědl. A co ti trpaslíci? Jako by snad na ni mrkli, nebo se dokonce pohnuli? Chloe brzy pochopí, že trpaslíci jsou skutečně živí, a navíc mají dům chránit před hrozbou z jiné dimenze. Tohle dobrodružství si rozhodně nesmí nechat ujít! (Fénix Distribution)', '2018-12-12 16:57:32.517', 2017, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000491, '201812/Solo-1.m4v', 'Han Solo', 4, 42000023, 'cz', 'cz', 'en', 'Han Solo', 'https://www.csfd.cz/film/377466-solo-star-wars-story/', 'Solo-1.jpg', 10, 'Naloďte se na Millennium Falcon a vydejte se do předaleké galaxie ve filmu Solo: Star Wars Story, zbrusu novém dobrodružství nejoblíbenějšího syčáka v galaxii. V řadě troufalých eskapád hluboko v temném a nebezpečném zločineckém podsvětí potkává Han Solo nejen mohutného Chewbaccu, svého budoucího kopilota, ale narazí i na nechvalně známého hazardního hráče Landa Calrissiana. Na této cestě se zrodí jeden z nejnepravděpodobnějších hrdinů ságy  Star Wars. (Falcon)', '2018-12-12 16:57:32.615', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000489, '201812/Zlomene Kvetiny-1.m4v', 'Zlomene kvetiny', 6, 0, 'en', 'cz', 'en', 'Broken Flowers', 'https://www.csfd.cz/film/205756-zlomene-kvetiny/', 'Zlomene Kvetiny-1.jpg', 0, 'Don Johnston (Bill Murray) je zapřisáhlý starý mládenec, jehož právě opustila jeho poslední láska Sherry. Nedá se tím vyrušit, odhodlán dál trávit čas své předčasné penze před televizí. Když ale dostane záhadný dopis, naznačující anonymně, že s některou z četných bývalých lásek má dospělého syna, je nucen přece jen se zamyslet nad minulostí. Poháněn spíš svým sousedem a kamarádem Winstonem, který si rád hraje na detektiva, než vlastní zvědavostí, vydá se nakonec Don pátrat, která z jeho někdejších milenek by mohla být autorkou dopisu a matkou jeho syna. Jediné stopy jsou růžová barva dopisu a psací stroj, na němž byl napsán. Postupně se setká se čtyřmi ženami, které něco znamenaly v jeho životě (Sharon Stoneová, Frances Conroyová, Jessica Langeová a Tilda Swintonová), ale žádná v něm neprobudí víc než trochu nostalgie. Nicméně ho všechna tato setkání konfrontují s jeho minulostí i přítomností. (oficiální text distributora)', '2018-12-12 16:57:32.59', 2005, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000487, '201812/Boj-2.m4v', 'Boj', 15, 0, 'cz', 'cz', 'en', 'Boj', 'https://www.csfd.cz/film/226313-boj/', 'Boj-2.jpg', 0, 'Agent FBI je posedlý pomstou vrahovi známému jako Rogue, který zabil jeho parťáka s rodinou. Když konečně stane Roguovi tváří v tvář, odhaluje se pravda o jejich minulosti. (oficiální text distributora)', '2018-12-12 16:57:32.558', 2007, NULL);
