INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000486, '201812/Deadpool 2-1.m4v', 'Deadpool 2', 4, 0, 'cz', 'cz', 'en', 'Deadpool 2', 'https://www.csfd.cz/film/426009-deadpool-2/', 'Deadpool 2-1.jpg', 0, 'Připravte se na druhý příchod Wade Wilsona alias Deadpoola ( Ryan Reynolds). Možná s ním prožijeme okamžiky, kdy jako kuchař přežívá skoro smrtící hovězí útok a hned poté prahne po splnění svého velkého snu: stát se nejvyhledávanějším barmanem v Mayberry. A zároveň se chce vyrovnat s tím, že zcela ztratil chuť a snaží se tedy znovuobjevit správné životní koření. Pátrá i po nějakém nestabilním kondenzátoru, bojuje s nindži i japonskou jakuzou a vzdoruje smečce sexuálně agresivních čoklů. Deadpool také vyráží do světa, aby objevil pro něj významné životní body: rodinu, přátelství, novou chuť prožívat dobrodružství a také, aby si zasloužil vytoužený hrneček s nápisem „Nejlepší milovník na světě“. Nebo taky ne. Deadpool je zkrátka chlapík mnoha tváří, byť by ta jeho pravá, ukrytá pod maskou, je pěkně hnusná. Ale za to nemůže. Což ovšem neplatí o jeho hodně černém smyslu pro humor a netradičním přístupu k superhrdinským pravidlům fair play. (CinemArt)', '2018-12-12 16:57:32.545', 2018, NULL);
