INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000504, '201901/Vynalez Zkazy-5.m4v', 'Vynalez Zkazy', 15, 0, 'cz', '  ', 'cz', 'Vynalez Zkazy', 'https://www.csfd.cz/film/10125-vynalez-zkazy/', 'Vynalez Zkazy-5.jpg', 0, 'Geniální vynálezce, profesor Roch, je na pokraji objevu látky, která může ve špatných rukou zničit svět. A právě takové ruce má hrabě Artigas, tajemný a temný dobrodruh, který ve své ponorce brázdí světová moře, potápí lodi a bohatství, kterého se tak zmocní, je mu nástrojem k ovládnutí světa. Artigas unese profesora Rocha ze sanatoria a nutí ho, aby v jeho sídle v kráteru sopky Back Cup vynález dokončil. Shodou okolností Artigas unese i profesorova asistenta, inženýra Harta, který je i vypravěčem příběhu, a který se všemožně pokouší profesora osvobodit a zamezit zkáze. Hart nakonec vyšle jícnem sopky balón s poselstvím k lidstvu, jež se díky tomu spojí a jehož válečné flotily v hodině dvanácté zkáze zamezí. (AČFK)', '2019-01-07 19:53:30.817', 1958, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000505, '201901/12yearsslave-10.mp4', '12 let v retezech', 15, 0, 'cz', 'cz', 'en', 'Twelve Years a Slave', 'https://www.csfd.cz/film/304544-12-let-v-retezech/', '12yearsslave-10.jpg', 0, 'Solomon Northup měl milující rodinu, dobrou práci, talent, majetek, svobodu i právo na život. Žil idylický středostavovský život jako většina z nás. V jeden jediný nečekaný moment ale o to všechno přišel. Skončil jako otrok spoutaný v řetězech a jeho jediným osudem se stala dřina na plantáži „pod bičem otrokáře". Většinou doslova. Přesně to se v roce 1841 přihodilo Američanu Solomonu Northupovi (Chiwetel Ejiofor), který svůj skutečný příběh zachytil v autobiografické knize 12 Years a Slave. Solomon měl smůlu, že byl... černoch. Dva únosci ho pod příslibem houslového melouchu vylákali do Washingtonu, kde Solomon po první noci skončil spoutaný v řetězech. V následujících dnech byl prodán do otroctví na americkém Jihu. Ocitl se ve zlém snu, který potrvá dalších dvanáct let. Za těch dvanáct let potká Solomona snad všechno, co černého otroka potkat mohlo. „Hodný" otrokář. Krutý otrokář. Dřina na bavlníkových plantážích. Pokus o lynčování. Bičování. Psychický teror. Neúspěšné pokusy o útěk. Víru ve svobodu a pomoc. (Bontonfilm)', '2019-01-07 19:53:30.849', 2013, NULL);
