INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000506, '201901/Prisoners-6.m4v', 'Zmizeni - Prisoners', 15, 0, 'cz', 'cz', 'en', 'Prisoners', 'https://www.csfd.cz/film/257071-zmizeni/', 'Prisoners-6.jpg', 0, 'Silné téma, vynikající scénář plný zvratů a životní herecké výkony poskládaly dohromady thriller o pátrání po dvou zmizelých děvčatech, který je napínavý až k nesnesení. Keller Dover (Hugh Jackman) čelí asi nejhorší noční můře každého rodiče, jeho šestiletá dcera Anna a její kamarádka Joy jednoho večera na Díkuvzdání zmizí. Jejich hledání nebere konce, hodiny se vlečnou a dívky jsou stále pohřešovány. Do objektu zájmu se při pátrání dostane zchátralý karavan, který parkoval před jejich domem a u kterého si dívky naposledy hrály. K případu je povolán detektiv Loki (Jake Gyllenhaal), který zatýká řidiče karavanu, retardovaného Alexe Jonese (Paul Dano), ale pro nedostatek důkazů je Jones po nějaké době propuštěn. Oficiální pátrání sice stále pokračuje, ale Dover se cítí bezradný, jeho strach se mísí se vztekem a zlobou. Bere spravedlnost do svých rukou a bez ohledu na následky se rozhodne vyslechnout jediné pojítko k případu sám, po svém a bez rukaviček. (Bontonfilm)', '2019-01-07 19:53:30.862', 2013, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000507, '201901/Broken City-1.m4v', 'Zlomene mesto - Broken City', 15, 0, 'cz', 'cz', 'en', 'Broken City', 'https://www.csfd.cz/film/300771-zlomene-mesto/', 'Broken City-1.jpg', 0, 'Billy byl kdysi chloubou policejního sboru, jenže když jeden gauner znásilnil a zavraždil sestru jeho přítelkyně, rozhodl se, že trochu obejde spravedlnost a potrestá ho sám trestem nejvyšším. Z obvinění z vraždy ho vyseká přímluva kamaráda u starosty Nicholase Hostetlera (Russell Crowe), jemuž stačí zatahat za pár nitek, aby bylo obvinění smazáno. Billy svůj neuvážený skutek odnese jen vyhozením od policie. Snaží se protloukat jako soukromé očko, ale příliš mu to nejde. Když už neví, kudy kam, zjeví se znovu na scéně starosta Hostetler a nabídne mu práci. Má sledovat jeho vlastní ženu (Catherine Zeta-Jones), která ho údajně podvádí s člověkem z týmu starostova politického oponenta. Lukrativní nabídku, kterou by levou zadní zvládlo i průměrné soukromé očko, Billy přijme, netuší však, že tímhle rozhodnutím strká hlavu do oprátky. Z banální sledovačky a příběhu o nevěře se totiž vyklube pořádně morbidní příběh, v němž se objevují první mrtví, a Billy je v tom najednou až po uši, protože si uvědomí, že možná nestojí na té správné straně barikády. Ve chvíli, kdy už není komu věřit, se musí spolehnout jen na svou pistoli a víru, že dokáže najít důkaz, který mu přinese vykoupení a záchranu. (oficiální text distributora)', '2019-01-07 19:53:30.899', 2013, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000508, '201901/Zelezna Srdce-2.m4v', 'Zelezna Srdce - Fury', 15, 0, 'cz', 'cz', 'en', 'Fury', 'https://www.csfd.cz/film/339501-zelezna-srdce/', 'Zelezna Srdce-2.jpg', 0, 'Duben 1945. Zatímco se spojenecká armáda pokouší o finální ofenzívu na evropské frontě, ujímá se válečný veterán, armádní seržant Wardaddy (Brad Pitt), velení tanku Sherman a vydává se s jeho pětičlennou posádkou na vražednou misi v týlu nepřítele. Tváří v tvář přesile početní i palebné a v doprovodu naprostého zelenáče, který se v jejich četě souhrou okolností ocitl, je Wardaddy se svými muži nucen získat navzdory veškeré pravděpodobnosti štěstí na svou stranu a dovést do úspěšného konce své hrdinské pokusy zaútočit na samotné srdce nacistického Německa. (Falcon)', '2019-01-07 19:53:30.913', 2014, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000503, '201901/American Made-22.m4v', ' Barry Seal: Nebesky gauner', 15, 0, 'cz', 'cz', 'en', 'American Made', 'https://www.csfd.cz/film/413149-barry-seal-nebesky-gauner/', 'American Made-22.jpg', 0, 'Barry Seal ( Tom Cruise) pracuje jako pilot dopravního letadla a rutina amerických vnitrostátních letů ho čím dále víc ubíjí. I proto nadšeně skočí po nabídce „náhodného kolemjdoucího“, z něhož se vyklube agent CIA ( Domhnall Gleeson), aby pilotoval pod záštitou tajných služeb. Barryho čekají sexy přelety nad základnami kolumbijských narkobaronů, ze kterých pořizuje bohatou fotodokumentaci, nutnou pro boj s drogovými kartely. Jenže proč létat takhle naprázdno, když se můžete spojit i s protistranou a kromě fotek pro CIA vozit do Ameriky i drogy? A proč se zároveň nestát informátorem DEA (Národní agentura pro boj s drogami), která má v oblasti také své specifické zájmy? A proč se rovnou nezapojit do jedné z největších tajných operací, které kdy americká vláda posvětila? Barry to prostě hraje na všechny světové strany a jde mu to víc než skvěle. Už dávno se přestal nudit. Má krásnou ženu, topí se v penězích a vůbec si nepřipouští, co se stane, až se tenhle domeček z karet zhroutí. A on se jednoho dne zhroutí. (CinemArt)', '2019-01-07 19:53:30.762', 2017, NULL);
