INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000523, '201905/Wilderness.m4v', 'Divocina', 3, 0, 'sk', 'sk', 'en', 'Wilderness', 'https://www.csfd.cz/film/194901-divocina/', 'Wilderness.jpg', 0, 'Film Divočina z produkce Walt Disney Pictures vypráví příběh zvířat z newyorské zoo, která se poté, co je jedno z nich omylem odvezeno do divočiny, vydávají na nebezpečnou záchrannou výpravu. Teprve ve chvíli, kdy opustí brány své zoo, pozná pestrá parta zvířat – včetně lva, žirafy, anakondy, koaly a veverky – jakou džunglí se může stát i obyčejné město. (oficiální text distributora)', '2019-05-28 16:43:15.201', 2006, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000525, '201905/AQUAMAN.m4v', 'Aquaman', 4, 0, 'cz', 'cz', 'en', 'Aquaman', 'https://www.csfd.cz/film/271857-aquaman/', 'AQUAMAN.jpg', 0, 'Filmový příběh o populárním superhrdinovi ze stáje DC Comics odhaluje původ Arthura Curryho ( Jason Momoa), napůl člověka, napůl obyvatele bájné Atlantidy, kterého jeho životní cesta přiměje čelit pravdě nejen o tom, kým ve skutečnosti je, ale zároveň prověří, zdali je hoden stát se tím, pro co byl zrozen… králem. (Vertical Ent.)', '2019-05-30 11:24:38.821', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000529, '201905/VENOM_2018.m4v', 'Venom', 4, 0, 'cz', 'cz', 'en', 'Venom', 'https://www.csfd.cz/film/265276-venom/', 'VENOM_2018.jpg', 0, 'Na filmová plátna přichází jedna z nejlepších a nejkomplexnějších comicsových postav společnosti Marvel. Novinář Eddie Brock ( Tom Hardy) se stává hostitelem mimozemského symbionta Venoma. Brock se v rámci své práce novináře pokouší již po celé roky odhalit veřejnosti skutečnou tvář známého vědce společnosti Life Foundation Carltona Drakea ( Riz Ahmed) – jeho odhodlání se přerodilo v posedlost, která mu zlikvidovala kariéru a zničila vztah s jeho přítelkyní Anne ( Michelle Williams). Když do svého těla Eddie vstřebá jeden z Drakeových experimentů – mimozemšťana Venoma – zničehonic zjišťuje, že disponuje novými superschopnostmi a má možnost dělat, cokoliv se mu zachce. Zvrhlý, temný, nepředvídatelný a zuřivý Venom má za následek, že se Eddie zoufale pokouší udržet pod kontrolou své nebezpečné schopnosti, které ho současně velice přitahují a opájejí. Jelikož Eddie a Venom navzájem jeden druhého potřebují k dosažení svých cílů, jejich vzájemné pouto a propojení se prohlubuje – až přestává být jasné, kde vlastně končí Eddie a začíná Venom. (Falcon)', '2019-05-30 11:24:39.287', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000526, '201905/UPGRADE.m4v', 'Upgrade', 4, 0, 'cz', 'cz', 'en', 'Upgrade', 'https://www.csfd.cz/film/601122-upgrade/', 'UPGRADE.jpg', 0, 'Za pár let už nebudeme muset ani hnout prstem, protože moderní technologie za nás všechno obstarají. Technofobům se v takové době nebude žít zrovna lehko. Grey Trace ( Logan Marshall-Green) je pověstný tím, že jako jeden z posledních lidí dává před technologiemi přednost vlastním rukám. Jedna noc, která mu obrátí život vzhůru nohama, však tenhle přístup rázně změní. Autonehoda na noční dálnici a střet s bandou grázlů, kteří mu zavraždí přítelkyni a jeho nadosmrti zmrzačí. Greyovy vyhlídky na invalidním vozíku jsou víc než temné, dokud mu kamarád vědec nenabídne technologické vylepšení. S čipem v míše dokáže Grey nejen znovu chodit a pohybovat rukama, ale získává i další, dosud netušené možnosti. Experimentální zařízení zvané STEM totiž dokáže například i vystopovat pachatele onoho nočního přepadení a také Greyovi pomoci si to s těmi gaunery vyřídit ručně. Kdo by odolal? Grey rozhodně ne. Když však zanechá prvního z pachatelů v tratolišti krve, napadne ho, jestli by STEM neměl aspoň trochu zbrzdit. Otázka zní, jestli to půjde. A jestli to Grey vůbec chce. (CinemArt)', '2019-05-30 11:24:39.117', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000527, '201905/BOHEMIAN_RHAPSODY.m4v', 'Bohemian Rhapsody', 15, 0, 'cz', 'cz', 'en', 'Bohemian Rhapsody', 'https://www.csfd.cz/film/300902-bohemian-rhapsody/', 'BOHEMIAN_RHAPSODY.jpg', 0, 'Film  Bohemian Rhapsody je oslavou rockové skupiny Queen, jejich hudby a především Freddieho Mercuryho, který svou tvorbou i životem vzdoroval všem myslitelným stereotypům, díky čemuž se stal jedním z nejvýraznějších umělců na světě. Snímek mapuje raketový vzestup nekonvenční skupiny prostřednictvím jejich revolučního zvuku a ikonických písní, jako jsou „We Will Rock You“, „We Are the Champions“ nebo právě „Bohemian Rhapsody“. Jejich příběh začíná bleskovým startem, pokračuje neřízenou životní spirálou a vrcholí nezapomenutelným, strhujícím vystoupením na koncertu Live Aid v roce 1985. Na jeho pódiu Queen v čele se rtuťovitým Freddiem předvedli jednu z největších show v historii rocku. Jejich hudba byla a stále je ohromnou inspirací pro celý svět. (CinemArt)', '2019-05-30 11:24:39.143', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000528, '201905/SEARCHING_2018.m4v', 'Patranie', 15, 0, 'cz', 'cz', 'en', 'Searching', 'https://www.csfd.cz/film/586217-patrani/', 'SEARCHING_2018.jpg', 0, 'Když Davidovi Kimovi ( John Cho) zmizí šestnáctiletá dcera, policie se pouští do vyšetřování. O 37 hodin později ale nemá žádnou stopu a David se rozhodne prohledat jediné místo, kam se ještě nikdo nepodíval: její laptop. V supermoderním thrilleru, jehož vyprávění se odvíjí prostřednictvím zařízení, která používáme ke každodenní komunikaci, musí David nalézt digitální stopy jeho dcery dřív, než nadobro zmizí. (Bontonfilm)', '2019-05-30 11:24:39.252', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000530, '201905/FIRST_MAN.m4v', 'Prvy clovek', 4, 0, 'cz', 'cz', 'en', 'First Man', 'https://www.csfd.cz/film/502300-prvni-clovek/', 'FIRST_MAN.jpg', 0, 'Před padesáti lety se půl miliardy lidí mačkalo u televizí, aby v přímém přenosu sledovali přistání posádky Apolla 11 na Měsíci. Krátce nato pronesl astronaut Neil Armstrong slavnou větu  „Je to malý krok pro člověka, obrovský skok pro lidstvo,“ a počin, který se stal možná nejodvážnější výpravou od plavby Kryštofa Kolumba, byl tímto úspěšně završen. Stejně fascinující ale byla éra, která tomuto letu předcházela, a životní příběh člověka, jenž dostal to privilegium udělat na Měsíci první „lidský“ krok. Příběh  Prvního člověka začíná v roce 1962, kdy začal pracovat jako pilot pro NASA a kdy se musel vyrovnat s osobní tragédií, smrtí dvouleté dcery na nevyléčitelnou nemoc. Pro práci, která představovala permanentní koketování se smrtí si těžko mohli v NASA vybrat vhodnějšího aspiranta. Armstrong ( Ryan Gosling) není vesmírný kovboj, který by riziko podstupoval dobrovolně a s frajerským nadhledem, na druhou stranu ani nepanikaří v situacích, kdy mu jde o život. Jeho základním charakterovým rysem je klid. Klid ve chvíli, kdy se ve zkušebním modulu řítí k zemi volným pádem, klid, když odpovídá synovi na otázku, zda se z mise vrátí, klid ve chvíli, kdy dělá onen historický první krok. Protiváhou tomuto klidu je pak absolutní neklid jeho ženy ( Claire Foy) a její vnitřní drama, s jakou prožívá jeho každý úspěch, a možnost, že se už nikdy nevrátí. (CinemArt)', '2019-05-30 11:24:39.32', 2018, NULL);
INSERT INTO movie (id, file_name, name, genre_id, serie_id, language, subtitles, original_language, original_name, url, picture_file_name, display_order, description, created, year, web_page) VALUES (47000524, '201905/FANTASTIC_BEASTS_2.m4v', 'Fantasticke zvery a ich vyskyt- Grindelwaldove zlociny', 4, 3, 'sk', 'sk', 'en', 'Fantastic Beasts: The Crimes of Grindelwald', 'https://www.csfd.cz/film/403861-fantasticka-zvirata-grindelwaldovy-zlociny/', 'FANTASTIC_BEASTS_2.jpg', 102, 'Na konci prvního filmu KOKUSA (Kouzelnícký kongres USA) zajme s pomocí Mloka Scamandera ( Eddie Redmayne) mocného černokněžníka Gellerta Grindelwalda ( Johnny Depp). Grindelwald ale splnil svou hrozbu, že uteče z vazby, a začal shromažďovat své stoupence, z nichž většina vůbec nic netušila o jeho skutečných cílech: připravit vládu čistokrevných čarodějů nad všemi nemagickými bytostmi. Ve snaze zmařit Grindelwaldovy plány, Albus Brumbál ( Jude Law) získá svého bývalého studenta Mloka Scamandera, který souhlasí s tím, že pomůže, aniž by si uvědomoval, jakému nebezpečí se tím vystavuje. Bitevní linie jsou vytyčeny a ve stále více znesvářeném světě čarodějů je podrobena zkoušce láska a loajalita, dokonce i mezi nejvěrnějšími přáteli a rodinou. (Vertical Ent.)', '2019-05-28 16:43:15.56', 2018, NULL);
